import React, { useRef, useEffect, useState } from "react";
import "../styles/LuxurySlider.css";

/**
 * LuxurySlider
 * - place inside any page where you want the purple MAX card with inner carousel.
 * - Replace image paths with your real images in public folder.
 */

const CARS = [
  {
    id: "c1",
    title: "2018 Jeep Compass",
    subtitle: "Sport 1.4 Petrol",
    price: "₹7.50 Lakh",
    emi: "EMI ₹12,772/m*",
    image: process.env.PUBLIC_URL + "/lux/jeep.jpg",
  },
  {
    id: "c2",
    title: "2020 Mercedes C-Class",
    subtitle: "C 200",
    price: "₹18.50 Lakh",
    emi: "EMI ₹31,000/m*",
    image: process.env.PUBLIC_URL + "/lux/merc.jpg",
  },
  {
    id: "c3",
    title: "2019 Audi Q3",
    subtitle: "35 TFSI",
    price: "₹15.20 Lakh",
    emi: "EMI ₹25,500/m*",
    image: process.env.PUBLIC_URL + "/lux/audi.jpg",
  },
  // add more cars as needed
];

export default function LuxurySlider({ title = "23+ similar luxury cars" }) {
  const listRef = useRef(null);
  const [canPrev, setCanPrev] = useState(false);
  const [canNext, setCanNext] = useState(true);

  useEffect(() => {
    const el = listRef.current;
    if (!el) return;
    const onScroll = () => {
      setCanPrev(el.scrollLeft > 10);
      // allow some tolerance for "end"
      setCanNext(el.scrollLeft + el.clientWidth + 10 < el.scrollWidth);
    };
    el.addEventListener("scroll", onScroll, { passive: true });
    onScroll();
    return () => el.removeEventListener("scroll", onScroll);
  }, []);

  const scrollBy = (dir = 1) => {
    const el = listRef.current;
    if (!el) return;
    // scroll by container width * 0.8 for a pleasing effect
    const amount = Math.round(el.clientWidth * 0.8) * dir;
    el.scrollBy({ left: amount, behavior: "smooth" });
  };

  // make container draggable with mouse (nice for desktop testing)
  useEffect(() => {
    const el = listRef.current;
    if (!el) return;
    let isDown = false;
    let startX = 0;
    let startScroll = 0;

    const onDown = (e) => {
      isDown = true;
      startX = e.touches ? e.touches[0].clientX : e.clientX;
      startScroll = el.scrollLeft;
      el.classList.add("no-transition");
    };
    const onMove = (e) => {
      if (!isDown) return;
      const clientX = e.touches ? e.touches[0].clientX : e.clientX;
      const dx = startX - clientX;
      el.scrollLeft = startScroll + dx;
    };
    const onUp = () => {
      isDown = false;
      el.classList.remove("no-transition");
    };

    el.addEventListener("mousedown", onDown);
    window.addEventListener("mousemove", onMove);
    window.addEventListener("mouseup", onUp);
    el.addEventListener("touchstart", onDown, { passive: true });
    el.addEventListener("touchmove", onMove, { passive: false });
    el.addEventListener("touchend", onUp);

    return () => {
      el.removeEventListener("mousedown", onDown);
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("mouseup", onUp);
      el.removeEventListener("touchstart", onDown);
      el.removeEventListener("touchmove", onMove);
      el.removeEventListener("touchend", onUp);
    };
  }, []);

  return (
    <section className="lux-section" aria-label="Luxury collection">
      <div className="lux-inner">
        <div className="lux-header">
          <img
            className="lux-logo"
            src={process.env.PUBLIC_URL + "/shortlogoo.png"}
            alt="Logo"
          />
          <div className="lux-title">{title}</div>
        </div>

        <div className="lux-carousel-shell">
          <button
            className={`lux-nav lux-prev ${canPrev ? "" : "disabled"}`}
            onClick={() => scrollBy(-1)}
            aria-hidden={!canPrev}
            aria-label="Previous"
          >
            ‹
          </button>

          <div className="lux-list-viewport" ref={listRef}>
            <div className="lux-list">
              {CARS.map((c) => (
                <article className="lux-card" key={c.id}>
                  <div
                    className="lux-card-image"
                    style={{ backgroundImage: `url(${c.image})` }}
                    role="img"
                    aria-label={c.title}
                  />
                  <div className="lux-card-body">
                    <div className="lux-card-row">
                      <div className="lux-card-title">{c.title}</div>
                      <div className="lux-card-price">{c.price}</div>
                    </div>
                    <div className="lux-card-sub">{c.subtitle}</div>
                    <div className="lux-card-emi">{c.emi}</div>
                  </div>
                </article>
              ))}

              {/* last special tile */}
              <article className="lux-card lux-card-more">
                <div className="lux-more-inner">
                  <div className="lux-more-text">View more luxury cars</div>
                  <button className="lux-explore-btn">Explore</button>
                </div>
              </article>
            </div>
          </div>

          <button
            className={`lux-nav lux-next ${canNext ? "" : "disabled"}`}
            onClick={() => scrollBy(1)}
            aria-hidden={!canNext}
            aria-label="Next"
          >
            ›
          </button>
        </div>
      </div>
    </section>
  );
}
