import React, { useRef, useState, useEffect, useCallback } from "react";
import "../styles/BodyTypeCarousel.css";

const SAMPLE = {
  hatchback: [
    { id: "h1", title: "Maruti Suzuki Baleno", price: "₹3.62 Lakh", km: "33,500 km", img: process.env.PUBLIC_URL + "/baleno.png" },
    { id: "h2", title: "Hyundai i20", price: "₹4.10 Lakh", km: "27,000 km", img: process.env.PUBLIC_URL + "/i20.png" },
    { id: "h3", title: "Tata Altroz", price: "₹5.00 Lakh", km: "15,200 km", img: process.env.PUBLIC_URL + "/altroz.png" },
  ],
  suv: [
    { id: "s1", title: "Kia Seltos", price: "₹12.5 Lakh", km: "22,000 km", img: process.env.PUBLIC_URL + "/seltos.png" },
    { id: "s2", title: "Hyundai Creta", price: "₹11.8 Lakh", km: "18,700 km", img: process.env.PUBLIC_URL + "/creta.png" },
    { id: "s3", title: "MG Hector", price: "₹13.2 Lakh", km: "21,300 km", img: process.env.PUBLIC_URL + "/hector.png" },
  ],
  sedan: [
    { id: "d1", title: "Honda City", price: "₹10.4 Lakh", km: "29,000 km", img: process.env.PUBLIC_URL + "/city.png" },
    { id: "d2", title: "Maruti Ciaz", price: "₹8.7 Lakh", km: "34,000 km", img: process.env.PUBLIC_URL + "/ciaz.png" },
    { id: "d3", title: "Skoda Slavia", price: "₹11.0 Lakh", km: "19,500 km", img: process.env.PUBLIC_URL + "/slavia.png" },
  ],
};

export default function BodyTypeCarousel({ data = SAMPLE }) {
  const types = ["hatchback", "suv", "sedan"];
  const [activeType, setActiveType] = useState("hatchback");
  const slides = data[activeType] || [];
  const trackRef = useRef(null);
  const startX = useRef(0);
  const prevTranslate = useRef(0);
  const currentTranslate = useRef(0);
  const dragging = useRef(false);
  const [index, setIndex] = useState(0);
  const [vw, setVw] = useState(window.innerWidth);

  useEffect(() => {
    const onResize = () => setVw(window.innerWidth);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  // when switching type or vw, reset index
  useEffect(() => {
    setIndex(0);
    prevTranslate.current = 0;
    currentTranslate.current = 0;
    setTranslate(0, true);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [activeType, vw]);

  // apply translate
  const setTranslate = (x, withTransition = false) => {
    const node = trackRef.current;
    if (!node) return;
    node.style.transition = withTransition ? "transform 420ms cubic-bezier(.2,.9,.3,1)" : "none";
    node.style.transform = `translateX(${x}px)`;
  };

  // clamp
  const clamp = (v, a, b) => Math.max(a, Math.min(b, v));

  // pointer handlers
  const handleStart = (clientX) => {
    dragging.current = true;
    startX.current = clientX;
    trackRef.current.style.transition = "none";
  };
  const handleMove = (clientX) => {
    if (!dragging.current) return;
    const dx = clientX - startX.current;
    currentTranslate.current = prevTranslate.current + dx;
    setTranslate(currentTranslate.current, false);
  };
  const handleEnd = () => {
    if (!dragging.current) return;
    dragging.current = false;
    const movedBy = currentTranslate.current - prevTranslate.current;
    const threshold = Math.max(40, vw * 0.12);
    if (movedBy < -threshold && index < slides.length - 1) {
      setIndex((i) => i + 1);
    } else if (movedBy > threshold && index > 0) {
      setIndex((i) => i - 1);
    } else {
      setTranslate(prevTranslate.current, true);
    }
  };

  // mouse fallback
  useEffect(() => {
    const onMove = (e) => handleMove(e.clientX);
    const onUp = () => handleEnd();
    window.addEventListener("mousemove", onMove);
    window.addEventListener("mouseup", onUp);
    return () => {
      window.removeEventListener("mousemove", onMove);
      window.removeEventListener("mouseup", onUp);
    };
  }, [index, slides.length, vw]);

  // when index changes, snap
  useEffect(() => {
    const x = -index * (cardWidth());
    prevTranslate.current = x;
    currentTranslate.current = x;
    setTranslate(x, true);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [index]);

  const cardWidth = () => {
    // card has side margins in CSS; compute approximate width to snap
    // We'll use viewport width minus container padding to make it centered
    const containerPad = 32; // matches CSS padding/margins
    return Math.min(560, vw - containerPad);
  };

  const onTouchStart = (e) => handleStart(e.touches[0].clientX);
  const onTouchMove = (e) => handleMove(e.touches[0].clientX);
  const onTouchEnd = () => handleEnd();
  const onMouseDown = (e) => {
    e.preventDefault();
    handleStart(e.clientX);
  };

  const prev = useCallback(() => setIndex((i) => clamp(i - 1, 0, slides.length - 1)), [slides.length]);
  const next = useCallback(() => setIndex((i) => clamp(i + 1, 0, slides.length - 1)), [slides.length]);

  return (
    <section className="btc-section">
      <h3 className="btc-title">Explore by Body Type</h3>

      <div className="btc-pill-wrap">
        <div className="btc-pill-inner">
          {types.map((t) => (
            <button
              key={t}
              className={`btc-pill ${activeType === t ? "active" : ""}`}
              onClick={() => setActiveType(t)}
              aria-pressed={activeType === t}
            >
              {t === "hatchback" ? "Hatchback" : t === "suv" ? "SUV" : "Sedan"}
            </button>
          ))}
        </div>
      </div>

      <div className="btc-carousel">
        <button className="btc-arrow left" onClick={prev} disabled={index === 0}>‹</button>

        <div
          className="btc-viewport"
          onTouchStart={onTouchStart}
          onTouchMove={onTouchMove}
          onTouchEnd={onTouchEnd}
          onMouseDown={onMouseDown}
        >
          <div ref={trackRef} className="btc-track" style={{ width: `${slides.length * 100}%` }}>
            {slides.map((card, i) => (
              <article
                key={card.id}
                className="btc-card"
                style={{ minWidth: `${100 / slides.length}%` }} // flexible card width
              >
                <div className="btc-card-inner">
                  <div className="btc-img" style={{ backgroundImage: `url(${card.img})` }} />
                  <div className="btc-info">
                    <div className="btc-name">{card.title}</div>
                    <div className="btc-price">{card.price}</div>
                    <div className="btc-meta">{card.km}</div>
                  </div>
                </div>
              </article>
            ))}
          </div>
        </div>

        <button className="btc-arrow right" onClick={next} disabled={index >= slides.length - 1}>›</button>
      </div>

      <div className="btc-dots">
        {slides.map((_, i) => <button key={i} className={`btc-dot ${i === index ? "active" : ""}`} onClick={() => setIndex(i)} />)}
      </div>

      <div className="btc-viewall">
        <button className="btc-viewall-btn">View all {activeType === "hatchback" ? "hatchbacks" : activeType}</button>
      </div>
    </section>
  );
}
