import React, { useState, useMemo, useEffect, useRef } from "react";
import "../styles/BodyTypeCatalog.css";

/* ---------------- SAMPLE DATA (replace images with real /public paths) ---------------- */
const CARS_BY_BODY = {
  hatchback: [
    { id: "h1", title: "Tata Tiago", price: "₹3.47 Lakh", image: "/placeholder-car-1.jpg" },
    { id: "h2", title: "Hyundai Elite i20", price: "₹4.04 Lakh", image: "/placeholder-car-2.jpg" },
    { id: "h3", title: "Maruti Alto K10", price: "₹2.89 Lakh", image: "/placeholder-car-3.jpg" },
    { id: "h4", title: "Tata Altroz", price: "₹6.05 Lakh", image: "/placeholder-car-4.jpg" },
    { id: "h5", title: "Maruti Swift", price: "₹5.10 Lakh", image: "/placeholder-car-5.jpg" },
  ],
  sedan: [
    { id: "s1", title: "Honda City", price: "₹9.25 Lakh", image: "/placeholder-sedan-1.jpg" },
    { id: "s2", title: "Hyundai Verna", price: "₹10.10 Lakh", image: "/placeholder-sedan-2.jpg" },
    { id: "s3", title: "Skoda Slavia", price: "₹11.80 Lakh", image: "/placeholder-sedan-3.jpg" },
    { id: "s4", title: "Maruti Ciaz", price: "₹8.90 Lakh", image: "/placeholder-sedan-4.jpg" },
  ],
  suv: [
    { id: "u1", title: "Kia Seltos", price: "₹13.50 Lakh", image: "/placeholder-suv-1.jpg" },
    { id: "u2", title: "Hyundai Creta", price: "₹12.90 Lakh", image: "/placeholder-suv-2.jpg" },
    { id: "u3", title: "MG Hector", price: "₹14.20 Lakh", image: "/placeholder-suv-3.jpg" },
    { id: "u4", title: "Tata Harrier", price: "₹15.30 Lakh", image: "/placeholder-suv-4.jpg" },
  ],
  muv: [
    { id: "m1", title: "Mahindra XUV300", price: "₹7.75 Lakh", image: "/placeholder-muv-1.jpg" },
    { id: "m2", title: "Toyota Innova", price: "₹18.50 Lakh", image: "/placeholder-muv-2.jpg" },
    { id: "m3", title: "Maruti Ertiga", price: "₹9.20 Lakh", image: "/placeholder-muv-3.jpg" },
  ],
};

/* ---------------- HELPERS ---------------- */
const BODY_OPTIONS = [
  { key: "hatchback", label: "Hatchback", icon: "car" },
  { key: "sedan", label: "Sedan", icon: "sedan" },
  { key: "suv", label: "SUV", icon: "suv" },
  { key: "muv", label: "MUV", icon: "muv" },
];

const getVisibleCount = (width) => {
  if (width <= 720) return 1;
  if (width <= 1100) return 2;
  return 4;
};

/* ---------------- SMALL UI PIECES ---------------- */
function Icon({ name }) {
  // simple placeholder SVGs - swap with custom icons if you have them
  if (name === "sedan")
    return (
      <svg width="36" height="20" viewBox="0 0 36 20" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="2" y="6" width="32" height="8" rx="2" stroke="currentColor" strokeWidth="1.5" fill="none" />
        <circle cx="9" cy="16" r="1.6" fill="currentColor" />
        <circle cx="27" cy="16" r="1.6" fill="currentColor" />
      </svg>
    );
  if (name === "suv")
    return (
      <svg width="36" height="20" viewBox="0 0 36 20" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="2" y="4" width="32" height="10" rx="2" stroke="currentColor" strokeWidth="1.5" fill="none" />
        <rect x="6" y="1" width="16" height="6" rx="2" stroke="currentColor" strokeWidth="1.5" fill="none" />
        <circle cx="10" cy="16" r="1.6" fill="currentColor" />
        <circle cx="26" cy="16" r="1.6" fill="currentColor" />
      </svg>
    );
  if (name === "muv")
    return (
      <svg width="36" height="20" viewBox="0 0 36 20" fill="none" xmlns="http://www.w3.org/2000/svg">
        <rect x="2" y="4" width="32" height="10" rx="3" stroke="currentColor" strokeWidth="1.5" fill="none" />
        <circle cx="10" cy="16" r="1.6" fill="currentColor" />
        <circle cx="26" cy="16" r="1.6" fill="currentColor" />
      </svg>
    );
  // hatchback (default)
  return (
    <svg width="36" height="20" viewBox="0 0 36 20" fill="none" xmlns="http://www.w3.org/2000/svg">
      <rect x="5" y="6" width="20" height="7" rx="2" stroke="currentColor" strokeWidth="1.5" fill="none" />
      <path d="M25 6h6v7" stroke="currentColor" strokeWidth="1.5" strokeLinecap="round" strokeLinejoin="round" />
      <circle cx="11" cy="16" r="1.6" fill="currentColor" />
      <circle cx="27" cy="16" r="1.6" fill="currentColor" />
    </svg>
  );
}

/* ---------------- CAR CARD ---------------- */
function Card({ car }) {
  return (
    <div className="btc-card">
      <div className="btc-card-media">
        <img src={car.image} alt={car.title} loading="lazy" />
      </div>
      <div className="btc-card-body">
        <div className="btc-card-title">{car.title}</div>
        <div className="btc-card-price">{car.price}</div>
      </div>
    </div>
  );
}

/* ---------------- MAIN COMPONENT ---------------- */
export default function BodyTypeCatalog() {
  const [active, setActive] = useState("hatchback");
  const cars = useMemo(() => CARS_BY_BODY[active] || [], [active]);

  const [visibleCount, setVisibleCount] = useState(() => getVisibleCount(window.innerWidth));
  const [index, setIndex] = useState(0);
  const carouselRef = useRef(null);

  useEffect(() => {
    let tid;
    const onResize = () => {
      clearTimeout(tid);
      tid = setTimeout(() => {
        setVisibleCount(getVisibleCount(window.innerWidth));
      }, 120);
    };
    window.addEventListener("resize", onResize);
    return () => {
      clearTimeout(tid);
      window.removeEventListener("resize", onResize);
    };
  }, []);

  // clamp index when cars or visibleCount change
  useEffect(() => {
    const maxIndex = Math.max(0, cars.length - visibleCount);
    setIndex((prev) => Math.min(prev, maxIndex));
  }, [cars.length, visibleCount]);

  // reset index when switching body type
  useEffect(() => {
    setIndex(0);
  }, [active]);

  const prev = () => setIndex((i) => Math.max(0, i - 1));
  const next = () => setIndex((i) => Math.min(i + 1, Math.max(0, cars.length - visibleCount)));

  const itemWidthPercent = 100 / visibleCount;
  const trackWidthPercent = cars.length * itemWidthPercent;
  const translatePercent = index * itemWidthPercent;

  const bottomButtonText = `View all ${active[0].toUpperCase() + active.slice(1)}s`;

  return (
    <section className="btc-section">
      <h2 className="btc-title">Explore by Body Type</h2>

      {/* Toggle pill */}
      <div className="btc-toggle-wrap">
        <div className="btc-toggle">
          {BODY_OPTIONS.map((opt) => (
            <button
              key={opt.key}
              className={`btc-toggle-btn ${active === opt.key ? "active" : ""}`}
              onClick={() => setActive(opt.key)}
            >
              <span className="btc-icon">
                <Icon name={opt.icon === "car" ? "hatch" : opt.icon} />
              </span>
              <span className="btc-label">{opt.label}</span>
            </button>
          ))}
        </div>
      </div>

      {/* Carousel */}
      <div className="btc-carousel-wrap">
        <button className="btc-arrow left" onClick={prev} disabled={index === 0} aria-label="prev">‹</button>

        <div className="btc-viewport" ref={carouselRef}>
          <div
            className="btc-track"
            style={{
              width: `${trackWidthPercent}%`,
              transform: `translateX(-${translatePercent}%)`,
            }}
          >
            {cars.map((c) => (
              <div
                key={c.id}
                className="btc-item"
                style={{ width: `${itemWidthPercent}%`, boxSizing: "border-box", padding: 12 }}
              >
                <Card car={c} />
              </div>
            ))}
          </div>
        </div>

        <button
          className="btc-arrow right"
          onClick={next}
          disabled={index >= Math.max(0, cars.length - visibleCount)}
          aria-label="next"
        >
          ›
        </button>
      </div>

      {/* Bottom CTA */}
      <div className="btc-cta">
        <button className="btc-view-all">{bottomButtonText}</button>
      </div>
    </section>
  );
}
