import React from "react";
import { useParams } from "react-router-dom";
import carsData from "../data/cars";
import "../styles/CarDetailsWeb.css";

export default function CarDetailsWeb() {
  const { id } = useParams();
  const car = carsData.find((c) => c.id === Number(id));

  if (!car) return <div>Car not found</div>;

  return (
    <div className="car-details-page">

      {/* LEFT SCROLLABLE SECTION */}
      <div className="car-details-left">
        <div className="large-photo">
          <img src={car.image} alt={car.title} />
        </div>

        <div className="thumb-row">
          {[car.image, car.image, car.image, car.image].map((img, i) => (
            <img key={i} src={img} className="thumb-img" />
          ))}
        </div>

        <div className="overview">
          <h2>Car Overview</h2>

          <p><strong>Brand:</strong> {car.brand}</p>
          <p><strong>Model:</strong> {car.model}</p>
          <p><strong>Fuel:</strong> {car.fuel}</p>
          <p><strong>Transmission:</strong> {car.transmission}</p>
          <p><strong>Body Type:</strong> {car.body}</p>
        </div>
      </div>

      {/* RIGHT FIXED SECTION */}
      <div className="car-details-right">
