import React, { useRef, useState, useEffect, useCallback } from "react";
import "../styles/FeaturedCars.css";

/**
 * FeaturedCards
 * - responsive: shows different number of visible cards based on viewport
 * - swipable with touch and mouse drag
 * - keyboard accessible (left/right)
 *
 * Usage: <FeaturedCards cards={cardsArray} />
 * cardsArray = [{ id, title, subtitle, price, image }, ...]
 */

const sampleCards = [
  { id: 1, title: "2020 Maruti Baleno", subtitle: "Zeta", price: "₹5.46 Lakh", image: process.env.PUBLIC_URL + "/car1.png" },
  { id: 2, title: "2023 Volkswagen Taigun", subtitle: "Highline 1.0 TSI AT", price: "₹10.39 Lakh", image: process.env.PUBLIC_URL + "/car2.png" },
  { id: 3, title: "2022 Maruti Swift", subtitle: "VXi AMT", price: "₹6.02 Lakh", image: process.env.PUBLIC_URL + "/car3.png" },
  { id: 4, title: "2021 Honda City", subtitle: "VX CVT", price: "₹9.50 Lakh", image: process.env.PUBLIC_URL + "/car4.png" },
  { id: 5, title: "2019 Hyundai Creta", subtitle: "SX", price: "₹8.10 Lakh", image: process.env.PUBLIC_URL + "/car5.png" },
];

export default function FeaturedCards({ cards = sampleCards }) {
  const trackRef = useRef(null);
  const viewportRef = useRef(null);
  const startX = useRef(0);
  const currentTranslate = useRef(0);
  const prevTranslate = useRef(0);
  const dragging = useRef(false);

  const [index, setIndex] = useState(0); // index of left-most visible card
  const [visible, setVisible] = useState(getVisibleCount(window.innerWidth));
  const [cardWidth, setCardWidth] = useState(0);

  function getVisibleCount(width) {
    if (width <= 520) return 1;
    if (width <= 900) return 2;
    return 3;
  }

  // resize handling
  useEffect(() => {
    let t;
    const onResize = () => {
      clearTimeout(t);
      t = setTimeout(() => {
        const v = getVisibleCount(window.innerWidth);
        setVisible(v);
      }, 100);
    };
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  // compute card width after visible changes or mount
  useEffect(() => {
    const viewport = viewportRef.current;
    if (!viewport) return;
    const vw = viewport.clientWidth;
    const cw = Math.floor(vw / visible); // integer px width for each card
    setCardWidth(cw);
    // adjust index if out of range
    setIndex((i) => Math.min(i, Math.max(0, cards.length - visible)));
  }, [visible, cards.length]);

  // whenever index or cardWidth changes, translate track
  useEffect(() => {
    const target = -(index * cardWidth);
    prevTranslate.current = target;
    currentTranslate.current = target;
    setTransform(target, true);
  }, [index, cardWidth]);

  // helper
  const setTransform = (x, withTransition = false) => {
    const node = trackRef.current;
    if (!node) return;
    node.style.transition = withTransition ? "transform 350ms cubic-bezier(.2,.9,.3,1)" : "none";
    node.style.transform = `translateX(${x}px)`;
  };

  // touch / mouse handlers
  const onStart = (clientX) => {
    dragging.current = true;
    startX.current = clientX;
    // starting base translate (prevTranslate.current)
    currentTranslate.current = prevTranslate.current;
    // avoid browser interfering with horizontal touch
    if (viewportRef.current) viewportRef.current.style.touchAction = "none";
    setTransform(currentTranslate.current, false);
  };

  const onMove = (clientX) => {
    if (!dragging.current) return;
    const dx = clientX - startX.current;
    currentTranslate.current = prevTranslate.current + dx;
    setTransform(currentTranslate.current, false);
  };

  const onEnd = () => {
    if (!dragging.current) return;
    dragging.current = false;
    const moved = currentTranslate.current - prevTranslate.current; // dx
    const threshold = Math.max(40, cardWidth * 0.18); // require reasonable movement
    if (moved < -threshold && index < cards.length - visible) {
      setIndex((i) => i + 1);
    } else if (moved > threshold && index > 0) {
      setIndex((i) => i - 1);
    } else {
      // snap back
      setTransform(prevTranslate.current, true);
    }
    if (viewportRef.current) viewportRef.current.style.touchAction = "pan-y";
  };

  // react touch bindings
  const handleTouchStart = (e) => onStart(e.touches[0].clientX);
  const handleTouchMove = (e) => onMove(e.touches[0].clientX);
  const handleTouchEnd = () => onEnd();

  // mouse bindings (desktop/dev)
  const handleMouseDown = (e) => {
    e.preventDefault();
    onStart(e.clientX);
  };
  useEffect(() => {
    const onMouseMove = (e) => onMove(e.clientX);
    const onMouseUp = () => onEnd();
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
    return () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };
  }, [cardWidth, index]);

  // controls
  const prev = useCallback(() => setIndex((i) => Math.max(0, i - 1)), []);
  const next = useCallback(() => setIndex((i) => Math.min(cards.length - visible, i + 1)), [cards.length, visible]);

  // keyboard left/right for accessibility
  useEffect(() => {
    const onKey = (e) => {
      if (e.key === "ArrowLeft") prev();
      if (e.key === "ArrowRight") next();
    };
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [prev, next]);

  return (
    <section className="fc-section">
      <div className="fc-inner">
        <h3 className="fc-title">Featured Spinny cars</h3>

        <div className="fc-wrapper">
          <button className="fc-arrow left" onClick={prev} disabled={index === 0} aria-label="Previous">‹</button>

          <div
            className="fc-viewport"
            ref={viewportRef}
            onTouchStart={handleTouchStart}
            onTouchMove={handleTouchMove}
            onTouchEnd={handleTouchEnd}
            onMouseDown={handleMouseDown}
            style={{ touchAction: "pan-y" }}
          >
            <div
              className="fc-track"
              ref={trackRef}
              style={{
                width: `${cards.length * cardWidth}px`,
              }}
            >
              {cards.map((c) => (
                <article
                  key={c.id}
                  className="fc-card"
                  style={{
                    width: `${cardWidth - 16}px`, // keep some gap for margin
                    backgroundImage: `url(${c.image})`,
                  }}
                >
                  <div className="fc-card-body">
                    <div className="fc-card-top">
                      {/* optional logo/badges */}
                    </div>
                    <div className="fc-card-info">
                      <div className="fc-name">{c.title}</div>
                      <div className="fc-sub">{c.subtitle}</div>
                    </div>
                    <div className="fc-price">{c.price}</div>
                  </div>
                </article>
              ))}
            </div>
          </div>

          <button className="fc-arrow right" onClick={next} disabled={index >= cards.length - visible} aria-label="Next">›</button>
        </div>

        <div className="fc-dots">
          {Array.from({ length: Math.max(1, cards.length - visible + 1) }).map((_, i) => (
            <button key={i} className={`fc-dot ${i === index ? "active" : ""}`} onClick={() => setIndex(i)} aria-label={`Go to set ${i + 1}`} />
          ))}
        </div>

        <div className="fc-viewall">
          <button className="fc-viewall-btn">View all Spinny cars</button>
        </div>
      </div>
    </section>
  );
}
