// src/pages/CarDetails.jsx
import React, { useEffect, useRef, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import carsData from "../data/cars";
import "../styles/CarDetailsWeb.css";

const TOPBAR_HEIGHT = 72; // adjust to match your topbar height (px)

export default function CarDetailsWeb() {
  const { id } = useParams();
  const navigate = useNavigate();
  const car = carsData.find((c) => c.id === Number(id));

  // refs to important parts
  const pageRef = useRef(null);
  const heroRef = useRef(null);
  const overviewRef = useRef(null);
  const reportRef = useRef(null);
  const specsRef = useRef(null);
  const financeRef = useRef(null);

  // sticky tab bar visible when hero image leaves view
  const [showStickyTabs, setShowStickyTabs] = useState(false);
  const [activeTab, setActiveTab] = useState("overview");

  // IntersectionObserver for hero to show sticky tabs
  useEffect(() => {
    if (!heroRef.current) return;
    const obs = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          // when hero is not intersecting (i.e. scrolled out) show sticky tabs
          setShowStickyTabs(!entry.isIntersecting);
        });
      },
      {
        root: null,
        threshold: 0.05,
      }
    );
    obs.observe(heroRef.current);
    return () => obs.disconnect();
  }, []);

  // IntersectionObserver to set active tab based on sections in view
  useEffect(() => {
    const sections = [
      { id: "overview", ref: overviewRef },
      { id: "report", ref: reportRef },
      { id: "specs", ref: specsRef },
      { id: "finance", ref: financeRef },
    ];

    const options = {
      root: null,
      rootMargin: `-${TOPBAR_HEIGHT + 12}px 0px -40% 0px`, // trigger when near top
      threshold: [0.15, 0.45],
    };

    const observer = new IntersectionObserver((entries) => {
      // choose the entry with highest intersectionRatio
      const visible = entries
        .filter((e) => e.isIntersecting)
        .sort((a, b) => b.intersectionRatio - a.intersectionRatio);
      if (visible.length > 0) {
        const el = visible[0].target;
        setActiveTab(el.dataset.section);
      }
    }, options);

    sections.forEach((s) => {
      if (s.ref.current) observer.observe(s.ref.current);
    });

    return () => observer.disconnect();
  }, []);

  if (!car) {
    return (
      <div className="car-details-page empty">
        <p>Car not found.</p>
        <button onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  function scrollToSection(ref) {
    if (!ref?.current) return;
    const offset = TOPBAR_HEIGHT + 8; // leave a little space under topbar
    const top =
      ref.current.getBoundingClientRect().top + window.scrollY - offset;
    window.scrollTo({ top, behavior: "smooth" });
  }

  return (
    <div className="car-details-wrapper" ref={pageRef}>
      {/* Sticky small tabs that appear once hero is scrolled away */}
      <div
        className={`sticky-tabs ${showStickyTabs ? "visible" : ""}`}
        style={{ top: `${TOPBAR_HEIGHT}px` }}
        role="navigation"
        aria-label="Car detail sections"
      >
        <div className="sticky-inner">
          <button
            className={`tab ${activeTab === "overview" ? "active" : ""}`}
            onClick={() => scrollToSection(overviewRef)}
          >
            Overview
          </button>
          <button
            className={`tab ${activeTab === "report" ? "active" : ""}`}
            onClick={() => scrollToSection(reportRef)}
          >
            Report
          </button>
          <button
            className={`tab ${activeTab === "specs" ? "active" : ""}`}
            onClick={() => scrollToSection(specsRef)}
          >
            Feature & Specs
          </button>
          <button
            className={`tab ${activeTab === "finance" ? "active" : ""}`}
            onClick={() => scrollToSection(financeRef)}
          >
            Finance
          </button>
        </div>
      </div>

      <div className="car-details-page">
        {/* LEFT: scrollable main content */}
        <div className="car-details-left">
          {/* HERO */}
          <div className="hero" ref={heroRef}>
            <div className="hero-inner">
              <div className="hero-image">
                <img src={car.image} alt={car.title} />
                <div className="hero-overlay">
                  <button className="back-btn" onClick={() => navigate(-1)}>
                    ← Back
                  </button>
                </div>
              </div>
            </div>
          </div>

          {/* Inline large nav that sits below the hero in normal state (desktop) */}
          <div className="hero-tabs">
            <button
              className={`hero-tab ${activeTab === "overview" ? "active" : ""}`}
              onClick={() => scrollToSection(overviewRef)}
            >
              Overview
            </button>
            <button
              className={`hero-tab ${activeTab === "report" ? "active" : ""}`}
              onClick={() => scrollToSection(reportRef)}
            >
              Report
            </button>
            <button
              className={`hero-tab ${activeTab === "specs" ? "active" : ""}`}
              onClick={() => scrollToSection(specsRef)}
            >
              Feature & Specs
            </button>
            <button
              className={`hero-tab ${activeTab === "finance" ? "active" : ""}`}
              onClick={() => scrollToSection(financeRef)}
            >
              Finance
            </button>
          </div>

          {/* Overview Section */}
          <section
            id="overview"
            ref={overviewRef}
            data-section="overview"
            className="section block overview-section"
          >
            <h2 className="section-title">Car Overview</h2>

            <div className="overview-card">
              <div className="overview-row">
                <div>
                  <div className="muted">Make Year</div>
                  <div className="strong">May 2024</div>
                </div>
                <div>
                  <div className="muted">Registration Year</div>
                  <div className="strong">Jun 2024</div>
                </div>
                <div>
                  <div className="muted">Fuel Type</div>
                  <div className="strong">{car.fuel}</div>
                </div>
              </div>

              <div className="overview-row">
                <div>
                  <div className="muted">Km driven</div>
                  <div className="strong">{car.km.toLocaleString()} km</div>
                </div>
                <div>
                  <div className="muted">Transmission</div>
                  <div className="strong">{car.transmission}</div>
                </div>
                <div>
                  <div className="muted">No. of Owner</div>
                  <div className="strong">{car.owner || "1st Owner"}</div>
                </div>
              </div>

              <div className="overview-row">
                <div>
                  <div className="muted">Insurance Validity</div>
                  <div className="strong">Jun 2026</div>
                </div>
                <div>
                  <div className="muted">Insurance Type</div>
                  <div className="strong">Comprehensive</div>
                </div>
                <div>
                  <div className="muted">RTO</div>
                  <div className="strong">UP14</div>
                </div>
              </div>

              <div className="overview-row last">
                <div>
                  <div className="muted">Car Location</div>
                  <div className="strong">{car.city}</div>
                </div>
                <div />
                <div />
              </div>
            </div>
          </section>

          {/* Report Section */}
          <section
            id="report"
            ref={reportRef}
            data-section="report"
            className="section block report-section"
          >
            <h2 className="section-title">Quality report</h2>

            <p className="muted">
              1571 parts evaluated by 5 automotive experts
            </p>

            <div className="report-card">
              <div className="report-row">
                <div className="report-item">
                  <div className="report-title">Core systems</div>
                  <div className="report-score">9.8</div>
                </div>

                <div className="report-item">
                  <div className="report-title">Supporting systems</div>
                  <div className="report-score">9.7</div>
                </div>
              </div>

              <div className="report-row">
                <div className="report-item">
                  <div className="report-title">Interiors & AC</div>
                  <div className="report-score">9.6</div>
                </div>

                <div className="report-item">
                  <div className="report-title">Exteriors & lights</div>
                  <div className="report-score">9.3</div>
                </div>
              </div>
            </div>
          </section>

          {/* Feature & Specs */}
          <section
            id="specs"
            ref={specsRef}
            data-section="specs"
            className="section block specs-section"
          >
            <h2 className="section-title">Feature & Specs</h2>

            <div className="specs-grid">
              <div className="spec-card">
                <div className="spec-key">Engine</div>
                <div className="spec-value">1.2 L K10 (Petrol)</div>
              </div>
              <div className="spec-card">
                <div className="spec-key">Power</div>
                <div className="spec-value">83 bhp</div>
              </div>
              <div className="spec-card">
                <div className="spec-key">Mileage</div>
                <div className="spec-value">22 kmpl</div>
              </div>
              <div className="spec-card">
                <div className="spec-key">Seating</div>
                <div className="spec-value">5</div>
              </div>
            </div>
          </section>

          {/* Finance */}
          <section
            id="finance"
            ref={financeRef}
            data-section="finance"
            className="section block finance-section"
          >
            <h2 className="section-title">Finance</h2>

            <div className="finance-card">
              <p className="muted">Fixed on road price</p>
              <h3 className="price-large">₹{(car.price / 100000).toFixed(2)} L</h3>

              <div className="emi-row">
                <div>
                  <div className="muted">Starting EMI</div>
                  <div className="strong">₹9,400/m</div>
                </div>
                <button className="calc-btn">Calculate your EMI</button>
              </div>

              <div style={{ marginTop: 16 }}>
                <button className="book-btn">BOOK NOW</button>
                <button className="test-btn">FREE TEST DRIVE</button>
              </div>
            </div>
          </section>

          <div style={{ height: 80 }} />
        </div>

        {/* RIGHT: sticky fixed summary */}
        <aside className="car-details-right">
          <div className="right-card">
            <div className="title-row">
              <h3 className="car-title">{car.title}</h3>
              <button className="like-btn" aria-label="Save car">♡</button>
            </div>
            <div className="meta">{car.km.toLocaleString()} km • {car.fuel} • {car.transmission}</div>
            <div className="hub">Spinny Car Hub, {car.city}</div>

            <div className="price-box">
              <div className="muted">Fixed on road price</div>
              <div className="price">₹{(car.price / 100000).toFixed(2)} L</div>
              <div className="muted small">Includes RC transfer, insurance & more</div>
            </div>

            <div className="cta-row">
              <button className="book-btn full">BOOK NOW</button>
              <button className="test-btn full ghost">FREE TEST DRIVE</button>
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
}
