import React, { useRef, useState, useEffect, useCallback } from "react";
import "../styles/MobileCarousel.css";

/**
 * MobileCarousel
 * - Usage: place inside your MobileLayout main area
 * - Provide an array of slides if you want dynamic content; using defaultSlides below as example
 *
 * Replace slide.image with real image paths in public folder: process.env.PUBLIC_URL + '/your.jpg'
 */

const defaultSlides = [
  {
    id: "c1",
    title: "god promise",
    subtitle: "Introducing Hot Wheels — limited time deals",
    image: process.env.PUBLIC_URL + "/slide1.png",
    cta: "View all cars",
  },
  {
    id: "c2",
    title: "spinny perks",
    subtitle: "Clean cars, transparent pricing",
    image: process.env.PUBLIC_URL + "/slide2.png",
    cta: "View all cars",
  },
  {
    id: "c3",
    title: "trusted inspections",
    subtitle: "200-point evaluation",
    image: process.env.PUBLIC_URL + "/slide3.png",
    cta: "View all cars",
  },
];

export default function MobileCarousel({ slides = defaultSlides }) {
  const trackRef = useRef(null);
  const startX = useRef(0);
  const currentTranslate = useRef(0);
  const prevTranslate = useRef(0);
  const dragging = useRef(false);
  const [index, setIndex] = useState(0);
  const [width, setWidth] = useState(window.innerWidth);

  useEffect(() => {
    const onResize = () => setWidth(window.innerWidth);
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);

  // update translation when index changes
  useEffect(() => {
    const x = -index * width;
    prevTranslate.current = x;
    currentTranslate.current = x;
    setTranslate(x, true);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [index, width]);

  const clamp = (val, a, b) => Math.max(a, Math.min(b, val));

  const setTranslate = (x, withTransition = false) => {
    const node = trackRef.current;
    if (!node) return;
    node.style.transition = withTransition ? "transform 420ms cubic-bezier(.2,.9,.3,1)" : "none";
    node.style.transform = `translateX(${x}px)`;
  };

  // pointer/touch handlers
  const handleStart = (clientX) => {
    dragging.current = true;
    startX.current = clientX;
    trackRef.current.style.transition = "none";
  };

  const handleMove = (clientX) => {
    if (!dragging.current) return;
    const dx = clientX - startX.current;
    currentTranslate.current = prevTranslate.current + dx;
    setTranslate(currentTranslate.current, false);
  };

  const handleEnd = () => {
    if (!dragging.current) return;
    dragging.current = false;
    const movedBy = currentTranslate.current - prevTranslate.current; // dx
    const threshold = Math.max(40, width * 0.12);

    if (movedBy < -threshold && index < slides.length - 1) {
      setIndex((i) => i + 1);
    } else if (movedBy > threshold && index > 0) {
      setIndex((i) => i - 1);
    } else {
      // revert
      setTranslate(prevTranslate.current, true);
    }
  };

  // mouse events (for desktop or dev)
  useEffect(() => {
    const onMouseMove = (e) => handleMove(e.clientX);
    const onMouseUp = () => handleEnd();
    window.addEventListener("mousemove", onMouseMove);
    window.addEventListener("mouseup", onMouseUp);
    return () => {
      window.removeEventListener("mousemove", onMouseMove);
      window.removeEventListener("mouseup", onMouseUp);
    };
  }, []);

  // handlers bound to slide container
  const onTouchStart = (e) => handleStart(e.touches[0].clientX);
  const onTouchMove = (e) => handleMove(e.touches[0].clientX);
  const onTouchEnd = () => handleEnd();
  const onMouseDown = (e) => {
    e.preventDefault();
    handleStart(e.clientX);
  };

  const prev = useCallback(() => {
    setIndex((i) => clamp(i - 1, 0, slides.length - 1));
  }, [slides.length]);

  const next = useCallback(() => {
    setIndex((i) => clamp(i + 1, 0, slides.length - 1));
  }, [slides.length]);

  return (
    <div className="mc-root" role="region" aria-label="Promotional carousel (mobile)">
      <div className="mc-viewport"
        onTouchStart={onTouchStart}
        onTouchMove={onTouchMove}
        onTouchEnd={onTouchEnd}
        onMouseDown={onMouseDown}
      >
        <div
          ref={trackRef}
          className="mc-track"
          style={{ width: `${slides.length * width}px` }}
        >
          {slides.map((s, i) => (
            <article
              key={s.id}
              className="mc-slide"
              style={{
                width: `${width}px`,
                backgroundImage: `url(${s.image})`,
              }}
              aria-hidden={i !== index}
            >
              <div className="mc-gradient" />

              <div className="mc-content">
                <div className="mc-top-badge">
                  {/* small pill or logo if you want */}
                </div>

                <div className="mc-titles">
                  <h3 className="mc-title">{s.title}</h3>
                  <div className="mc-sub">{s.subtitle}</div>
                </div>

                <div className="mc-bottom">
                  <button className="mc-cta" onClick={() => window.alert(`CTA: ${s.cta}`)}>
                    {s.cta}
                  </button>
                </div>
              </div>
            </article>
          ))}
        </div>
      </div>

      {/* nav controls */}
      <div className="mc-controls">
        <button className="mc-arrow mc-left" onClick={prev} aria-label="Previous" disabled={index === 0}>‹</button>
        <div className="mc-dots">
          {slides.map((_, i) => (
            <button
              key={i}
              className={`mc-dot ${i === index ? "active" : ""}`}
              onClick={() => setIndex(i)}
              aria-label={`Go to slide ${i + 1}`}
            />
          ))}
        </div>
        <button className="mc-arrow mc-right" onClick={next} aria-label="Next" disabled={index === slides.length - 1}>›</button>
      </div>
    </div>
  );
}
