// CarDetails.jsx
import React, { useState, useRef, useEffect } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import "../styles/CarDetails.css";

export default function CarDetails() {
  const location = useLocation();
  const navigate = useNavigate();
  const params = useParams();
  // car passed via state when navigating from buy page
  const passedCar = location.state && location.state.car ? location.state.car : null;

  // If you want to support deep-linking by id, implement a fetch here using params.id
  const [car, setCar] = useState(passedCar);
  useEffect(() => {
    if (!car) {
      // fallback: you could fetch car from API using params.id
      // For now, show fallback UI (or navigate back)
      // Example:
      // fetch(`/api/cars/${params.id}`).then(r=>r.json()).then(setCar)
    }
  }, [car, params.id]);

  const images = (car && (car.images || [car.thumb])) || [];

  // carousel state + handlers
  const [idx, setIdx] = useState(0);
  const startX = useRef(null);
  useEffect(() => { if (idx >= images.length) setIdx(0); }, [images.length]);

  function prev() { setIdx((i) => (i - 1 + images.length) % images.length); }
  function next() { setIdx((i) => (i + 1) % images.length); }
  function onTouchStart(e) { startX.current = e.touches[0].clientX; }
  function onTouchEnd(e) {
    if (startX.current == null) return;
    const dx = e.changedTouches[0].clientX - startX.current;
    if (dx > 40) prev();
    else if (dx < -40) next();
    startX.current = null;
  }
  function goTo(i) { setIdx(i); }

  if (!car) {
    return (
      <div className="car-details-root">
        <div className="cd-header">
          <button onClick={() => navigate(-1)} className="cd-back">‚Üê</button>
          <div className="cd-title">Car details</div>
        </div>

        <main className="cd-main">
          <div className="cd-empty">
            <h3>Car not found</h3>
            <p>Looks like we don't have data for this car in state. Try going back or load via API.</p>
            <button className="btn-primary" onClick={() => navigate(-1)}>Back</button>
          </div>
        </main>
      </div>
    );
  }

  return (
    <div className="car-details-root">
      <header className="cd-header">
        <button onClick={() => navigate(-1)} className="cd-back" aria-label="Back">‚Üê</button>
        <div className="cd-title">{car.title ?? `${car.brand} ${car.model}`}</div>
        <div className="cd-actions">
          <button className="icon">üîç</button>
          <button className="icon">üîó</button>
          <button className="icon">‚ô°</button>
        </div>
      </header>

      <section className="cd-carousel" onTouchStart={onTouchStart} onTouchEnd={onTouchEnd}>
        <div className="cd-image-wrap">
          {images.length > 0 ? (
            <img src={images[idx]} alt={`${car.title} - ${idx + 1}`} className="cd-image" />
          ) : (
            <div className="cd-image placeholder">No image</div>
          )}
        </div>

        {/* controls */}
        {images.length > 1 && (
          <>
            <button className="cd-arrow left" onClick={prev} aria-label="Previous image">‚Äπ</button>
            <button className="cd-arrow right" onClick={next} aria-label="Next image">‚Ä∫</button>

            <div className="cd-dots">
              {images.map((_, i) => (
                <button key={i} className={`cd-dot ${i === idx ? "active" : ""}`} onClick={() => goTo(i)} aria-label={`Go to image ${i+1}`} />
              ))}
            </div>

            <div className="cd-thumbs">
              {images.map((src, i) => (
                <button key={i} className={`cd-thumb ${i === idx ? "active" : ""}`} onClick={() => goTo(i)}>
                  <img src={src} alt={`thumb-${i+1}`} />
                </button>
              ))}
            </div>
          </>
        )}
      </section>

      <main className="cd-main">
        <div className="cd-summary">
          <div className="cd-left">
            <h2 className="cd-name">{car.title}</h2>
            <div className="cd-meta">{car.km} ¬∑ {car.fuel} ¬∑ {car.trans}</div>

            <div className="price-card">
              <div className="price-label">Fixed on road price</div>
              <div className="price-large">{car.price ?? "‚Äî"}</div>
              <div className="price-sub">Includes RC transfer, insurance & more</div>
            </div>

            <div className="assurance">Assured ¬∑ High quality, less driven</div>
            <div className="location">üìç {car.location ?? "Unknown location"}</div>
          </div>

          <div className="cd-right">
            <button className="btn-ghost">Contact seller</button>
            <button className="btn-primary">Book now</button>
          </div>
        </div>

        <section className="cd-info-grid">
          <h3>Basic info</h3>
          <div className="info-row">
            <div className="info-col">
              <div className="info-key">Make Year</div>
              <div className="info-val">{car.year ?? "-"}</div>
            </div>
            <div className="info-col">
              <div className="info-key">Km driven</div>
              <div className="info-val">{car.km ?? "-"}</div>
            </div>
          </div>

          <div className="info-row">
            <div className="info-col">
              <div className="info-key">Transmission</div>
              <div className="info-val">{car.trans}</div>
            </div>
            <div className="info-col">
              <div className="info-key">Owner</div>
              <div className="info-val">{car.owner ?? "1st Owner"}</div>
            </div>
          </div>
        </section>

        <section className="cd-quality">
          <h3>Quality report</h3>
          <p className="muted">1430 parts evaluated by 5 automotive experts</p>
          <ul className="quality-list">
            <li>‚úÖ Meter not tampered</li>
            <li>‚úÖ Non-flooded</li>
            <li>‚úÖ Core structure intact</li>
          </ul>
          <div className="quality-cards">
            <div className="q-card"><div className="q-title">Core systems</div><div className="q-score">9.8</div></div>
            <div className="q-card"><div className="q-title">Supporting systems</div><div className="q-score">8.3</div></div>
            <div className="q-card"><div className="q-title">Interiors & AC</div><div className="q-score">8.8</div></div>
          </div>
        </section>
      </main>

      <footer className="cd-footer">
        <button className="btn-outline">Free test drive</button>
        <button className="btn-primary large">Book now</button>
      </footer>
    </div>
  );
}
