import React, { useEffect, useRef, useState } from "react";
import FilterBar from "../components/FilterBar"; // optional: you can keep or remove
import BottomNav from "../components/BottomNav";
import "../styles/BuyPage.css";

// Use your uploaded banner image (local path)
const HERO_BANNER = "/mnt/data/53e3c8d8-bc90-46c5-846f-ee263aec5ece.png";

/**
 * Small accessible dropdown used as a filter chip + menu.
 * Props:
 *  - label: string
 *  - options: [{ value, label }]
 *  - selected: string | null
 *  - onChange: fn(value)
 */
function FilterDropdown({ label, options = [], selected = null, onChange }) {
  const [open, setOpen] = useState(false);
  const ref = useRef(null);

  // close when clicking outside
  useEffect(() => {
    function onDoc(e) {
      if (!ref.current) return;
      if (!ref.current.contains(e.target)) setOpen(false);
    }
    window.addEventListener("click", onDoc);
    return () => window.removeEventListener("click", onDoc);
  }, []);

  // keyboard handling
  const handleKey = (e) => {
    if (e.key === "Escape") setOpen(false);
  };

  return (
    <div className="fp-root" ref={ref} onKeyDown={handleKey}>
      <button
        type="button"
        className={`fp-chip ${open ? "open" : ""}`}
        aria-haspopup="menu"
        aria-expanded={open}
        onClick={() => setOpen((v) => !v)}
      >
        <span className="fp-chip-label">{label}</span>
        <span className="fp-chip-caret">▾</span>
      </button>

      <div
        className="fp-menu"
        role="menu"
        aria-hidden={!open}
        style={{ display: open ? "block" : "none" }}
      >
        <ul role="none" className="fp-options">
          {/* first option = all / reset */}
          <li role="none">
            <button
              role="menuitem"
              className={`fp-opt ${selected === null ? "selected" : ""}`}
              onClick={() => {
                onChange?.(null);
                setOpen(false);
              }}
            >
              All
            </button>
          </li>

          {options.map((o) => (
            <li role="none" key={o.value}>
              <button
                role="menuitem"
                className={`fp-opt ${selected === o.value ? "selected" : ""}`}
                onClick={() => {
                  onChange?.(o.value);
                  setOpen(false);
                }}
              >
                {o.label}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

/**
 * BuyPage - main page scaffold with top filter dropdowns.
 */
export default function BuyPage() {
  // sample filters' option lists
  const yearOptions = [
    { value: "2024", label: "2024" },
    { value: "2023", label: "2023" },
    { value: "2022", label: "2022" },
    { value: "2019-2021", label: "2019 - 2021" },
    { value: "older", label: "Before 2019" },
  ];

  const transmissionOptions = [
    { value: "auto", label: "Automatic" },
    { value: "manual", label: "Manual" },
    { value: "am", label: "AMT" },
  ];

  const bodyOptions = [
    { value: "hatch", label: "Hatchback" },
    { value: "sedan", label: "Sedan" },
    { value: "suv", label: "SUV" },
    { value: "mpv", label: "MPV" },
  ];

  const colorOptions = [
    { value: "white", label: "White" },
    { value: "black", label: "Black" },
    { value: "silver", label: "Silver" },
    { value: "red", label: "Red" },
  ];

  // selected filter state
  const [selYear, setSelYear] = useState(null);
  const [selTrans, setSelTrans] = useState(null);
  const [selBody, setSelBody] = useState(null);
  const [selColor, setSelColor] = useState(null);

  // sample car data (replace with API)
  const [cars, setCars] = useState([]);
  useEffect(() => {
    const sample = [
      {
        id: "c1",
        title: "2018 Tata Tiago",
        variant: "XT CNG (Outside Fitted)",
        price: "₹3.74 Lakh",
        emi: "EMI ₹6,369/m*",
        km: "61,000 km",
        fuel: "CNG",
        trans: "Manual",
        location: "DL12",
        thumb: process.env.PUBLIC_URL + "/baleno.avif",
      },
      {
        id: "c2",
        title: "2022 Jeep Meridian",
        variant: "Limited (O) 4X2 AT",
        price: "₹22.75 Lakh",
        emi: "EMI ₹39,126/m*",
        km: "24,500 km",
        fuel: "Diesel",
        trans: "Automatic",
        location: "HR98",
        thumb: process.env.PUBLIC_URL + "/venue.avif",
      },
    ];
    setCars(sample);
  }, []);

  // derived filtered cars (basic example)
  const filtered = cars.filter((c) => {
    if (selTrans && c.trans && selTrans !== c.trans.toLowerCase().slice(0, 3)) {
      // we matched "auto" & "manual" options above to values; this is example logic
      // you'll replace with exact matching to your data fields
      return selTrans === "auto" ? c.trans.toLowerCase().includes("auto") : c.trans.toLowerCase().includes("man");
    }
    if (selBody) {
      // example: no body data in sample so skip in demo
    }
    if (selYear) {
      // example: no year in sample so skip
    }
    if (selColor) {
      // example: skip
    }
    return true;
  });

  return (
    <div className="buypage-root">
      {/* Topbar / header */}
      <header className="buy-header">
        <div className="buy-header-inner">
          <button className="hamburger" aria-label="Open menu">☰</button>
          <div className="brand">CarsDedo</div>
          <div className="header-actions">
            <button className="action">Sell</button>
            <button className="action active">Buy</button>
          </div>
        </div>

        {/* banner */}
        <div className="hero-banner">
          <img src={HERO_BANNER} alt="Top deals" className="hero-img" />
        </div>

        {/* --- FILTER DROPDOWNS (top row) --- */}
        <div className="filters-top">
          <FilterDropdown
            label="Year"
            options={yearOptions}
            selected={selYear}
            onChange={(v) => setSelYear(v)}
          />
          <FilterDropdown
            label="Transmission"
            options={transmissionOptions}
            selected={selTrans}
            onChange={(v) => setSelTrans(v)}
          />
          <FilterDropdown
            label="Body Type"
            options={bodyOptions}
            selected={selBody}
            onChange={(v) => setSelBody(v)}
          />
          <FilterDropdown
            label="Color"
            options={colorOptions}
            selected={selColor}
            onChange={(v) => setSelColor(v)}
          />
        </div>
      </header>

      {/* Secondary filter chips row (slidable) - you can reuse the FilterBar component here */}
      <div className="filters-chip-row">
        {/* optional: re-use FilterBar if you have it, else simple scrollable chips */}
        <div className="chip-track" role="toolbar" aria-label="Quick filters">
          <button className="chip">Year ▾</button>
          <button className="chip">Transmission ▾</button>
          <button className="chip">Body Type ▾</button>
          <button className="chip">Color ▾</button>
          <button className="chip">Fuel Type ▾</button>
          <button className="chip">Price Range ▾</button>
        </div>
      </div>

      {/* Results header */}
      <div className="results-header">
        <h3>1140 Used cars in Delhi</h3>
      </div>

      {/* Product list */}
      <main className="buy-main">
        <div className="list">
          {filtered.map((car) => (
            <article key={car.id} className="car-card">
              <div className="car-thumb">
                <img src={car.thumb} alt={car.title} />
              </div>

              <div className="car-content">
                <div className="car-row">
                  <div>
                    <div className="car-title">{car.title}</div>
                    <div className="car-variant">{car.variant}</div>
                  </div>

                  <div className="car-price">
                    <div className="price">{car.price}</div>
                    <div className="emi">{car.emi}</div>
                  </div>
                </div>

                <div className="car-tags">
                  <span className="tag">{car.km}</span>
                  <span className="tag">{car.fuel}</span>
                  <span className="tag">{car.trans}</span>
                  <span className="tag">{car.location}</span>
                </div>

                <div className="car-footer">
                  <div className="hub">HUB · Trillium Avenue, Gurgaon</div>
                  <button className="fav" aria-label="Add to wishlist">♡</button>
                </div>
              </div>
            </article>
          ))}
        </div>
      </main>

      <BottomNav />
    </div>
  );
}
