import React, { useState, useEffect, useRef, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import carsData from "../data/cars";
import "../styles/CarDetails.css";

const TOPBAR_HEIGHT = 72;

export default function CarDetailsWeb() {
  const { id } = useParams();
  const navigate = useNavigate();

  // Always compute car FIRST (NOT conditional)
  const car = carsData.find((c) => Number(c.id) === Number(id));

  // Hooks MUST run unconditionally
  const images = car?.images?.length ? car.images : car ? [car.image] : [];

  const [heroImage, setHeroImage] = useState(images[0] || "");
  const containerRef = useRef(null);
  const leftRef = useRef(null);
  const heroRef = useRef(null);
  const rightCardRef = useRef(null);

  // section refs
  const overviewRef = useRef(null);
  const reportRef = useRef(null);
  const specsRef = useRef(null);
  const financeRef = useRef(null);

  // sticky tabs + active section
  const [activeTab, setActiveTab] = useState("overview");
  const [showStickyTabs, setShowStickyTabs] = useState(false);
  const [rightMode, setRightMode] = useState("fixed");

  // Sticky Tabs observer
  useEffect(() => {
    if (!heroRef.current) return;

    const io = new IntersectionObserver(
      (entries) => {
        setShowStickyTabs(!entries[0].isIntersecting);
      },
      { threshold: 0.08 }
    );

    io.observe(heroRef.current);
    return () => io.disconnect();
  }, []);

  // ScrollSpy observer
  useEffect(() => {
    const sections = [
      overviewRef.current,
      reportRef.current,
      specsRef.current,
      financeRef.current,
    ].filter(Boolean);

    if (!sections.length) return;

    const io = new IntersectionObserver(
      (entries) => {
        const visible = entries.filter((e) => e.isIntersecting);
        if (visible.length > 0) {
          visible.sort((a, b) => b.intersectionRatio - a.intersectionRatio);
          setActiveTab(visible[0].target.id);
        }
      },
      {
        root: null,
        rootMargin: `-${TOPBAR_HEIGHT + 8}px 0px -35% 0px`,
        threshold: [0.15, 0.35, 0.6],
      }
    );

    sections.forEach((s) => io.observe(s));
    return () => io.disconnect();
  }, []);

  // Right card behavior (fixed → absolute → static)
  const computeRightMode = useCallback(() => {
    const sentinel = document.getElementById("right-sentinel");

    if (!rightCardRef.current || !leftRef.current || !sentinel) {
      setRightMode("fixed");
      return;
    }

    const sentinelRect = sentinel.getBoundingClientRect();
    const viewportHeight = window.innerHeight;

    // Footer reached → stop fixing
    if (sentinelRect.top <= viewportHeight - 20) {
      setRightMode("static");
      return;
    }

    // Left column height too small → absolute inside left
    const leftRect = leftRef.current.getBoundingClientRect();
    const cardHeight = rightCardRef.current.offsetHeight;

    if (leftRect.bottom - TOPBAR_HEIGHT < cardHeight + 40) {
      setRightMode("absolute");
      return;
    }

    setRightMode("fixed");
  }, []);

  useEffect(() => {
    computeRightMode();
    window.addEventListener("scroll", computeRightMode, { passive: true });
    window.addEventListener("resize", computeRightMode);

    return () => {
      window.removeEventListener("scroll", computeRightMode);
      window.removeEventListener("resize", computeRightMode);
    };
  }, [computeRightMode]);

  // ScrollTo helper
  const scrollTo = (ref) => {
    if (!ref.current) return;
    const top =
      ref.current.getBoundingClientRect().top +
      window.scrollY -
      TOPBAR_HEIGHT -
      8;
    window.scrollTo({ top, behavior: "smooth" });
  };

  // AFTER all hooks — now we can conditionally return
  if (!car) {
    return (
      <div style={{ padding: 40 }}>
        <h2>Car not found</h2>
        <button onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  return (
    <div className="car-details-page" style={{ "--topbar-height": `${TOPBAR_HEIGHT}px` }}>
      {/* sticky small tab bar (appears when hero scrolled away) */}
      <div className={`cd-mini-tabs ${showStickyTabs ? "visible" : ""}`}>
        <div className="cd-mini-inner">
          <button className={activeTab === "overview" ? "active" : ""} onClick={() => scrollTo(overviewRef)}>Overview</button>
          <button className={activeTab === "report" ? "active" : ""} onClick={() => scrollTo(reportRef)}>Report</button>
          <button className={activeTab === "specs" ? "active" : ""} onClick={() => scrollTo(specsRef)}>Feature & Specs</button>
          <button className={activeTab === "finance" ? "active" : ""} onClick={() => scrollTo(financeRef)}>Finance</button>
        </div>
      </div>

      <div className="cd-container" ref={containerRef}>
        {/* left column (main content) */}
        <main className="cd-left" ref={leftRef}>
          <div className="cd-hero" ref={heroRef}>
            <div className="cd-hero-img">
              <img src={heroImage} alt={car.title} />
            </div>

            {/* thumbnails below hero */}
            <div className="cd-thumbs">
              {images.map((src, idx) => (
                <button
                  key={idx}
                  className={`cd-thumb ${src === heroImage ? "selected" : ""}`}
                  onClick={() => setHeroImage(src)}
                  aria-label={`thumbnail-${idx}`}
                >
                  <img src={src} alt={`thumb-${idx}`} />
                </button>
              ))}
            </div>
          </div>

          {/* navigation tabs under hero (full width within left column) */}
          <nav className="cd-tabs">
            <button className={activeTab === "overview" ? "active" : ""} onClick={() => scrollTo(overviewRef)}>Overview</button>
            <button className={activeTab === "report" ? "active" : ""} onClick={() => scrollTo(reportRef)}>Report</button>
            <button className={activeTab === "specs" ? "active" : ""} onClick={() => scrollTo(specsRef)}>Feature & Specs</button>
            <button className={activeTab === "finance" ? "active" : ""} onClick={() => scrollTo(financeRef)}>Finance</button>
          </nav>

          {/* ==== sections ==== */}
          <section id="overview" ref={overviewRef} className="cd-section">
            <h2>Car Overview</h2>
            <div className="cd-card overview-card">
              <div className="grid">
                <div>
                  <div className="label">Make Year</div>
                  <div className="val">May 2024</div>
                </div>
                <div>
                  <div className="label">Registration Year</div>
                  <div className="val">Jun 2024</div>
                </div>
                <div>
                  <div className="label">Fuel Type</div>
                  <div className="val">{car.fuel}</div>
                </div>
              </div>

              <div className="grid">
                <div>
                  <div className="label">Km driven</div>
                  <div className="val">{car.km.toLocaleString()} km</div>
                </div>
                <div>
                  <div className="label">Transmission</div>
                  <div className="val">{car.transmission}</div>
                </div>
                <div>
                  <div className="label">No. of Owner</div>
                  <div className="val">{car.owner || "1st Owner"}</div>
                </div>
              </div>

              <div className="grid last">
                <div>
                  <div className="label">Car Location</div>
                  <div className="val">{car.city}</div>
                </div>
              </div>
            </div>
          </section>

          <section id="report" ref={reportRef} className="cd-section">
            <h2>Quality report</h2>
            <div className="cd-card report-card">
              <p className="muted">1571 parts evaluated by 5 automotive experts</p>
              <div className="report-grid">
                <div className="report-box"><strong>Core systems</strong><span className="score">9.8</span></div>
                <div className="report-box"><strong>Supporting systems</strong><span className="score">9.7</span></div>
                <div className="report-box"><strong>Interiors & AC</strong><span className="score">9.6</span></div>
                <div className="report-box"><strong>Exteriors & lights</strong><span className="score">9.3</span></div>
              </div>
            </div>
          </section>

          <section id="specs" ref={specsRef} className="cd-section">
            <h2>Feature & Specs</h2>
            <div className="cd-card specs-card">
              <div className="specs-grid">
                <div className="spec">
                  <div className="label">Mileage (ARAI)</div>
                  <div className="val">22.35 kmpl</div>
                </div>
                <div className="spec">
                  <div className="label">Boot space</div>
                  <div className="val">318 litres</div>
                </div>
                <div className="spec">
                  <div className="label">Displacement</div>
                  <div className="val">1197 cc</div>
                </div>
              </div>

              <div className="features-block">
                <h4>Top Features of this car</h4>
                <div className="features-grid">
                  <ul><li>Airbags</li><li>Hill hold control</li><li>Parking sensors</li></ul>
                  <ul><li>Rear defogger</li><li>Power boot</li></ul>
                  <ul><li>Automatic climate control</li><li>Adjustable ORVM</li></ul>
                </div>
              </div>
            </div>
          </section>

          <section id="finance" ref={financeRef} className="cd-section">
            <h2>Finance</h2>
            <div className="cd-card finance-card">
              <div className="finance-grid">
                <div>
                  <div className="big-emi">₹9,400</div>
                  <div className="muted">per month (approx)</div>
                </div>
                <div>
                  <div className="label">Loan Amount</div>
                  <div className="val">₹{(car.price - 110400).toLocaleString()}</div>
                  <div className="label">Duration</div>
                  <div className="val">66 Months</div>
                </div>
              </div>
              <div style={{ marginTop: 12 }}>
                <button className="btn primary">Check eligibility</button>
              </div>
            </div>
          </section>
        </main>

        {/* right column */}
        <aside className="cd-right">
          <div
            ref={rightCardRef}
            className={`cd-right-card mode-${rightMode}`}
            style={
              rightMode === "fixed"
                ? { position: "fixed", top: `${TOPBAR_HEIGHT + 14}px`, right: `calc((100% - 1180px)/2 + 760px + 18px)` }
                : rightMode === "absolute"
                ? { position: "absolute", bottom: 0, right: 0 }
                : { position: "relative" }
            }
          >
            <div className="right-inner">
              <div className="title">
                <h3>{car.title}</h3>
                <button className="like">♡</button>
              </div>

              <div className="meta">{car.km.toLocaleString()} km • {car.fuel} • {car.transmission}</div>
              <div className="hub">Spinny Car Hub, {car.city}</div>

              <div className="price-block">
                <div className="label muted">Fixed on road price</div>
                <div className="price">₹{(car.price / 100000).toFixed(2)} L</div>
                <div className="muted small">Includes RC transfer, insurance & more</div>
              </div>

              <div className="cta">
                <button className="btn book">BOOK NOW</button>
                <button className="btn test">FREE TEST DRIVE</button>
              </div>

              <div className="share">
                <small>Share with a friend :</small>
                <div className="icons">📷 · f · ✕ · ✉️</div>
              </div>
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
}
