// BuyPage.jsx
import React, { useEffect, useRef, useState, useLayoutEffect } from "react";
import { createPortal } from "react-dom";
import BottomNav from "../components/BottomNav";
import "../styles/BuyPage.css";

// Use your uploaded banner image (local path)
const HERO_BANNER = "/mnt/data/53e3c8d8-bc90-46c5-846f-ee263aec5ece.png";

/* --------------------------
   Portal-aware FilterDropdown
   (closes previous dropdowns via closeAllDropdowns event)
   -------------------------- */
function FilterDropdown({ label, options = [], selected = null, onChange }) {
  const [open, setOpen] = useState(false);
  const [menuStyle, setMenuStyle] = useState({});
  const rootRef = useRef(null);
  const btnRef = useRef(null);

  useEffect(() => {
    function onDoc(e) {
      if (rootRef.current && rootRef.current.contains(e.target)) return;
      setOpen(false);
    }
    window.addEventListener("click", onDoc);
    return () => window.removeEventListener("click", onDoc);
  }, []);

  useEffect(() => {
    function onCloseAll() {
      setOpen(false);
    }
    window.addEventListener("closeAllDropdowns", onCloseAll);
    return () => window.removeEventListener("closeAllDropdowns", onCloseAll);
  }, []);

  useLayoutEffect(() => {
    if (!open) return;
    function updatePos() {
      const btn = btnRef.current;
      if (!btn) return;
      const rect = btn.getBoundingClientRect();
      const minWidth = rect.width;
      let left = rect.left;
      let top = rect.bottom + 8;
      const maxWidth = Math.max(document.documentElement.clientWidth, window.innerWidth || 0);
      const estimatedMenuWidth = Math.max(minWidth, 200);
      if (left + estimatedMenuWidth > maxWidth - 12) {
        left = Math.max(12, maxWidth - estimatedMenuWidth - 12);
      }
      setMenuStyle({
        position: "fixed",
        top: `${top}px`,
        left: `${left}px`,
        minWidth: `${minWidth}px`,
        zIndex: 999999,
      });
    }
    updatePos();
    window.addEventListener("resize", updatePos);
    window.addEventListener("scroll", updatePos, true);
    return () => {
      window.removeEventListener("resize", updatePos);
      window.removeEventListener("scroll", updatePos, true);
    };
  }, [open]);

  const handleKey = (e) => {
    if (e.key === "Escape") setOpen(false);
  };

  const menu = open ? (
    <div className="fp-menu" role="menu" aria-hidden={!open} style={{ ...menuStyle, display: open ? "block" : "none" }} onKeyDown={handleKey}>
      <ul role="none" className="fp-options">
        <li role="none">
          <button role="menuitem" className={`fp-opt ${selected === null ? "selected" : ""}`} onClick={() => { onChange?.(null); setOpen(false); }}>
            All
          </button>
        </li>
        {options.map((o) => (
          <li role="none" key={o.value}>
            <button role="menuitem" className={`fp-opt ${selected === o.value ? "selected" : ""}`} onClick={() => { onChange?.(o.value); setOpen(false); }}>
              {o.label}
            </button>
          </li>
        ))}
      </ul>
    </div>
  ) : null;

  return (
    <div className="fp-root" ref={rootRef}>
      <button
        type="button"
        ref={btnRef}
        className={`fp-chip ${open ? "open" : ""}`}
        aria-haspopup="menu"
        aria-expanded={open}
        onClick={(e) => {
          e.stopPropagation();
          if (!open) {
            window.dispatchEvent(new Event("closeAllDropdowns"));
            setOpen(true);
          } else setOpen(false);
        }}
        onKeyDown={handleKey}
      >
        <span className="fp-chip-label">{label}</span>
        <span className="fp-chip-caret">▾</span>
      </button>

      {createPortal(menu, document.body)}
    </div>
  );
}

/* --------------------------
   SortModal - bottom sheet
   -------------------------- */
function SortModal({ open, onClose, value, onChange }) {
  useEffect(() => {
    function onKey(e) {
      if (e.key === "Escape") onClose();
    }
    if (open) window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [open, onClose]);

  if (!open) return null;

  return createPortal(
    <>
      <div className="sheet-backdrop" onClick={onClose} />
      <div className="sheet sheet-bottom" role="dialog" aria-modal="true" aria-label="Sort options">
        <div className="sheet-handle" />
        <div className="sheet-content">
          <h4 className="sheet-title">Sort by</h4>
          <ul className="sheet-list" role="radiogroup">
            {[
              { key: "relevance", label: "Relevance (Default)" },
              { key: "discount_desc", label: "Discount - High to Low" },
              { key: "price_asc", label: "Price - Low to High" },
              { key: "price_desc", label: "Price - High to Low" },
              { key: "km_asc", label: "KM Driven - Low to High" },
              { key: "year_desc", label: "Year - New to Old" },
              { key: "newest", label: "Newest First" },
            ].map((opt) => (
              <li key={opt.key} className="sheet-row" onClick={() => onChange(opt.key)}>
                <div>{opt.label}</div>
                <div className={`radio-outer ${value === opt.key ? "checked" : ""}`}>
                  <div className="radio-inner" />
                </div>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>,
    document.body
  );
}

/* --------------------------
   FilterDrawer (embedded) - redesigned with fancy checkboxes + left nav
   -------------------------- */

function IconCheck({ checked }) {
  return (
    <svg className={`checkmark ${checked ? "show" : ""}`} width="14" height="14" viewBox="0 0 24 24" aria-hidden>
      <path fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" d="M20 6L9 17l-5-5" />
    </svg>
  );
}

function FancyCheckbox({ checked = false, onChange, label, id }) {
  return (
    <label className="f-chk" htmlFor={id}>
      <input id={id} type="checkbox" checked={checked} onChange={(e) => onChange(e.target.checked)} />
      <span className="f-chk-box" aria-hidden>
        <IconCheck checked={checked} />
      </span>
      <span className="f-chk-label">{label}</span>
    </label>
  );
}

function ColorCheckbox({ colorCode = "#ddd", checked = false, onChange, label, id }) {
  return (
    <label className="f-chk color" htmlFor={id}>
      <input id={id} type="checkbox" checked={checked} onChange={(e) => onChange(e.target.checked)} />
      <span className="f-chk-box" aria-hidden>
        <span className="swatch" style={{ backgroundColor: colorCode }} />
        <IconCheck checked={checked} />
      </span>
      <span className="f-chk-label">{label}</span>
    </label>
  );
}

function FilterDrawerEmbedded({ open, onClose, appliedFilters = {}, onApply, onClear }) {
  const [local, setLocal] = useState({
    year: appliedFilters.year ? [appliedFilters.year] : [],
    brands: appliedFilters.brands ?? [],
    colors: appliedFilters.color ? [appliedFilters.color] : [],
    transmission: appliedFilters.trans ? [appliedFilters.trans] : [],
    features: appliedFilters.features ?? [],
    seats: appliedFilters.seats ?? [],
    rto: appliedFilters.rto ?? [],
  });

  const categories = [
    "Saved Filters",
    "Price Range",
    "Brands + Models",
    "Year",
    "Kms Driven",
    "Fuel Type",
    "Body Type",
    "Transmission",
    "Car Category",
    "Color",
    "Features",
    "Seats",
    "RTO",
    "Owner",
    "Spinny Hubs",
    "Availability",
  ];
  const [active, setActive] = useState("Price Range");
  const [brandQuery, setBrandQuery] = useState("");

  useEffect(() => {
    if (open) {
      setLocal({
        year: appliedFilters.year ? [appliedFilters.year] : [],
        brands: appliedFilters.brands ?? [],
        colors: appliedFilters.color ? [appliedFilters.color] : [],
        transmission: appliedFilters.trans ? [appliedFilters.trans] : [],
        features: appliedFilters.features ?? [],
        seats: appliedFilters.seats ?? [],
        rto: appliedFilters.rto ?? [],
      });
      setActive("Price Range");
      setBrandQuery("");
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
    return () => (document.body.style.overflow = "");
  }, [open, appliedFilters]);

  useEffect(() => {
    function onKey(e) {
      if (e.key === "Escape") onClose();
    }
    if (open) window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [open, onClose]);

  if (!open) return null;

  // sample options (replace with real data)
  const brandOptions = ["Maruti Suzuki", "Hyundai", "Honda", "Tata", "Toyota", "Ford", "Mahindra", "Kia", "MG", "Skoda", "Volkswagen"];
  const yearOptions = ["2023 & above", "2021 & above", "2019 & above", "2017 & above", "2015 & above", "2013 & above"];
  const transmissionOptions = ["Automatic", "Manual", "AMT"];
  const featuresOptions = ["Sunroof", "Keyless start", "ABS", "Airbags", "Rear camera", "Parking sensor", "Power windows", "Power steering"];
  const seatsOptions = ["4 seater", "5 seater", "6+ seater"];
  const rtoOptions = ["UP", "DL", "HR"];
  const colorMap = [
    { name: "Yellow, Golden", code: "#FFF56D" },
    { name: "Red, Maroon", code: "#C8342A" },
    { name: "Beige, Bronze, Brown", code: "#D9CBB3" },
    { name: "Violet, Purple, Wine", code: "#7B35D9" },
    { name: "White", code: "#FFFFFF" },
    { name: "Silver", code: "#CFCFCF" },
    { name: "Grey", code: "#949494" },
    { name: "Black", code: "#000000" },
    { name: "Blue", code: "#2D76E6" },
    { name: "Green", code: "#3BB54A" },
    { name: "Orange", code: "#F08D2E" },
  ];

  function toggleArray(key, value) {
    setLocal((s) => {
      const prev = s[key] || [];
      const found = prev.includes(value);
      return { ...s, [key]: found ? prev.filter((x) => x !== value) : [...prev, value] };
    });
  }

  function singleSelect(key, value) {
    setLocal((s) => ({ ...s, [key]: s[key] && s[key][0] === value ? [] : [value] }));
  }

  function handleApply() {
    const out = {
      year: local.year[0] ?? null,
      brands: local.brands,
      color: local.colors[0] ?? null,
      trans: local.transmission[0] ?? null,
      features: local.features,
      seats: local.seats,
      rto: local.rto,
    };
    onApply?.(out);
    onClose?.();
  }

  function handleClear() {
    setLocal({ year: [], brands: [], colors: [], transmission: [], features: [], seats: [], rto: [] });
    onClear?.();
  }

  const filteredBrands = brandOptions.filter((b) => b.toLowerCase().includes(brandQuery.toLowerCase()));

  return createPortal(
    <>
      <div className="filter-backdrop" onClick={onClose} aria-hidden />
      <aside className="filter-drawer" role="dialog" aria-modal="true" aria-label="Filter">
        <header className="filter-head">
          <button className="back" onClick={onClose} aria-label="Close filter">←</button>
          <div className="title">Filter</div>
          <button className="clear-all" onClick={handleClear}>CLEAR ALL</button>
        </header>

        <div className="filter-body">
          <nav className="filter-nav" aria-label="Filter categories">
            {categories.map((c) => (
              <button key={c} className={`filter-nav-item ${c === active ? "active" : ""}`} onClick={() => setActive(c)}>
                {c}
              </button>
            ))}
          </nav>

          <div className="filter-panel">
            {active === "Price Range" && (
              <div className="panel-section">
                <h4 className="panel-title">₹50,000 - ₹70,00,000</h4>
                <div className="price-visual">
                  <div className="price-rail" />
                  <div className="price-handle top" />
                  <div className="price-handle bottom" />
                  <div className="price-bubble top">₹70,00,000</div>
                  <div className="price-bubble bottom">₹50,000</div>
                </div>
              </div>
            )}

            {active === "Brands + Models" && (
              <div className="panel-section">
                <label className="search">
                  <input placeholder="Search" value={brandQuery} onChange={(e) => setBrandQuery(e.target.value)} />
                  <span className="search-icon">🔍</span>
                </label>
                <div className="brand-list">
                  {filteredBrands.map((b) => {
                    const checked = local.brands.includes(b);
                    return (
                      <FancyCheckbox key={b} id={`brand-${b}`} label={b} checked={checked} onChange={(v) => toggleArray("brands", b)} />
                    );
                  })}
                </div>
              </div>
            )}

            {active === "Year" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="radio-list">
                  {yearOptions.map((y) => {
                    const checked = local.year.includes(y);
                    return (
                      <label key={y} className="radio-row" onClick={() => singleSelect("year", y)}>
                        <input type="radio" name="year" checked={checked} onChange={() => singleSelect("year", y)} />
                        <span>{y}</span>
                      </label>
                    );
                  })}
                </div>
              </div>
            )}

            {active === "Transmission" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {transmissionOptions.map((t) => (
                    <FancyCheckbox key={t} id={`trans-${t}`} label={t} checked={local.transmission.includes(t)} onChange={() => toggleArray("transmission", t)} />
                  ))}
                </div>
              </div>
            )}

            {active === "Color" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="color-grid">
                  {colorMap.map((c) => {
                    const checked = local.colors.includes(c.name);
                    return <ColorCheckbox key={c.name} id={`color-${c.name}`} label={c.name} colorCode={c.code} checked={checked} onChange={() => toggleArray("colors", c.name)} />;
                  })}
                </div>
              </div>
            )}

            {active === "Features" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {featuresOptions.map((f) => (
                    <FancyCheckbox key={f} id={`feat-${f}`} label={f} checked={local.features.includes(f)} onChange={() => toggleArray("features", f)} />
                  ))}
                </div>
              </div>
            )}

            {active === "Seats" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {seatsOptions.map((s) => (
                    <FancyCheckbox key={s} id={`seat-${s}`} label={s} checked={local.seats.includes(s)} onChange={() => toggleArray("seats", s)} />
                  ))}
                </div>
              </div>
            )}

            {active === "RTO" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {rtoOptions.map((r) => (
                    <FancyCheckbox key={r} id={`rto-${r}`} label={r} checked={local.rto.includes(r)} onChange={() => toggleArray("rto", r)} />
                  ))}
                </div>
              </div>
            )}

            {["Saved Filters", "Kms Driven", "Fuel Type", "Body Type", "Car Category", "Owner", "Spinny Hubs", "Availability"].includes(active) && (
              <div className="panel-section">
                <p className="placeholder">Options for <strong>{active}</strong> go here — checkboxes, lists or controls as required.</p>
              </div>
            )}
          </div>
        </div>

        <footer className="filter-footer">
          <button className="btn-outline" onClick={handleClear}>Reset</button>
          <button className="btn-primary" onClick={handleApply}>Apply</button>
        </footer>
      </aside>
    </>,
    document.body
  );
}

/* --------------------------
   SwipableInfo (simple carousel)
   -------------------------- */
function SwipableInfo({ slides = [] }) {
  const [idx, setIdx] = useState(0);
  const startXRef = useRef(null);

  useEffect(() => {
    if (!slides || slides.length === 0) return;
    setIdx((i) => Math.min(i, slides.length - 1));
  }, [slides]);

  const prev = () => setIdx((i) => (i - 1 + slides.length) % slides.length);
  const next = () => setIdx((i) => (i + 1) % slides.length);

  function onTouchStart(e) {
    startXRef.current = e.touches[0].clientX;
  }
  function onTouchEnd(e) {
    if (startXRef.current == null) return;
    const endX = e.changedTouches[0].clientX;
    const diff = endX - startXRef.current;
    if (diff > 40) prev();
    else if (diff < -40) next();
    startXRef.current = null;
  }

  if (!slides || slides.length === 0) return null;
  const current = slides[idx];

  return (
    <div className="swipe-root" onTouchStart={onTouchStart} onTouchEnd={onTouchEnd} aria-roledescription="carousel">
      <div className="swipe-card">
        <div className="swipe-card-inner">
          <h4 className="swipe-title">{current.title ?? `Info ${idx + 1}`}</h4>
          <p className="swipe-text">{current.text ?? current}</p>
        </div>
        <div className="swipe-controls">
          <button aria-label="Previous" onClick={prev} className="swipe-btn">‹</button>
          <div className="swipe-dots" role="tablist">
            {slides.map((_, i) => (
              <button key={i} onClick={() => setIdx(i)} className={`swipe-dot ${i === idx ? "active" : ""}`} aria-label={`Go to slide ${i + 1}`} />
            ))}
          </div>
          <button aria-label="Next" onClick={next} className="swipe-btn">›</button>
        </div>
      </div>
    </div>
  );
}

