import React, { useEffect, useRef, useState, useCallback } from "react";
import "../styles/BodyTypeCarousel.css";

/**
 * BodyTypeCarousel
 * - toggle: Hatchback | SUV | Sedan
 * - swipeable cards with peeking
 * - place your small icon images inside the pill (see pillImg property)
 *
 * Replace sample images in `DATA` with your own images in public/ or pass via props.
 */

const DATA = {
  hatchback: [
    { id: "h1", title: "Maruti Baleno", price: "₹3.62 Lakh", km: "33,500 km", img: process.env.PUBLIC_URL + "/car1.png" },
    { id: "h2", title: "Honda Jazz", price: "₹4.20 Lakh", km: "21,000 km", img: process.env.PUBLIC_URL + "/car2.png" },
    { id: "h3", title: "Hyundai i20", price: "₹5.10 Lakh", km: "12,000 km", img: process.env.PUBLIC_URL + "/car3.png" },
  ],
  suv: [
    { id: "s1", title: "Kia Seltos", price: "₹12.3 Lakh", km: "18,500 km", img: process.env.PUBLIC_URL + "/suv1.png" },
    { id: "s2", title: "Hyundai Creta", price: "₹11.8 Lakh", km: "22,000 km", img: process.env.PUBLIC_URL + "/suv2.png" },
  ],
  sedan: [
    { id: "d1", title: "Honda City", price: "₹9.1 Lakh", km: "29,000 km", img: process.env.PUBLIC_URL + "/sedan1.png" },
    { id: "d2", title: "Skoda Octavia", price: "₹14.2 Lakh", km: "40,000 km", img: process.env.PUBLIC_URL + "/sedan2.png" },
  ],
};

export default function BodyTypeCarousel() {
  const [active, setActive] = useState("hatchback"); // hatchback | suv | sedan
  const slides = DATA[active] || [];
  const containerRef = useRef(null);
  const trackRef = useRef(null);

  const startX = useRef(0);
  const dragging = useRef(false);
  const prevTranslate = useRef(0);
  const currentTranslate = useRef(0);

  const [cardWidth, setCardWidth] = useState(0);
  const [index, setIndex] = useState(0);

  // compute card width (peeking look — 86% width)
  useEffect(() => {
    const updateSize = () => {
      if (!containerRef.current) return;
      const w = containerRef.current.offsetWidth;
      setCardWidth(Math.round(w * 0.86));
    };
    updateSize();
    window.addEventListener("resize", updateSize);
    return () => window.removeEventListener("resize", updateSize);
  }, []);

  // reset index on body-type change
  useEffect(() => {
    setIndex(0);
  }, [active]);

  // move track on index or cardWidth change
  useEffect(() => {
    prevTranslate.current = -index * cardWidth;
    currentTranslate.current = prevTranslate.current;
    setTransform(prevTranslate.current, true);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [index, cardWidth]);

  const setTransform = (x, withTransition = false) => {
    const n = trackRef.current;
    if (!n) return;
    n.style.transition = withTransition ? "transform 420ms cubic-bezier(.2,.9,.3,1)" : "none";
    n.style.transform = `translateX(${x}px)`;
  };

  // pointer logic
  const onStart = (clientX) => {
    dragging.current = true;
    startX.current = clientX;
    trackRef.current.style.transition = "none";
  };
  const onMove = (clientX) => {
    if (!dragging.current) return;
    const dx = clientX - startX.current;
    currentTranslate.current = prevTranslate.current + dx;
    setTransform(currentTranslate.current, false);
  };
  const onEnd = () => {
    if (!dragging.current) return;
    dragging.current = false;
    const moved = currentTranslate.current - prevTranslate.current;
    const threshold = Math.max(40, cardWidth * 0.12);
    if (moved < -threshold && index < slides.length - 1) setIndex((i) => i + 1);
    else if (moved > threshold && index > 0) setIndex((i) => i - 1);
    else setTransform(prevTranslate.current, true);
  };

  // global mouse listeners (so mouse drag works even if pointer leaves)
  useEffect(() => {
    const mm = (e) => onMove(e.clientX);
    const mu = () => onEnd();
    window.addEventListener("mousemove", mm);
    window.addEventListener("mouseup", mu);
    return () => {
      window.removeEventListener("mousemove", mm);
      window.removeEventListener("mouseup", mu);
    };
  }, [cardWidth, slides.length, index]); // keep deps

  const handleTouchStart = (e) => onStart(e.touches[0].clientX);
  const handleTouchMove = (e) => onMove(e.touches[0].clientX);
  const handleTouchEnd = () => onEnd();
  const handleMouseDown = (e) => {
    e.preventDefault();
    onStart(e.clientX);
  };

  const prev = useCallback(() => setIndex((i) => Math.max(0, i - 1)), []);
  const next = useCallback(() => setIndex((i) => Math.min(slides.length - 1, i + 1)), [slides.length]);

  // pill image placeholders: you will replace srcs with your small icons
  const pillImg = {
    hatchback: process.env.PUBLIC_URL + "/pill-hatch.png",
    suv: process.env.PUBLIC_URL + "/pill-suv.png",
    sedan: process.env.PUBLIC_URL + "/pill-sedan.png",
  };

  return (
    <section className="btc-root">
      <h3 className="btc-title">Explore by Body Type</h3>

      <div className="btc-pill-wrap" role="tablist" aria-label="Body type">
        {["hatchback", "suv", "sedan"].map((t) => (
          <button
            key={t}
            role="tab"
            aria-selected={active === t}
            className={`btc-pill ${active === t ? "active" : ""}`}
            onClick={() => setActive(t)}
          >
            {/* small image area - you will add icons here; kept fixed size */}
            <span className="btc-pill-imgwrap">
              <img src={pillImg[t]} alt="" className="btc-pill-img" />
            </span>
            <span className="btc-pill-text">
              {t === "hatchback" ? "Hatchback" : t === "suv" ? "SUV" : "Sedan"}
            </span>
          </button>
        ))}
      </div>

      <div className="btc-carousel" ref={containerRef}>
        <button className="btc-arrow left" onClick={prev} disabled={index === 0} aria-label="Previous">
          ‹
        </button>

        <div
          className="btc-viewport"
          onTouchStart={handleTouchStart}
          onTouchMove={handleTouchMove}
          onTouchEnd={handleTouchEnd}
          onMouseDown={handleMouseDown}
        >
          <div
            ref={trackRef}
            className="btc-track"
            style={{ width: slides.length * (cardWidth || 1) + "px" }}
          >
            {slides.map((c, i) => (
              <article
                key={c.id}
                className="btc-card"
                style={{ width: cardWidth ? `${cardWidth}px` : "86%" }}
                aria-hidden={i !== index}
              >
                <div className="btc-card-image" style={{ backgroundImage: `url(${c.img})` }} />
                <div className="btc-card-body">
                  <div className="btc-card-title">{c.title}</div>
                  <div className="btc-card-price">{c.price}</div>
                  <div className="btc-card-km">{c.km}</div>
                </div>
              </article>
            ))}
          </div>
        </div>

        <button className="btc-arrow right" onClick={next} disabled={index >= slides.length - 1} aria-label="Next">
          ›
        </button>
      </div>

      <div className="btc-dots">
        {slides.map((_, i) => (
          <button
            key={i}
            className={`btc-dot ${i === index ? "active" : ""}`}
            onClick={() => setIndex(i)}
            aria-label={`Go to ${i + 1}`}
          />
        ))}
      </div>
    </section>
  );
}
