// src/pages/CarDetailsWeb.jsx
import React, {
    useCallback,
    useEffect,
    useRef,
    useState,
  } from "react";
  import { useNavigate, useParams } from "react-router-dom";
  import carsData from "../data/cars";
  import "../styles/CarDetails.css";

  const TOPBAR_HEIGHT = 72; // adjust if your topbar height differs
  const CONTENT_MAX_WIDTH = 1200; // used to align sticky tabs & fixed right panel

  export default function CarDetailsWeb() {
    const { id } = useParams();
    const navigate = useNavigate();

    // find car (computed before hooks)
    const car = carsData.find((c) => Number(c.id) === Number(id)) || null;

    // Setup images array safely (no hooks conditional)
    const imagesArray = (car && (Array.isArray(car.images) ? car.images : [car.image])) || [];

    // ===== Hooks - unconditional =====
    const [heroImage, setHeroImage] = useState(imagesArray[0] || "");
    const [activeTab, setActiveTab] = useState("overview");
    const [showStickyTabs, setShowStickyTabs] = useState(false);
    const [rightMode, setRightMode] = useState("fixed"); // "fixed" | "absolute" | "static"
    const [tabsLeft, setTabsLeft] = useState(0);
    const [tabsWidth, setTabsWidth] = useState(760);

    const containerRef = useRef(null);
    const leftRef = useRef(null);
    const heroRef = useRef(null);
    const rightCardRef = useRef(null);
    const sentinelRef = useRef(null);

    // section refs
    const overviewRef = useRef(null);
    const reportRef = useRef(null);
    const specsRef = useRef(null);
    const financeRef = useRef(null);

    // helper: scroll to element
    const scrollToRef = (ref) => {
      if (!ref?.current) return;
      const top =
        window.scrollY +
        ref.current.getBoundingClientRect().top -
        TOPBAR_HEIGHT -
        8;
      window.scrollTo({ top, behavior: "smooth" });
    };

    // sticky tabs: observe hero's intersection to toggle sticky
    useEffect(() => {
      if (!heroRef.current) return;
      const io = new IntersectionObserver(
        (entries) => {
          const ent = entries[0];
          setShowStickyTabs(!ent.isIntersecting);
        },
        { threshold: 0.06 }
      );
      io.observe(heroRef.current);
      return () => io.disconnect();
    }, []);

    // compute tabsLeft and tabsWidth so sticky tabs align with left column
    const recomputeTabsLayout = useCallback(() => {
      const leftRect = leftRef.current?.getBoundingClientRect();
      const containerRect = containerRef.current?.getBoundingClientRect();
      if (!leftRect || !containerRect) return;
      setTabsLeft(leftRect.left + window.pageXOffset);
      // tabs width should be left column width minus small padding
      setTabsWidth(Math.max(320, leftRect.width - 20));
    }, []);

    useEffect(() => {
      recomputeTabsLayout();
      window.addEventListener("resize", recomputeTabsLayout);
      return () => window.removeEventListener("resize", recomputeTabsLayout);
    }, [recomputeTabsLayout]);

    // ScrollSpy: set active tab based on which section is visible
    useEffect(() => {
      const sections = [
        { id: "overview", ref: overviewRef },
        { id: "report", ref: reportRef },
        { id: "specs", ref: specsRef },
        { id: "finance", ref: financeRef },
      ].map((s) => s.ref.current).filter(Boolean);

      if (!sections.length) return;
      const io = new IntersectionObserver(
        (entries) => {
          const visible = entries.filter((e) => e.isIntersecting);
          if (visible.length > 0) {
            // choose the one with highest intersectionRatio
            visible.sort((a, b) => b.intersectionRatio - a.intersectionRatio);
            const id = visible[0].target.id;
            setActiveTab(id);
          }
        },
        {
          root: null,
          rootMargin: `-${TOPBAR_HEIGHT + 8}px 0px -30% 0px`,
          threshold: [0.15, 0.4, 0.7],
        }
      );

      sections.forEach((s) => io.observe(s));
      return () => io.disconnect();
    }, []);

    // Compute right panel mode to avoid overlapping footer
    const computeRightMode = useCallback(() => {
      if (!rightCardRef.current || !leftRef.current || !sentinelRef.current) {
        setRightMode("fixed");
        return;
      }

      const sentinelRect = sentinelRef.current.getBoundingClientRect();
      const viewportHeight = window.innerHeight;

      // If sentinel (bottom of content) is inside viewport (near footer), make right static
      if (sentinelRect.top <= viewportHeight - 24) {
        setRightMode("static");
        return;
      }

      // If left column is too short for right card height -> absolute (stick to bottom of left)
      const leftRect = leftRef.current.getBoundingClientRect();
      const cardHeight = rightCardRef.current.offsetHeight;
      const spaceBelowTopbar = leftRect.bottom - TOPBAR_HEIGHT;

      if (spaceBelowTopbar < cardHeight + 40) {
        setRightMode("absolute");
        return;
      }

      // default fixed
      setRightMode("fixed");
    }, []);

    useEffect(() => {
      // compute on mount and user interactions
      computeRightMode();
      window.addEventListener("scroll", computeRightMode, { passive: true });
      window.addEventListener("resize", computeRightMode);
      return () => {
        window.removeEventListener("scroll", computeRightMode);
        window.removeEventListener("resize", computeRightMode);
      };
    }, [computeRightMode]);

    // Ensure heroImage updates if the car changes (e.g., navigation)
    useEffect(() => {
      if (imagesArray.length && !imagesArray.includes(heroImage)) {
        setHeroImage(imagesArray[0]);
      }
      // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [id]); // update when route id changes

    // ===== AFTER hooks: handle missing car =====
    if (!car) {
      return (
        <div style={{ padding: 36 }}>
          <h2>Car not found</h2>
          <p>We couldn't find that car. It might have been removed.</p>
          <button onClick={() => navigate(-1)}>Go back</button>
        </div>
      );
    }

    // ==== JSX ====
    return (
      <div className="car-details-root">
        <div
          className="car-details-wrapper"
          ref={containerRef}
          style={{ ["--content-max-width"]: `${CONTENT_MAX_WIDTH}px` }}
        >
          {/* LEFT - main content */}
          <div className="car-details-left" ref={leftRef}>
            {/* big hero area */}
            <div className="hero-area">
              <div className="hero-image" ref={heroRef}>
                {heroImage ? (
                  <img src={heroImage} alt={car.title} />
                ) : (
                  <div className="hero-placeholder">No image</div>
                )}
              </div>

              {/* thumbnails - below hero */}
              <div className="thumbs-row" aria-label="thumbnails">
                {imagesArray.map((img, i) => (
                  <button
                    key={i}
                    className={`thumb-btn ${heroImage === img ? "active" : ""}`}
                    onClick={() => setHeroImage(img)}
                    aria-pressed={heroImage === img}
                  >
                    <img src={img} alt={`${car.title} ${i + 1}`} />
                  </button>
                ))}
              </div>
            </div>

            {/* sticky tabs (appears only after hero scrolled) */}
            <div
              className={`details-tabs ${showStickyTabs ? "show" : ""}`}
              style={
                showStickyTabs
                  ? {
                      left: tabsLeft,
                      width: tabsWidth,
                      maxWidth: tabsWidth,
                      transform: "translateX(0)",
                    }
                  : {}
              }
            >
              <nav className="tabs-nav" role="tablist">
                <button
                  className={activeTab === "overview" ? "active" : ""}
                  onClick={() => scrollToRef(overviewRef)}
                >
                  Overview
                </button>
                <button
                  className={activeTab === "report" ? "active" : ""}
                  onClick={() => scrollToRef(reportRef)}
                >
                  Report
                </button>
                <button
                  className={activeTab === "specs" ? "active" : ""}
                  onClick={() => scrollToRef(specsRef)}
                >
                  Feature & Specs
                </button>
                <button
                  className={activeTab === "finance" ? "active" : ""}
                  onClick={() => scrollToRef(financeRef)}
                >
                  Finance
                </button>
              </nav>
            </div>

            {/* SECTIONS */}
            <section
              id="overview"
              ref={overviewRef}
              className="detail-section"
              tabIndex={-1}
            >
              <h2>Car Overview</h2>
              <div className="overview-card">
                <div className="overview-grid">
                  <div>
                    <div className="muted">Make Year</div>
                    <div className="bold">May 2024</div>
                  </div>
                  <div>
                    <div className="muted">Registration Year</div>
                    <div className="bold">Jun 2024</div>
                  </div>
                  <div>
                    <div className="muted">Fuel Type</div>
                    <div className="bold">{car.fuel}</div>
                  </div>

                  <div>
                    <div className="muted">Km driven</div>
                    <div className="bold">{Math.round(car.km / 100) / 10}K km</div>
                  </div>
                  <div>
                    <div className="muted">Transmission</div>
                    <div className="bold">{car.transmission}</div>
                  </div>
                  <div>
                    <div className="muted">No. of Owner</div>
                    <div className="bold">1st Owner</div>
                  </div>

                  <div>
                    <div className="muted">Insurance Validity</div>
                    <div className="bold">Jun 2026</div>
                  </div>
                  <div>
                    <div className="muted">Insurance Type</div>
                    <div className="bold">Comprehensive</div>
                  </div>
                  <div>
                    <div className="muted">RTO</div>
                    <div className="bold">UP14</div>
                  </div>

                  <div style={{ gridColumn: "1 / -1" }}>
                    <div className="muted">Car Location</div>
                    <div className="bold">Spinny Car Hub, {car.city}</div>
                  </div>
                </div>
              </div>
            </section>

            <section id="report" ref={reportRef} className="detail-section">
              <h2>Quality report</h2>
              <p>Some summary about the car's quality and checks.</p>
              <div className="report-box">
                <div className="report-item">
                  <div className="report-title">Core systems</div>
                  <div className="report-score">9.8</div>
                </div>
                <div className="report-item">
                  <div className="report-title">Interiors & AC</div>
                  <div className="report-score">9.6</div>
                </div>
              </div>
            </section>

            <section id="specs" ref={specsRef} className="detail-section">
              <h2>Feature & Specs</h2>
              <div className="specs-grid">
                <div className="spec-col">
                  <h4>Top Features</h4>
                  <ul>
                    <li>Airbags</li>
                    <li>Power steering</li>
                  </ul>
                </div>
                <div className="spec-col">
                  <h4>Comfort</h4>
                  <ul>
                    <li>Automatic climate control</li>
                    <li>Power windows</li>
                  </ul>
                </div>
              </div>
            </section>

            <section id="finance" ref={financeRef} className="detail-section">
              <h2>Finance</h2>
              <div className="finance-box">
                <div className="finance-left">
                  <div className="big-emi">9,400</div>
                  <div className="muted">per month (approx)</div>
                  <button className="btn-cta">Check eligibility</button>
                </div>
                <div className="finance-right">
                  <div className="muted">Loan amount</div>
                  <div className="bold">4,41,600</div>
                </div>
              </div>
            </section>

            {/* sentinel - bottom of left column to detect footer proximity */}
            <div ref={sentinelRef} className="right-sentinel" />
          </div>

          {/* RIGHT - fixed card */}
          <aside
            className={`car-details-right ${rightMode}`}
            ref={rightCardRef}
            aria-label="Car summary and actions"
          >
            <div className="right-card">
              <div className="title-row">
                <h3 className="car-title">{car.title}</h3>
                <button
                  className="fav-btn"
                  title="Add to shortlist"
                  aria-label="Add to shortlist"
                >

                </button>
              </div>

              <div className="meta-row">
                <div>{Math.round(car.km / 100) / 10}K km</div>
                <div>  </div>
                <div>{car.fuel}</div>
                <div>  </div>
                <div>{car.transmission}</div>
              </div>

              <div className="price-row">
                <div className="price-label">Fixed on road price</div>
                <div className="price-value">
                  {(car.price / 100000).toFixed(2)} L
                </div>
              </div>

              <div className="cta-row">
                <button className="btn primary">BOOK NOW</button>
                <button className="btn secondary">FREE TEST DRIVE</button>
              </div>

              <div className="share-row">
                <small>Share with a friend:</small>
                <div className="share-icons">  </div>
              </div>
            </div>
          </aside>
        </div>
      </div>
    );
  }
