import React from "react";
import "../styles/WhyBuySection.css";

/**
 * WhyBuySection
 * Props:
 *  - title (string) optional
 *  - children (ReactNode) body content (or pass `text` prop)
 *  - text (string) optional alternative to children
 *
 * Usage:
 * <WhyBuySection />
 * or
 * <WhyBuySection title="Why buy from CarsDedo?">Your custom paragraph here</WhyBuySection>
 */
export default function WhyBuySection({ title = "Why buy a used car from Spinny?", text, children }) {
  return (
    <section className="why-section" aria-labelledby="why-title">
      <div className="why-inner">
        <h3 id="why-title" className="why-title">{title}</h3>

        <div className="why-body">
          {text ? <p className="why-text">{text}</p> : children ? <div className="why-text">{children}</div> : (
            <p className="why-text">
              Spinny takes the uncertainty and risk out of buying a used car, offering peace of mind at every step with zero compromises.
              Our selection process ensures that only the highest quality certified second hand cars in your city. A Spinny car is only certified once it passes a thorough 200-point evaluation that checks the condition of every part of the car. Any used car can get certified. It takes perfection to be Spinny Assured. Experience a simple & fully transparent way of buying used cars with Spinny. Find your perfect match from our wide range of fully inspected & certified used cars at the best prices.
              All Spinny cars come with hassle-free paperwork, free RC transfer, and used car finance options with low-interest rates starting from only 12.99%. With Spinny, pre-owned is better than new. Get the savings of a pre-owned with the quality of a new car. All this through a transparent, convenient and trustworthy process, to make sure you buy a car youâ€™ll love, guaranteed.
            </p>
          )}
        </div>
      </div>
    </section>
  );
}
