import React, { useEffect, useState, useRef, useCallback } from "react";
import "../styles/FeaturedCars.css";

/**
 * FeaturedCars
 * - Two tabs (best / newly) that switch data sets
 * - Responsive carousel (1 / 2 / 3 visible)
 * - Replace sample image paths with your actual public URLs
 */

const BEST_BUYS = [
  {
    id: "b1",
    title: "2020 Maruti Baleno",
    variant: "Zeta",
    price: "₹ 5.46 Lakh",
    emi: "EMI ₹9,298/m*",
    km: "33,500 km",
    fuel: "Petrol",
    trans: "Manual",
    image: process.env.PUBLIC_URL + "/cars/baleno.png",
  },
  {
    id: "b2",
    title: "2019 Hyundai i20",
    variant: "Asta",
    price: "₹ 6.20 Lakh",
    emi: "EMI ₹10,700/m*",
    km: "28,200 km",
    fuel: "Petrol",
    trans: "Automatic",
    image: process.env.PUBLIC_URL + "/cars/i20.png",
  },
  {
    id: "b3",
    title: "2021 Tata Nexon",
    variant: "XZ+",
    price: "₹ 8.10 Lakh",
    emi: "EMI ₹13,800/m*",
    km: "20,100 km",
    fuel: "Petrol",
    trans: "Manual",
    image: process.env.PUBLIC_URL + "/cars/nexon.png",
  },
  {
    id: "b4",
    title: "2018 Honda City",
    variant: "VX",
    price: "₹ 9.99 Lakh",
    emi: "EMI ₹17,000/m*",
    km: "40,000 km",
    fuel: "Petrol",
    trans: "Automatic",
    image: process.env.PUBLIC_URL + "/cars/city.png",
  },
];

const NEWLY = [
  {
    id: "n1",
    title: "2023 Toyota Glanza",
    variant: "V",
    price: "₹ 7.10 Lakh",
    emi: "EMI ₹12,000/m*",
    km: "5,600 km",
    fuel: "Petrol",
    trans: "Manual",
    image: process.env.PUBLIC_URL + "/cars/glanza.png",
  },
  {
    id: "n2",
    title: "2022 Skoda Rapid",
    variant: "Style",
    price: "₹ 7.80 Lakh",
    emi: "EMI ₹13,200/m*",
    km: "12,900 km",
    fuel: "Petrol",
    trans: "Manual",
    image: process.env.PUBLIC_URL + "/cars/rapid.png",
  },
  {
    id: "n3",
    title: "2023 VW Taigun",
    variant: "Highline",
    price: "₹ 10.4 Lakh",
    emi: "EMI ₹17,900/m*",
    km: "2,200 km",
    fuel: "Petrol",
    trans: "Automatic",
    image: process.env.PUBLIC_URL + "/cars/taigun.png",
  },
];

function useVisibleCount() {
  const [count, setCount] = useState(getCount(window.innerWidth));
  useEffect(() => {
    let t;
    const onResize = () => {
      clearTimeout(t);
      t = setTimeout(() => setCount(getCount(window.innerWidth)), 120);
    };
    window.addEventListener("resize", onResize);
    return () => window.removeEventListener("resize", onResize);
  }, []);
  return count;

  function getCount(w) {
    if (w <= 600) return 1;
    if (w <= 900) return 2;
    return 3;
  }
}

export default function FeaturedCars() {
  const visible = useVisibleCount();
  const [activeTab, setActiveTab] = useState("best"); // 'best' | 'new'
  const data = activeTab === "best" ? BEST_BUYS : NEWLY;
  const [index, setIndex] = useState(0);
  const trackRef = useRef(null);

  useEffect(() => {
    // clamp index if data length changed or visible changed
    const maxIndex = Math.max(0, data.length - visible);
    setIndex((i) => Math.min(i, maxIndex));
  }, [data.length, visible]);

  useEffect(() => {
    const x = -(index * (100 / visible));
    if (trackRef.current) {
      trackRef.current.style.transform = `translateX(${x}%)`;
    }
  }, [index, visible]);

  const prev = useCallback(() => {
    setIndex((i) => Math.max(0, i - 1));
  }, []);

  const next = useCallback(() => {
    setIndex((i) => Math.min(i + 1, Math.max(0, data.length - visible)));
  }, [data.length, visible]);

  const onTab = (tab) => {
    setActiveTab(tab);
    setIndex(0); // reset carousel when switching tabs
  };

  return (
    <section className="fc-section" aria-label="Featured cars">
      <div className="fc-inner">
        <h2 className="fc-title">Featured Cars</h2>

        <div className="fc-tabs" role="tablist" aria-label="Filter featured">
          <button
            role="tab"
            aria-selected={activeTab === "best"}
            className={`fc-tab ${activeTab === "best" ? "active" : ""}`}
            onClick={() => onTab("best")}
          >
            Best buys for you
          </button>

          <button
            role="tab"
            aria-selected={activeTab === "new"}
            className={`fc-tab ${activeTab === "new" ? "active" : ""}`}
            onClick={() => onTab("new")}
          >
            Newly added
          </button>
        </div>

        <div className="fc-carousel">
          <button className="fc-arrow left" onClick={prev} disabled={index === 0}>
            ‹
          </button>

          <div className="fc-viewport">
            <div
              className="fc-track"
              ref={trackRef}
              style={{ width: `${(data.length * 100) / visible}%` }}
            >
              {data.map((c) => (
                <article className="fc-card" key={c.id} aria-hidden={false}>
                  <div className="fc-card-media">
                    <img src={c.image} alt={c.title} className="fc-car-img" />
                    <button className="fc-heart" aria-label="Save car">♡</button>
                  </div>

                  <div className="fc-card-body">
                    <div className="fc-left">
                      <div className="fc-title-row">
                        <div className="fc-name">{c.title}</div>
                        <div className="fc-price">{c.price}</div>
                      </div>
                      <div className="fc-variant">{c.variant}</div>

                      <div className="fc-chips">
                        <span className="fc-chip">{c.km}</span>
                        <span className="fc-chip">{c.fuel}</span>
                        <span className="fc-chip">{c.trans}</span>
                      </div>
                    </div>
                  </div>

                  <div className="fc-card-footer">
                    <div className="fc-hub">HUB · Gaur City Mall, Noida</div>
                    <div className="fc-emi">{c.emi}</div>
                  </div>
                </article>
              ))}
            </div>
          </div>

          <button
            className="fc-arrow right"
            onClick={next}
            disabled={index >= Math.max(0, data.length - visible)}
          >
            ›
          </button>
        </div>

        <div className="fc-dots">
          {data.map((_, i) => (
            <button
              key={i}
              className={`fc-dot ${i === index ? "active" : ""}`}
              onClick={() => setIndex(i)}
              aria-label={`Go to card ${i + 1}`}
            />
          ))}
        </div>

        <div className="fc-cta-row">
          <button className="fc-view-all">View all {activeTab === "best" ? "Spinny cars" : "new cars"}</button>
        </div>
      </div>
    </section>
  );
}
