import React from "react";
import { NavLink } from "react-router-dom";
import "../styles/BottomNav.css";

/**
 * BottomNav
 * - Uses NavLink so active route gets `.active` class automatically.
 * - Place this inside your mobile layout (rendered near the end of the DOM).
 */
export default function BottomNav() {
  // small helper to render an item
  const Item = ({ to, label, children }) => (
    <NavLink
      to={to}
      className={({ isActive }) => `bn-item ${isActive ? "active" : ""}`}
      aria-label={label}
      title={label}
    >
      <span className="bn-icon" aria-hidden>
        {children}
      </span>
      <span className="bn-label">{label}</span>
    </NavLink>
  );

  return (
    <nav className="bn-root" role="navigation" aria-label="Bottom navigation">
      <div className="bn-inner">
        <Item to="/" label="Home">
          {/* home SVG */}
          <svg viewBox="0 0 24 24" width="22" height="22" fill="none" stroke="currentColor" strokeWidth="1.4">
            <path d="M3 11.5L12 4l9 7.5" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M5 21V12h14v9" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </Item>

        <Item to="/cars" label="Buy Car">
          {/* car SVG */}
          <svg viewBox="0 0 24 24" width="22" height="22" fill="none" stroke="currentColor" strokeWidth="1.4">
            <path d="M3 13.5h18v3a1 1 0 0 1-1 1h-1a1 1 0 0 1-1-1v-1H6v1a1 1 0 0 1-1 1H4a1 1 0 0 1-1-1v-3z" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M5 13.5L6.5 8.5h11L19 13.5" strokeLinecap="round" strokeLinejoin="round"/>
            <circle cx="7.5" cy="17.5" r="1.25" />
            <circle cx="16.5" cy="17.5" r="1.25" />
          </svg>
        </Item>

        <Item to="/shortlists" label="Shortlists">
          {/* heart SVG */}
          <svg viewBox="0 0 24 24" width="22" height="22" fill="none" stroke="currentColor" strokeWidth="1.4">
            <path d="M20.8 7.2a4.6 4.6 0 0 0-6.4 0L12 9.6l-2.4-2.4a4.6 4.6 0 0 0-6.4 6.4L12 21l8.8-7.4a4.6 4.6 0 0 0 0-6.4z" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </Item>

        <Item to="/sell" label="Sell Car">
          {/* key / sell SVG */}
          <svg viewBox="0 0 24 24" width="22" height="22" fill="none" stroke="currentColor" strokeWidth="1.4">
            <path d="M3 11.5h6v6H3z" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M9.5 14.5l4-4a3 3 0 1 1 4 4l-4 4" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
        </Item>

        <Item to="/account" label="Account">
          {/* diamond / profile SVG */}
          <svg viewBox="0 0 24 24" width="22" height="22" fill="none" stroke="currentColor" strokeWidth="1.4">
            <path d="M12 2l7 5v10l-7 5-7-5V7z" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M12 12a2.5 2.5 0 1 0 0-5 2.5 2.5 0 0 0 0 5z" />
          </svg>
        </Item>
      </div>
    </nav>
  );
}
