import React from "react";
import "../styles/MobileReviews.css";

/**
 * MobileReviews - improved, production-ready mobile reviews component.
 * - Replace `reviews` with real API data as needed.
 * - Uses inline initials for avatars.
 */

export default function MobileReviews() {
  const overall = { score: 4.9, count: 1245 };

  const reviews = [
    {
      id: 1,
      name: "Aftab Alam",
      time: "4 days ago",
      city: "Kolkata",
      rating: 5,
      text:
        "Today I sell my car in CarsDedo CC2 hub. I am very satisfied. Here all employees are very good.",
    },
    {
      id: 2,
      name: "Pradeep Sahoo",
      time: "4 days ago",
      city: "Hyderabad",
      rating: 5,
      text:
        "I recently sold my old car through CarsDedo. Obviously it was a nice experience in dealing with CarsDedo. The entire process was smooth, hassle-free, tran...",
    },
    {
      id: 3,
      name: "Sunny Agarwal",
      time: "4 days ago",
      city: "Gurgaon",
      rating: 5,
      text:
        "Kumar Raj was very helpful and completed the handover formalities smoothly.",
    },
    {
      id: 4,
      name: "Chaitali Bhise",
      time: "4 days ago",
      city: "Faridabad",
      rating: 5,
      text: "Its good experience not hustle & at good price great service",
    },
  ];

  const initials = (fullName) =>
    fullName
      .split(" ")
      .map((n) => n[0])
      .slice(0, 2)
      .join("")
      .toUpperCase();

  return (
    <section className="mr-root" aria-label="User reviews and ratings">
      <div className="mr-inner">
        <h2 className="mr-title">User Reviews &amp; Ratings</h2>

        <div className="mr-overall-row">
          <div className="mr-score-pill" aria-hidden>
            <div className="mr-score">{overall.score.toFixed(1)}</div>
            <div className="mr-stars-inline" aria-hidden>
              {Array.from({ length: 5 }).map((_, i) => (
                <span key={i} className={`mr-star ${i < Math.round(overall.score) ? "on" : ""}`}>
                  ★
                </span>
              ))}
            </div>
          </div>
        </div>

        <ul className="mr-list" role="list">
          {reviews.map((r) => (
            <li key={r.id} className="mr-item" role="listitem" aria-label={`${r.name} review`}>
              <div className="mr-left">
                <div className="mr-avatar" aria-hidden>
                  {initials(r.name)}
                </div>
              </div>

              <div className="mr-middle">
                <div className="mr-heading-row">
                  <div className="mr-name">{r.name}</div>
                  <div className="mr-meta">
                    <span className="mr-time">{r.time}</span>
                    <span className="mr-sep"> | </span>
                    <span className="mr-city">{r.city}</span>
                  </div>
                </div>

                <p className="mr-text">{r.text}</p>

                {r.text.length > 120 ? (
                  <button
                    className="mr-readmore"
                    onClick={() => {
                      /* Expand or navigate - placeholder behavior */
                      alert("Open full review (implement real expand or route).");
                    }}
                    aria-label={`Read more about ${r.name}'s review`}
                  >
                    Read more
                  </button>
                ) : null}
              </div>

              <div className="mr-right" aria-hidden>
                <div className="mr-stars-compact">
                  {Array.from({ length: 5 }).map((_, i) => (
                    <span key={i} className={`mr-star-compact ${i < r.rating ? "on" : ""}`}>
                      ★
                    </span>
                  ))}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </section>
  );
}
