import React, { useState, useMemo } from "react";
import styles from "../styles/EMICalculator.module.css"

export default function EMICalculator() {
  const minLoan = 100000;
  const maxLoan = 1387985;

  const [loanAmount, setLoanAmount] = useState(1148000);
  const [downPayment, setDownPayment] = useState(239985);
  const [duration, setDuration] = useState(66);

  const monthlyInterestRate = 0.009;

  const principal = loanAmount - downPayment;

  const emi = useMemo(() => {
    const r = monthlyInterestRate;
    const n = duration;
    const p = principal;

    if (p <= 0) return 0;

    const pow = Math.pow(1 + r, n);
    const emiVal = (p * r * pow) / (pow - 1);
    return Math.round(emiVal);
  }, [principal, duration]);

  const totalInterest = emi * duration - principal;
  const totalPayable = principal + totalInterest;

  return (
    <div className={styles.emiCard}>
      {/* LEFT */}
      <div className={styles.emiLeft}>
        <div className={styles.emiHeading}>EMI starting from</div>

        <div className={styles.emiValue}>
          ₹ {emi.toLocaleString("en-IN")} <span>per month</span>
        </div>

        {/* Semi Circle */}
        <div className={styles.emiSemicircleWrap}>
          <div className={styles.semi}>
            <div
              className={styles.semiFill}
              style={{
                width: `${(principal / maxLoan) * 100}%`,
              }}
            ></div>
          </div>

          <div className={styles.emiLegend}>
            <div className={styles.legendItem}>
              <span className={styles.legendBox}></span>
              <div>
                <div className={styles.legendLabel}>Principal Loan Amount</div>
                <div className={styles.legendValue}>
                  ₹ {principal.toLocaleString("en-IN")}
                </div>
              </div>
            </div>

            <div className={styles.legendItem}>
              <span className={`${styles.legendBox} ${styles.interest}`}></span>
              <div>
                <div className={styles.legendLabel}>Total Interest Payable</div>
                <div className={styles.legendValue}>
                  ₹ {totalInterest.toLocaleString("en-IN")}
                </div>
              </div>
            </div>
          </div>

          <div className={styles.emiTotal}>
            <div>Total Amount Payable</div>
            <div>₹ {totalPayable.toLocaleString("en-IN")}</div>
          </div>
        </div>
      </div>

      {/* RIGHT */}
      <div className={styles.emiRight}>
        {/* Loan Amount */}
        <div className={styles.fieldRow}>
          <div>
            <div className={styles.fieldLabel}>Loan Amount</div>
            <div className={styles.fieldSub}>
              ₹ {minLoan.toLocaleString("en-IN")}
            </div>
          </div>

          <div className={styles.fieldValue}>
            ₹ {loanAmount.toLocaleString("en-IN")}
          </div>
        </div>

        <input
          type="range"
          min={minLoan}
          max={maxLoan}
          value={loanAmount}
          onChange={(e) => setLoanAmount(Number(e.target.value))}
          className={styles.range}
        />

        {/* Down Payment */}
        <div className={styles.fieldRow} style={{ marginTop: 20 }}>
          <div>
            <div className={styles.fieldLabel}>Down Payment*</div>
            <div className={styles.fieldSub}>₹ 0</div>
          </div>

          <div className={styles.fieldValue}>
            ₹ {downPayment.toLocaleString("en-IN")}
          </div>
        </div>

        <input
          type="range"
          min={0}
          max={loanAmount}
          value={downPayment}
          onChange={(e) => setDownPayment(Number(e.target.value))}
          className={styles.range}
        />

        {/* Duration */}
        <div className={styles.fieldRow} style={{ marginTop: 20 }}>
          <div>
            <div className={styles.fieldLabel}>Duration of Loan</div>
            <div className={styles.fieldSub}>12 Months</div>
          </div>

          <div className={styles.fieldValue}>{duration} Months</div>
        </div>

        <input
          type="range"
          min={12}
          max={84}
          value={duration}
          onChange={(e) => setDuration(Number(e.target.value))}
          className={styles.range}
        />

        <button className={styles.emiBtn}>₹ CHECK ELIGIBILITY</button>

        <div className={styles.emiDisclaimer}>
          *Processing fee and other loan charges are not included.
          <br />
          Disclaimer: Rate of interest may vary as per credit profile.
        </div>
      </div>
    </div>
  );
}
