// BuyPage.jsx
import React, { useEffect, useRef, useState } from "react";
import BottomNav from "../components/BottomNav";
import "../styles/BuyPage.css";

const HERO_BANNER = "/mnt/data/4b504ec2-9aab-45cb-8618-dd9a0b75e116.png";

/* Small SVG check */
function IconCheck({ show }) {
  return (
    <svg className={`check-svg ${show ? "on" : ""}`} width="14" height="14" viewBox="0 0 24 24" aria-hidden>
      <path fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" d="M20 6L9 17l-5-5" />
    </svg>
  );
}

/* Inline (non-portal) top filter dropdown — safer when other overlays exist */
function FilterDropdown({ label, options = [], selected = null, onChange }) {
  const [open, setOpen] = useState(false);
  const rootRef = useRef(null);

  useEffect(() => {
    function onDoc(e) {
      if (!rootRef.current) return;
      if (!rootRef.current.contains(e.target)) setOpen(false);
    }
    window.addEventListener("click", onDoc);
    return () => window.removeEventListener("click", onDoc);
  }, []);

  return (
    <div className="fp-root" ref={rootRef}>
      <button
        type="button"
        className={`fp-chip ${open ? "open" : ""}`}
        aria-haspopup="menu"
        aria-expanded={open}
        onClick={(e) => {
          e.stopPropagation();
          setOpen((v) => !v);
        }}
      >
        <span className="fp-chip-label">{label}</span>
        <span className="fp-chip-caret">▾</span>
      </button>

      <div className="fp-menu" role="menu" style={{ display: open ? "block" : "none" }}>
        <ul role="none" className="fp-options">
          <li role="none">
            <button
              role="menuitem"
              className={`fp-opt ${selected === null ? "selected" : ""}`}
              onClick={() => {
                onChange?.(null);
                setOpen(false);
              }}
            >
              All
            </button>
          </li>

          {options.map((o) => (
            <li role="none" key={o.value}>
              <button
                role="menuitem"
                className={`fp-opt ${selected === o.value ? "selected" : ""}`}
                onClick={() => {
                  onChange?.(o.value);
                  setOpen(false);
                }}
              >
                {o.label}
              </button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

/* Simple fancy checkbox component */
function FancyCheckbox({ id, label, checked, onChange }) {
  return (
    <label className="f-chk" htmlFor={id}>
      <input id={id} type="checkbox" checked={checked} onChange={(e) => onChange(e.target.checked)} />
      <span className={`f-chk-box`} aria-hidden><IconCheck show={checked} /></span>
      <span className="f-chk-label">{label}</span>
    </label>
  );
}

/* Color option with visible swatch outside the checkbox */
function ColorOption({ id, name, color, checked, onChange }) {
  return (
    <label className="color-option" htmlFor={id}>
      <div className="color-swatch" style={{ background: color }} />
      <div className="color-name">{name}</div>
      <input id={id} type="checkbox" checked={checked} onChange={(e) => onChange(e.target.checked)} />
      <span className={`color-check ${checked ? "on" : ""}`} aria-hidden><IconCheck show={checked} /></span>
    </label>
  );
}

/* Filter Drawer */
function FilterDrawer({ open, appliedFilters, onApply, onClose, onClear }) {
  // rupee constants
  const PRICE_MIN = 50000;
  const PRICE_MAX = 7000000;
  const PRICE_STEP = 50000;

  const brandsToModels = {
    "Maruti Suzuki": ["Baleno", "Swift", "Dzire", "Wagon R", "Ciaz"],
    Hyundai: ["i20", "Creta", "Verna", "Venue"],
    Honda: ["City", "Amaze", "Jazz"],
    Tata: ["Tiago", "Altroz", "Harrier"],
    Kia: ["Sonet", "Seltos"],
  };

  const colorMap = [
    { name: "White", code: "#FFFFFF" },
    { name: "Black", code: "#000000" },
    { name: "Silver", code: "#CFCFCF" },
    { name: "Grey", code: "#949494" },
    { name: "Red", code: "#C8342A" },
    { name: "Blue", code: "#2D76E6" },
    { name: "Green", code: "#3BB54A" },
  ];

  const yearOptions = ["2023 & above", "2021 & above", "2019 & above", "2017 & above", "2015 & above", "2013 & above", "2011 & above", "2009 & above"];
  const kmsOptions = ["< 20,000", "20,001 - 50,000", "50,001 - 1,00,000", "100,001+"];

  const [state, setState] = useState(() => ({
    priceMin: appliedFilters.priceMin ?? PRICE_MIN,
    priceMax: appliedFilters.priceMax ?? PRICE_MAX,
    brands: appliedFilters.brands ?? [],
    brandModels: appliedFilters.brandModels ?? {},
    year: appliedFilters.year ?? null,
    kms: appliedFilters.kms ?? null,
    colors: appliedFilters.color ? [appliedFilters.color] : [],
    body: appliedFilters.body ?? [],
    trans: appliedFilters.trans ?? null,
    owner: appliedFilters.owner ?? []
  }));

  useEffect(() => {
    if (open) {
      setState({
        priceMin: appliedFilters.priceMin ?? PRICE_MIN,
        priceMax: appliedFilters.priceMax ?? PRICE_MAX,
        brands: appliedFilters.brands ?? [],
        brandModels: appliedFilters.brandModels ?? {},
        year: appliedFilters.year ?? null,
        kms: appliedFilters.kms ?? null,
        colors: appliedFilters.color ? [appliedFilters.color] : [],
        body: appliedFilters.body ?? [],
        trans: appliedFilters.trans ?? null,
        owner: appliedFilters.owner ?? []
      });
      document.body.style.overflow = "hidden";
    } else document.body.style.overflow = "";
    return () => (document.body.style.overflow = "");
  }, [open, appliedFilters]);

  if (!open) return null;

  function toggleBrand(b) {
    setState((s) => {
      const setB = new Set(s.brands);
      if (setB.has(b)) {
        setB.delete(b);
        const bm = { ...s.brandModels };
        delete bm[b];
        return { ...s, brands: Array.from(setB), brandModels: bm };
      } else {
        setB.add(b);
        const bm = { ...s.brandModels };
        if (!bm[b]) bm[b] = [brandsToModels[b]?.[0]].filter(Boolean);
        return { ...s, brands: Array.from(setB), brandModels: bm };
      }
    });
  }

  function toggleModel(brand, model) {
    setState((s) => {
      const bm = { ...(s.brandModels || {}) };
      const arr = new Set(bm[brand] || []);
      if (arr.has(model)) arr.delete(model);
      else arr.add(model);
      bm[brand] = Array.from(arr);
      if (bm[brand].length === 0) {
        // ensure at least one model stays selected for a brand — pick first
        const fallback = brandsToModels[brand] && brandsToModels[brand][0];
        if (fallback) bm[brand] = [fallback];
        else delete bm[brand];
      }
      const brands = new Set(s.brands);
      if ((bm[brand] || []).length) brands.add(brand);
      return { ...s, brandModels: bm, brands: Array.from(brands) };
    });
  }

  function setPrice(which, value) {
    const n = Math.round(Number(value) || 0);
    setState((s) => {
      if (which === "min") {
        const minv = Math.min(n, s.priceMax - PRICE_STEP);
        return { ...s, priceMin: Math.max(PRICE_MIN, minv) };
      } else {
        const maxv = Math.max(n, s.priceMin + PRICE_STEP);
        return { ...s, priceMax: Math.min(PRICE_MAX, maxv) };
      }
    });
  }

  function apply() {
    // always send numeric rupees
    const out = {
      priceMin: Math.round(state.priceMin),
      priceMax: Math.round(state.priceMax),
      brands: state.brands,
      brandModels: state.brandModels,
      year: state.year,
      kms: state.kms,
      color: state.colors[0] ?? null,
      body: state.body,
      trans: state.trans,
      owner: state.owner
    };
    onApply(out);
    onClose();
  }

  function clearAll() {
    setState({
      priceMin: PRICE_MIN,
      priceMax: PRICE_MAX,
      brands: [],
      brandModels: {},
      year: null,
      kms: null,
      colors: [],
      body: [],
      trans: null,
      owner: []
    });
    onClear();
  }

  const topPct = 100 - Math.round(((state.priceMax - PRICE_MIN) / (PRICE_MAX - PRICE_MIN)) * 100);
  const bottomPct = 100 - Math.round(((state.priceMin - PRICE_MIN) / (PRICE_MAX - PRICE_MIN)) * 100);
  const railGradient = `linear-gradient(180deg, #f0e8ff 0%, #f0e8ff ${topPct}%, #6b2be6 ${topPct}%, #6b2be6 ${bottomPct}%, #f0e8ff ${bottomPct}%, #f0e8ff 100%)`;

  return (
    <div className="filter-drawer-root" role="dialog" aria-modal="true">
      <div className="filter-backdrop" onClick={onClose} />
      <aside className="filter-drawer">
        <header className="filter-head">
          <button className="back" onClick={onClose}>←</button>
          <div className="title">Filter</div>
          <button className="clear-all" onClick={clearAll}>CLEAR ALL</button>
        </header>

        <div className="filter-body">
          <nav className="filter-nav">
            <button className="filter-nav-item active">Saved Filters</button>
            <button className="filter-nav-item active">Price Range</button>
            <button className="filter-nav-item">Brands + Models</button>
            <button className="filter-nav-item">Year</button>
            <button className="filter-nav-item">Kms Driven</button>
            <button className="filter-nav-item">Fuel Type</button>
            <button className="filter-nav-item">Body Type</button>
            <button className="filter-nav-item">Transmission</button>
            <button className="filter-nav-item">Car Category</button>
            <button className="filter-nav-item">Color</button>
            <button className="filter-nav-item">Features</button>
            <button className="filter-nav-item">Seats</button>
            <button className="filter-nav-item">RTO</button>
            <button className="filter-nav-item">Owner</button>
          </nav>

          <div className="filter-panel">
            {/* Price */}
            <div className="panel-section">
              <h4 className="panel-title">₹{state.priceMin.toLocaleString()} - ₹{state.priceMax.toLocaleString()}</h4>

              <div className="price-range-wrap">
                <div className="price-rail-vertical" style={{ background: railGradient }} />

                <div className="bubbles-wrapper">
                  <div className="price-bubble top" style={{ top: `${topPct}%` }}>₹{state.priceMax.toLocaleString()}</div>
                  <div className="price-bubble bottom" style={{ top: `${bottomPct}%` }}>₹{state.priceMin.toLocaleString()}</div>
                </div>

                <div className="vertical-ranges">
                  <input
                    className="v-range min"
                    type="range"
                    min={PRICE_MIN}
                    max={PRICE_MAX}
                    step={PRICE_STEP}
                    value={state.priceMin}
                    onChange={(e) => setPrice("min", e.target.value)}
                    aria-label="Min"
                  />
                  <input
                    className="v-range max"
                    type="range"
                    min={PRICE_MIN}
                    max={PRICE_MAX}
                    step={PRICE_STEP}
                    value={state.priceMax}
                    onChange={(e) => setPrice("max", e.target.value)}
                    aria-label="Max"
                  />
                </div>
              </div>
              <p className="hint">Drag handles to set price bracket.</p>
            </div>

            {/* Brands + Models (example, first brand expanded) */}
            <div className="panel-section">
              <h5 className="select-by">Brands + Models</h5>
              <div className="brands-grid">
                {Object.keys(brandsToModels).map((brand) => {
                  const selected = state.brands.includes(brand);
                  return (
                    <div key={brand} className="brand-block">
                      <label className="brand-row">
                        <input type="checkbox" checked={selected} onChange={() => toggleBrand(brand)} />
                        <span className="brand-name">{brand}</span>
                      </label>

                      {/* Expand models — always allow selecting models when brand is selected */}
                      <div className="models-inline">
                        {(brandsToModels[brand] || []).map((m) => {
                          const checked = (state.brandModels[brand] || []).includes(m);
                          return (
                            <label key={m} className="model-row">
                              <input type="checkbox" checked={checked} onChange={() => toggleModel(brand, m)} />
                              <span className="model-name">{m}</span>
                            </label>
                          );
                        })}
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>

            {/* Year radios */}
            <div className="panel-section">
              <h5 className="select-by">Year</h5>
              <div className="radio-list">
                {yearOptions.map((y, idx) => (
                  <label key={y} className="radio-row">
                    <input name="year" type="radio" checked={state.year === y} onChange={() => setState((s) => ({ ...s, year: s.year === y ? null : y }))} />
                    <span className="radio-label">{y}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Kms radios */}
            <div className="panel-section">
              <h5 className="select-by">Kms Driven</h5>
              <div className="radio-list">
                {kmsOptions.map((k) => (
                  <label key={k} className="radio-row">
                    <input name="kms" type="radio" checked={state.kms === k} onChange={() => setState((s) => ({ ...s, kms: s.kms === k ? null : k }))} />
                    <span className="radio-label">{k}</span>
                  </label>
                ))}
              </div>
            </div>

            {/* Color grid */}
            <div className="panel-section">
              <h5 className="select-by">Color</h5>
              <div className="color-grid">
                {colorMap.map((c) => (
                  <ColorOption
                    key={c.name}
                    id={`col-${c.name}`}
                    name={c.name}
                    color={c.code}
                    checked={state.colors.includes(c.name)}
                    onChange={(val) => setState((s) => ({ ...s, colors: val ? [...new Set([...s.colors, c.name])] : s.colors.filter(x => x !== c.name) }))}
                  />
                ))}
              </div>
            </div>

            {/* Owner */}
            <div className="panel-section">
              <h5 className="select-by">Owner</h5>
              <div className="grid-cols">
                {["1st owner", "2nd owner", "3rd owner"].map((o) => (
                  <FancyCheckbox
                    key={o}
                    id={`owner-${o}`}
                    label={o}
                    checked={state.owner.includes(o)}
                    onChange={(v) => setState((s) => ({ ...s, owner: v ? [...s.owner, o] : s.owner.filter(x => x !== o) }))}
                  />
                ))}
              </div>
            </div>

          </div>
        </div>

        <footer className="filter-footer">
          <button className="btn-outline" onClick={clearAll}>Reset</button>
          <button className="btn-primary" onClick={apply}>Apply</button>
        </footer>
      </aside>
    </div>
  );
}

/* Main BuyPage */
export default function BuyPage() {
  const [filterOpen, setFilterOpen] = useState(false);
  const [sortOpen, setSortOpen] = useState(false);
  const [sortKey, setSortKey] = useState("relevance");

  const [appliedFilters, setAppliedFilters] = useState({
    year: null,
    kms: null,
    brands: [],
    brandModels: {},
    color: null,
    body: [],
    trans: null,
    priceMin: null,
    priceMax: null,
    owner: []
  });

  const [cars, setCars] = useState([]);
  useEffect(() => {
    // sample data (priceNum in lakhs)
    setCars([
      { id: "c1", brand: "Maruti Suzuki", model: "Tiago", title: "2018 Maruti Tiago", priceNum: 3.74, price: "₹3.74 Lakh", kmNum: 61000, km: "61,000 km", fuel: "CNG", transVal: "manual", location: "DL12", year: "2018", body: "Hatchback", color: "White", thumb: process.env.PUBLIC_URL + "/baleno.avif" },
      { id: "c2", brand: "Tata", model: "Harrier", title: "2022 Tata Harrier", priceNum: 22.75, price: "₹22.75 Lakh", kmNum: 24500, km: "24,500 km", fuel: "Diesel", transVal: "automatic", location: "HR98", year: "2022", body: "SUV", color: "Black", thumb: process.env.PUBLIC_URL + "/venue.avif" },
      { id: "c3", brand: "Maruti Suzuki", model: "Swift", title: "2020 Maruti Swift", priceNum: 6.10, price: "₹6.10 Lakh", kmNum: 45000, km: "45,000 km", fuel: "Petrol", transVal: "manual", location: "DL05", year: "2020", body: "Hatchback", color: "Red", thumb: process.env.PUBLIC_URL + "/swift.avif" },
      { id: "c4", brand: "Hyundai", model: "Verna", title: "2021 Hyundai Verna", priceNum: 8.40, price: "₹8.40 Lakh", kmNum: 33000, km: "33,000 km", fuel: "Petrol", transVal: "amt", location: "MH04", year: "2021", body: "Sedan", color: "Silver", thumb: process.env.PUBLIC_URL + "/verna.avif" },
      { id: "c5", brand: "Toyota", model: "Glanza", title: "2023 Toyota Glanza", priceNum: 7.60, price: "₹7.60 Lakh", kmNum: 12000, km: "12,000 km", fuel: "Petrol", transVal: "amt", location: "DL09", year: "2023", body: "Hatchback", color: "Silver", thumb: process.env.PUBLIC_URL + "/glanza.avif" },
    ]);
  }, []);

  function matchesFilters(c, f) {
    // brands/models
    if (f.brands && f.brands.length) {
      if (!f.brands.includes(c.brand)) {
        // check models selection if brandModels provided
        const bm = f.brandModels && f.brandModels[c.brand];
        if (!bm || (bm.length && !bm.includes(c.model))) return false;
      }
    }
    // trans
    if (f.trans && c.transVal && f.trans !== c.transVal) return false;
    // year (radio like)
    if (f.year) {
      if (f.year === "2023 & above" && Number(c.year) < 2023) return false;
      if (f.year === "2021 & above" && Number(c.year) < 2021) return false;
      if (f.year === "2019 & above" && Number(c.year) < 2019) return false;
    }
    // kms
    if (f.kms) {
      const kms = c.kmNum || 0;
      if (f.kms === "< 20,000" && kms > 20000) return false;
      if (f.kms === "20,001 - 50,000" && (kms < 20001 || kms > 50000)) return false;
      if (f.kms === "50,001 - 1,00,000" && (kms < 50001 || kms > 100000)) return false;
    }
    // color
    if (f.color && c.color && f.color !== c.color) return false;
    // body
    if (f.body && f.body.length && !f.body.includes(c.body)) return false;
    // owner (not in sample)
    // price - convert car.priceNum (lakhs) to rupees
    if (f.priceMin != null && f.priceMax != null) {
      const carPrice = (c.priceNum || 0) * 100000;
      if (carPrice < Number(f.priceMin) || carPrice > Number(f.priceMax)) return false;
    }
    return true;
  }

  function applySort(list) {
    switch (sortKey) {
      case "price_asc": return list.sort((a, b) => a.priceNum - b.priceNum);
      case "price_desc": return list.sort((a, b) => b.priceNum - a.priceNum);
      default: return list;
    }
  }

  const visible = applySort(cars.filter((c) => matchesFilters(c, appliedFilters)));

  return (
    <div className="buypage-root">
      <header className="buy-header">
        <div className="buy-header-inner">
          <button className="hamburger">☰</button>
          <div className="brand">CarsDedo</div>
          <div className="header-actions">
            <button className="action">Sell</button>
            <button className="action active">Buy</button>
          </div>
        </div>

        <div className="hero-banner">
          <img src={HERO_BANNER} alt="Top deals" className="hero-img" />
        </div>

        <div className="filters-top">
          <FilterDropdown label={`Year${appliedFilters.year ? `: ${appliedFilters.year}` : ""}`} options={[
            { value: "2023", label: "2023 & above" },
            { value: "2021", label: "2021 & above" },
            { value: "2019", label: "2019 & above" }
          ]} selected={appliedFilters.year} onChange={(v) => setAppliedFilters((s) => ({ ...s, year: v }))} />

          <FilterDropdown label={`Transmission${appliedFilters.trans ? `: ${appliedFilters.trans}` : ""}`} options={[
            { value: "automatic", label: "Automatic" },
            { value: "manual", label: "Manual" },
            { value: "amt", label: "AMT" }
          ]} selected={appliedFilters.trans} onChange={(v) => setAppliedFilters((s) => ({ ...s, trans: v }))} />

          <FilterDropdown label={`Body Type${appliedFilters.body && appliedFilters.body.length ? `: ${appliedFilters.body[0]}` : ""}`} options={[
            { value: "Hatchback", label: "Hatchback" },
            { value: "Sedan", label: "Sedan" },
            { value: "SUV", label: "SUV" },
          ]} selected={appliedFilters.body && appliedFilters.body[0]} onChange={(v) => setAppliedFilters((s) => ({ ...s, body: v ? [v] : [] }))} />

          <FilterDropdown label={`Color${appliedFilters.color ? `: ${appliedFilters.color}` : ""}`} options={[
            { value: "White", label: "White" }, { value: "Black", label: "Black" }, { value: "Silver", label: "Silver" }
          ]} selected={appliedFilters.color} onChange={(v) => setAppliedFilters((s) => ({ ...s, color: v }))} />
        </div>
      </header>

      <div className="results-header"><h3>{visible.length} Used cars in Delhi</h3></div>

      <main className="buy-main">
        <div className="list">
          {visible.length ? visible.map((car) => (
            <article key={car.id} className="car-card">
              <div className="car-thumb"><img src={car.thumb} alt={car.title} loading="lazy" /></div>
              <div className="car-content">
                <div className="car-row">
                  <div>
                    <div className="car-title">{car.title}</div>
                    <div className="car-variant">{car.brand} {car.model}</div>
                  </div>
                  <div className="car-price"><div className="price">{car.price}</div></div>
                </div>
                <div className="car-tags"><span className="tag">{car.km}</span><span className="tag">{car.fuel}</span></div>
              </div>
            </article>
          )) : <div style={{ padding: 24 }}>No cars match your filters.</div>}
        </div>
      </main>

      <BottomNav />

      {!filterOpen && (
        <div className="bottom-controls">
          <button className="bottom-btn" onClick={() => setSortOpen(true)}>⇅ Sort</button>
          <button className="bottom-btn primary" onClick={() => setFilterOpen(true)}>⚙ Filter</button>
        </div>
      )}

      {/* Filter drawer - controlled */}
      <FilterDrawer
        open={filterOpen}
        appliedFilters={appliedFilters}
        onApply={(filters) => {
          // ensure numeric price bounds
          setAppliedFilters({
            ...filters,
            priceMin: Math.round(Number(filters.priceMin || 50000)),
            priceMax: Math.round(Number(filters.priceMax || 7000000))
          });
        }}
        onClose={() => setFilterOpen(false)}
        onClear={() => setAppliedFilters({
          year: null, kms: null, brands: [], brandModels: {}, color: null, body: [], trans: null, priceMin: null, priceMax: null, owner: []
        })}
      />
    </div>
  );
}
