// src/components/Filters.jsx
import React, { useMemo } from "react";

/**
 * Props:
 *  - filters: current filter state object
 *  - setFilters: setter function
 *  - metadata: {brands, years, fuels, bodies} arrays for UI
 */
export default function Filters({ filters, setFilters, metadata }) {
  const { brands = [], years = [], fuels = [], bodies = [] } = metadata;

  // toggle helpers
  function toggleArray(key, value) {
    setFilters((s) => {
      const set = new Set(s[key]);
      if (set.has(value)) set.delete(value);
      else set.add(value);
      return { ...s, [key]: Array.from(set) };
    });
  }

  // Price min/max handlers with guards
  function setPriceMin(v) {
    const val = Number(v) || 0;
    // ensure min <= max - 1000 (small gap)
    const maxAllowed = Math.max(filters.priceMax, val);
    if (val > filters.priceMax) {
      setFilters((s) => ({ ...s, priceMin: filters.priceMax, priceMax: val }));
    } else {
      setFilters((s) => ({ ...s, priceMin: val }));
    }
  }
  function setPriceMax(v) {
    const val = Number(v) || 0;
    if (val < filters.priceMin) {
      setFilters((s) => ({ ...s, priceMin: val, priceMax: filters.priceMin }));
    } else {
      setFilters((s) => ({ ...s, priceMax: val }));
    }
  }

  // quick presets (optional)
  const presets = useMemo(
    () => [
      { label: "Up to ₹5 L", min: 0, max: 500000 },
      { label: "Up to ₹10 L", min: 0, max: 1000000 },
      { label: "₹5L - ₹20L", min: 500000, max: 2000000 },
    ],
    []
  );

  return (
    <aside className="filters" aria-label="Filters">
      {/* --- Search --- */}
      <div className="filter-block">
        <label className="filter-label">Search</label>
        <input
          className="filter-search"
          placeholder="Search title, model..."
          value={filters.q}
          onChange={(e) => setFilters((s) => ({ ...s, q: e.target.value }))}
        />
      </div>

      {/* --- Price (dual range + numeric) --- */}
      <div className="filter-block">
        <label className="filter-label">Price (₹)</label>

        <div className="price-presets" aria-hidden>
          {presets.map((p) => (
            <button
              key={p.label}
              type="button"
              className="pill preset"
              onClick={() => setFilters((s) => ({ ...s, priceMin: p.min, priceMax: p.max }))}
            >
              {p.label}
            </button>
          ))}
        </div>

        <div className="price-range-row">
          {/* numeric inputs */}
          <input
            type="number"
            className="price-input"
            min="0"
            value={filters.priceMin}
            onChange={(e) => setPriceMin(e.target.value)}
            aria-label="Minimum price"
          />
          <span className="price-sep">—</span>
          <input
            type="number"
            className="price-input"
            min="0"
            value={filters.priceMax}
            onChange={(e) => setPriceMax(e.target.value)}
            aria-label="Maximum price"
          />
        </div>

        {/* two range sliders (min + max). They overlap visually;
            JS ensures min <= max */}
        <div className="range-wrap">
          <input
            type="range"
            min="0"
            max="5000000"
            step="10000"
            value={filters.priceMin}
            onChange={(e) => setPriceMin(Number(e.target.value))}
            className="range-min"
            aria-label="Price minimum slider"
          />
          <input
            type="range"
            min="0"
            max="5000000"
            step="10000"
            value={filters.priceMax}
            onChange={(e) => setPriceMax(Number(e.target.value))}
            className="range-max"
            aria-label="Price maximum slider"
          />
        </div>

        <div className="price-values">
          <small>Min: ₹{Math.round(filters.priceMin / 1000)}k</small>
          <small>Max: ₹{Math.round(filters.priceMax / 1000)}k</small>
        </div>
      </div>

      {/* --- Brands --- */}
      <div className="filter-block">
        <label className="filter-label">Brands</label>
        <div className="brand-list">
          {brands.map((b) => (
            <label key={b} className="checkbox">
              <input
                type="checkbox"
                checked={filters.brands.includes(b)}
                onChange={() => toggleArray("brands", b)}
              />
              <span>{b}</span>
            </label>
          ))}
        </div>
      </div>

      {/* --- Year --- */}
      <div className="filter-block">
        <label className="filter-label">Year</label>
        <div>
          {years.map((y) => (
            <label key={y} className="radio">
              <input
                type="radio"
                name="year"
                value={y}
                checked={String(filters.year) === String(y)}
                onChange={() => setFilters((s) => ({ ...s, year: y }))}
              />
              <span>{y} & above</span>
            </label>
          ))}
          <label className="radio">
            <input
              type="radio"
              name="year"
              value=""
              checked={filters.year === ""}
              onChange={() => setFilters((s) => ({ ...s, year: "" }))}
            />
            <span>Any</span>
          </label>
        </div>
      </div>

      {/* --- KM Driven --- */}
      <div className="filter-block">
        <label className="filter-label">Km Driven</label>
        <div>
          {[10000, 30000, 50000, 75000, 100000].map((k) => (
            <label key={k} className="checkbox">
              <input
                type="checkbox"
                checked={filters.kms.includes(k)}
                onChange={() =>
                  setFilters((s) => {
                    const set = new Set(s.kms);
                    if (set.has(k)) set.delete(k);
                    else set.add(k);
                    return { ...s, kms: Array.from(set) };
                  })
                }
              />
              <span>{k.toLocaleString()} km or less</span>
            </label>
          ))}
        </div>
      </div>

      {/* --- Fuel Type --- */}
      <div className="filter-block">
        <label className="filter-label">Fuel Type</label>
        <div>
          {fuels.map((f) => (
            <label key={f} className="checkbox">
              <input
                type="checkbox"
                checked={filters.fuel.includes(f)}
                onChange={() => toggleArray("fuel", f)}
              />
              <span>{f}</span>
            </label>
          ))}
        </div>
      </div>

      {/* --- Body Type --- */}
      <div className="filter-block">
        <label className="filter-label">Body Type</label>
        <div className="body-list">
          {bodies.map((b) => (
            <label key={b} className="checkbox">
              <input
                type="checkbox"
                checked={filters.body.includes(b)}
                onChange={() => toggleArray("body", b)}
              />
              <span>{b}</span>
            </label>
          ))}
        </div>
      </div>

      {/* --- Reset --- */}
      <div className="filter-actions">
        <button
          className="btn"
          onClick={() =>
            setFilters({
              q: "",
              priceMin: 0,
              priceMax: 5000000,
              brands: [],
              year: "",
              kms: [],
              fuel: [],
              body: [],
              sortBy: "relevance",
            })
          }
        >
          Reset
        </button>
      </div>
    </aside>
  );
}
