// src/pages/CarDetails.jsx
import React, { useEffect, useRef, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import carsData from "../data/cars";
import "../styles/CarDetailsWeb.css";

const TOPBAR_HEIGHT = 72; // if your topbar height differs, update this constant + CSS --topbar-height

export default function CarDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const car = carsData.find((c) => Number(c.id) === Number(id));

  // refs
  const containerRef = useRef(null);
  const leftRef = useRef(null);
  const heroRef = useRef(null);
  const overviewRef = useRef(null);
  const reportRef = useRef(null);
  const specsRef = useRef(null);
  const financeRef = useRef(null);
  const rightWrapperRef = useRef(null);
  const rightCardRef = useRef(null);

  // UI state
  const [showStickyTabs, setShowStickyTabs] = useState(false);
  const [activeTab, setActiveTab] = useState("overview");
  const [stickyStyle, setStickyStyle] = useState({ left: 0, width: 760 });
  const [rightMode, setRightMode] = useState("fixed"); // "fixed" | "absolute" | "static"
  const [heroImage, setHeroImage] = useState(car ? (car.images?.[0] || car.image) : "");

  const sections = [
    { id: "overview", ref: overviewRef },
    { id: "report", ref: reportRef },
    { id: "specs", ref: specsRef },
    { id: "finance", ref: financeRef },
  ];

  // Fallback safety
  useEffect(() => {
    if (!car) return;
    setHeroImage(car.images && car.images.length ? car.images[0] : car.image);
  }, [car]);

  // show sticky tabs when hero mostly out of view
  useEffect(() => {
    if (!heroRef.current) return;
    const obs = new IntersectionObserver(
      (entries) => setShowStickyTabs(!entries[0].isIntersecting),
      { threshold: 0.05 }
    );
    obs.observe(heroRef.current);
    return () => obs.disconnect();
  }, []);

  // scrollspy for active tab
  useEffect(() => {
    const obs = new IntersectionObserver(
      (entries) => {
        const visible = entries.filter((e) => e.isIntersecting);
        if (visible.length > 0) {
          visible.sort((a, b) => b.intersectionRatio - a.intersectionRatio);
          setActiveTab(visible[0].target.dataset.section);
        }
      },
      {
        root: null,
        rootMargin: `-${TOPBAR_HEIGHT + 8}px 0px -40% 0px`,
        threshold: [0.12, 0.4, 0.7],
      }
    );

    sections.forEach((s) => s.ref.current && obs.observe(s.ref.current));
    return () => obs.disconnect();
  }, []);

  // sticky tabs position calculation
  const updateStickyPosition = useCallback(() => {
    if (!leftRef.current || !containerRef.current) return;
    const leftRect = leftRef.current.getBoundingClientRect();
    const containerRect = containerRef.current.getBoundingClientRect();
    // clamp left inside container + small gutter
    const left = Math.max(containerRect.left + 12, leftRect.left);
    // cap width so tab row doesn't get excessively wide
    const width = Math.min(leftRect.width, 760);
    setStickyStyle({ left: Math.round(left), width: Math.round(width) });
  }, []);

  useEffect(() => {
    updateStickyPosition();
    window.addEventListener("resize", updateStickyPosition);
    return () => window.removeEventListener("resize", updateStickyPosition);
  }, [updateStickyPosition]);

  // compute right card behavior using sentinel element (preferred) or footer fallback
  useEffect(() => {
    function computeRightMode() {
      const sentinel = document.getElementById("right-sentinel");
      const fallbackFooter = document.querySelector("footer");
      const targetElement = sentinel || fallbackFooter;
      const rightWrapper = rightWrapperRef.current;
      const card = rightCardRef.current;
      if (!rightWrapper || !card || !targetElement) {
        // if anything missing, default to fixed
        setRightMode("fixed");
        return;
      }

      const wrapperRect = rightWrapper.getBoundingClientRect();
      const cardRect = card.getBoundingClientRect();
      const targetRect = targetElement.getBoundingClientRect();
      const viewportHeight = window.innerHeight;

      // If sentinel/top of footer has come into view (targetRect.top <= viewportHeight)
      if (targetRect.top <= viewportHeight - 8) {
        // when footer (or sentinel) visible, allow normal flow (static)
        setRightMode("static");
        return;
      }

      // determine if wrapper bottom is close to viewport bottom and card would overlap next area
      // compute distance from top to wrapper bottom:
      const wrapperBottomFromTop = wrapperRect.bottom;
      // if wrapper bottom is within card height + margin of viewport bottom, switch to absolute anchor
      if (wrapperBottomFromTop <= cardRect.height + (TOPBAR_HEIGHT + 36)) {
        setRightMode("absolute");
        return;
      }

      // otherwise fixed
      setRightMode("fixed");
    }

    computeRightMode();
    const throttled = () => window.requestAnimationFrame(computeRightMode);
    window.addEventListener("scroll", throttled, { passive: true });
    window.addEventListener("resize", throttled);

    // Also monitor sentinel presence (in case app adds/removes)
    const mo = new MutationObserver(computeRightMode);
    mo.observe(document.body, { subtree: true, childList: true });

    return () => {
      window.removeEventListener("scroll", throttled);
      window.removeEventListener("resize", throttled);
      mo.disconnect();
    };
  }, []);

  function scrollToRef(ref) {
    if (!ref || !ref.current) return;
    const y = ref.current.getBoundingClientRect().top + window.scrollY - TOPBAR_HEIGHT - 8;
    window.scrollTo({ top: y, behavior: "smooth" });
  }

  if (!car) {
    return (
      <div style={{ padding: 40 }}>
        <h3>Car not found</h3>
        <button onClick={() => navigate(-1)}>Go back</button>
      </div>
    );
  }

  const thumbs = car.images && car.images.length ? car.images : [car.image];

  return (
    <div className="car-details-page-wrapper">
      {/* sticky tabs left-only */}
      <div
        className={`cd-sticky-tabs ${showStickyTabs ? "visible" : ""}`}
        style={{
          top: `${TOPBAR_HEIGHT + 8}px`,
          left: `${stickyStyle.left}px`,
          width: `${stickyStyle.width}px`,
        }}
      >
        <div className="cd-sticky-inner">
          <button className={`cd-tab ${activeTab === "overview" ? "active" : ""}`} onClick={() => scrollToRef(overviewRef)}>
            Overview
          </button>
          <button className={`cd-tab ${activeTab === "report" ? "active" : ""}`} onClick={() => scrollToRef(reportRef)}>
            Report
          </button>
          <button className={`cd-tab ${activeTab === "specs" ? "active" : ""}`} onClick={() => scrollToRef(specsRef)}>
            Feature & Specs
          </button>
          <button className={`cd-tab ${activeTab === "finance" ? "active" : ""}`} onClick={() => scrollToRef(financeRef)}>
            Finance
          </button>
        </div>
      </div>

      <div className="car-details-container" ref={containerRef}>
        {/* LEFT column */}
        <main className="cd-left" ref={leftRef}>
          <div className="cd-hero" ref={heroRef}>
            <div className="cd-hero-inner">
              <div className="cd-hero-image">
                <img src={heroImage} alt={car.title} />
                <div className="cd-hero-controls">
                  <button className="cd-back" onClick={() => navigate(-1)}> Back</button>
                </div>
              </div>

              <div className="cd-thumbs">
                {thumbs.map((t, i) => (
                  <button key={i} className={`cd-thumb ${t === heroImage ? "selected" : ""}`} onClick={() => setHeroImage(t)}>
                    <img src={t} alt={`thumb-${i}`} />
                  </button>
                ))}
              </div>
            </div>
          </div>

          <nav className="cd-hero-tabs">
            <button className={`hero-tab ${activeTab === "overview" ? "active" : ""}`} onClick={() => scrollToRef(overviewRef)}>Overview</button>
            <button className={`hero-tab ${activeTab === "report" ? "active" : ""}`} onClick={() => scrollToRef(reportRef)}>Report</button>
            <button className={`hero-tab ${activeTab === "specs" ? "active" : ""}`} onClick={() => scrollToRef(specsRef)}>Feature & Specs</button>
            <button className={`hero-tab ${activeTab === "finance" ? "active" : ""}`} onClick={() => scrollToRef(financeRef)}>Finance</button>
          </nav>

          <section id="overview" ref={overviewRef} data-section="overview" className="cd-section overview">
            <h2 className="cd-section-title">Car Overview</h2>
            <div className="cd-card overview-card">
              <div className="row">
                <div className="field">
                  <div className="label">Make Year</div>
                  <div className="value">May 2024</div>
                </div>
                <div className="field">
                  <div className="label">Registration Year</div>
                  <div className="value">Jun 2024</div>
                </div>
                <div className="field">
                  <div className="label">Fuel Type</div>
                  <div className="value">{car.fuel}</div>
                </div>
              </div>

              <div className="row">
                <div className="field">
                  <div className="label">Km driven</div>
                  <div className="value">{car.km.toLocaleString()} km</div>
                </div>
                <div className="field">
                  <div className="label">Transmission</div>
                  <div className="value">{car.transmission}</div>
                </div>
                <div className="field">
                  <div className="label">No. of Owner</div>
                  <div className="value">{car.owner || "1st Owner"}</div>
                </div>
              </div>

              <div className="row last">
                <div className="field full">
                  <div className="label">Car Location</div>
                  <div className="value">{car.city}</div>
                </div>
              </div>
            </div>
          </section>

          <section id="report" ref={reportRef} data-section="report" className="cd-section report">
            <h2 className="cd-section-title">Quality report</h2>
            <p className="muted">1571 parts evaluated by 5 automotive experts</p>
            <div className="cd-card report-card">
              <div className="report-grid">
                <div className="report-item"><div className="report-title">Core systems</div><div className="report-score">9.8</div></div>
                <div className="report-item"><div className="report-title">Supporting systems</div><div className="report-score">9.7</div></div>
                <div className="report-item"><div className="report-title">Interiors & AC</div><div className="report-score">9.6</div></div>
                <div className="report-item"><div className="report-title">Exteriors & lights</div><div className="report-score">9.3</div></div>
              </div>
            </div>
          </section>

          <section id="specs" ref={specsRef} data-section="specs" className="cd-section specs">
            <h2 className="cd-section-title">Feature & Specs</h2>
            <div className="cd-card specs-card">
              <div className="specs-grid">
                <div className="spec"><div className="spec-ttl">Mileage (ARAI)</div><div className="spec-val">22.35 kmpl</div></div>
                <div className="spec"><div className="spec-ttl">Boot space</div><div className="spec-val">318 litres</div></div>
                <div className="spec"><div className="spec-ttl">Displacement</div><div className="spec-val">1197 cc</div></div>
              </div>
              <div className="features"><h4>Top Features of this car</h4>
                <div className="features-grid">
                  <ul><li>Airbags</li><li>Hill hold control</li><li>Parking sensors</li><li>Electronic stability program (ESP)</li></ul>
                  <ul><li>Rear defogger</li></ul>
                  <ul><li>Power boot</li><li>Automatic climate control</li><li>Adjustable ORVM</li></ul>
                </div>
              </div>
            </div>
          </section>

          <section id="finance" ref={financeRef} data-section="finance" className="cd-section finance">
            <h2 className="cd-section-title">Finance</h2>
            <div className="cd-card finance-card">
              <div className="emi">
                <div className="emi-left">
                  <div className="emi-number">9,400</div>
                  <div className="muted">per month (approx)</div>
                </div>
                <div className="emi-right">
                  <div className="muted">Loan Amount</div>
                  <div className="value">{(car.price - 110400).toLocaleString()}</div>
                  <div className="muted">Duration</div>
                  <div className="value">66 Months</div>
                </div>
              </div>
              <div className="finance-cta"><button className="btn primary">Check eligibility</button></div>
            </div>
          </section>

          <div style={{ height: 48 }} />
        </main>

        {/* RIGHT wrapper */}
        <aside ref={rightWrapperRef} className={`cd-right ${rightMode === "static" ? "static-mode" : ""}`}>
          <div
            ref={rightCardRef}
            className={`cd-right-card transition-mode-${rightMode}`}
            style={
              rightMode === "fixed"
                ? { position: "fixed", top: `${TOPBAR_HEIGHT + 12}px`, right: "calc((100% - 1180px)/2 + 18px)" }
                : rightMode === "absolute"
                ? { position: "absolute", bottom: 0, top: "auto", right: 0 }
                : { position: "relative", top: "auto", bottom: "auto" } // static
            }
          >
            <div className="right-inner">
              <div className="title-row">
                <h3 className="car-title">{car.title}</h3>
                <button className="like"></button>
              </div>

              <div className="meta">{car.km.toLocaleString()} km  {car.fuel}  {car.transmission}</div>
              <div className="hub">Spinny Car Hub, {car.city}</div>

              <div className="price-block">
                <div className="label muted">Fixed on road price</div>
                <div className="price">{(car.price / 100000).toFixed(2)} L</div>
                <div className="muted small">Includes RC transfer, insurance & more</div>
              </div>

              <div className="cta">
                <button className="btn book">BOOK NOW</button>
                <button className="btn test">FREE TEST DRIVE</button>
              </div>

              <div className="share">
                <p>Share with a friend :</p>
                <div className="icons">  f    </div>
              </div>
            </div>
          </div>
        </aside>
      </div>
    </div>
  );
}
