import React, { useEffect, useRef, useState } from "react";
import "../styles/StoriesSection.css";

const STORIES = [
  {
    id: "s1",
    image: process.env.PUBLIC_URL + "/moneyback.png",
    name: "Ayush Srivastava",
    city: "Lucknow",
    text: "Our first car that we'd truly love for years to come.",
  },
  {
    id: "s2",
    image: process.env.PUBLIC_URL + "/stories/s2.jpg",
    name: "Darshan",
    city: "Delhi",
    text: "Our family had our hearts set on XUV 700. So, when we saw it on Spinny, we just got it home.",
  },
  {
    id: "s3",
    image: process.env.PUBLIC_URL + "/stories/s3.jpg",
    name: "Madhulika Singh",
    city: "Lucknow",
    text: "Spinny helped us find a family car that's great for daily commutes and long trips.",
  },
  {
    id: "s4",
    image: process.env.PUBLIC_URL + "/stories/s4.jpg",
    name: "Pazhaniappan",
    city: "Chennai",
    text: "Being able to spoil my picky son felt like a win for Spinny and a safe choice for me.",
  },
  // add more as needed
];

function useVisibleCount() {
  const [count, setCount] = useState(getFromWidth(window.innerWidth));
  useEffect(() => {
    let t;
    const onResize = () => {
      clearTimeout(t);
      t = setTimeout(() => setCount(getFromWidth(window.innerWidth)), 120);
    };
    window.addEventListener("resize", onResize);
    return () => {
      clearTimeout(t);
      window.removeEventListener("resize", onResize);
    };
  }, []);
  return count;

  function getFromWidth(w) {
    if (w <= 600) return 1;
    if (w <= 1000) return 2;
    return 3; // large screens show 3 cards (close to screenshot)
  }
}

export default function StoriesSection() {
  const visibleCount = useVisibleCount();
  const [index, setIndex] = useState(0);
  const maxIndex = Math.max(0, STORIES.length - visibleCount);
  const trackRef = useRef(null);

  useEffect(() => {
    // clamp index when visibleCount changes (for example on resize)
    setIndex((i) => Math.min(i, Math.max(0, STORIES.length - visibleCount)));
  }, [visibleCount]);

  const prev = () => setIndex((i) => Math.max(0, i - 1));
  const next = () => setIndex((i) => Math.min(i + 1, maxIndex));

  // track translate percentage
  const itemPercent = 100 / visibleCount;
  const trackWidthPercent = (STORIES.length * 100) / visibleCount; // total width in %
  const translatePercent = (index * 100) / STORIES.length; // works with track width

  return (
    <section className="stories-section" aria-label="Spinny love stories">
      <div className="stories-inner">
        <h2 className="stories-title">
          <span className="rule" />
          Over 2 Lakh Spinny Love Stories
          <span className="rule" />
        </h2>

        <div className="stories-wrapper">
          <button
            className="stories-arrow left"
            onClick={prev}
            disabled={index === 0}
            aria-label="Previous"
          >
            ‹
          </button>

          <div className="stories-viewport">
            <div
              className="stories-track"
              ref={trackRef}
              style={{
                width: `${trackWidthPercent}%`,
                transform: `translateX(-${(index * 100) / STORIES.length}%)`,
              }}
            >
              {STORIES.map((s) => (
                <article
                  key={s.id}
                  className="stories-item"
                  style={{ width: `${itemPercent}%` }}
                >
                  <div
                    className="story-card"
                    role="group"
                    aria-label={`${s.name} ${s.city} story`}
                    style={{ backgroundImage: `url(${s.image})` }}
                  >
                    <div className="story-badge">myspinny</div>

                    <div className="story-overlay" aria-hidden />

                    <div className="story-footer">
                      <div className="story-icon">
                        {/* small purple diamond avatar - replace with image if you have one */}
                        <svg width="28" height="28" viewBox="0 0 24 24" fill="none" aria-hidden>
                          <rect x="5" y="5" width="14" height="14" rx="3" transform="rotate(45 12 12)" fill="#6f1a7a"/>
                          <text x="12" y="15" textAnchor="middle" fill="#fff" fontSize="12" fontWeight="700">S</text>
                        </svg>
                      </div>

                      <div className="story-meta">
                        <div className="story-name">{s.name} <span className="sep">|</span> <span className="city">{s.city}</span></div>
                        <div className="story-text">{s.text}</div>
                      </div>
                    </div>
                  </div>
                </article>
              ))}
            </div>
          </div>

          <button
            className="stories-arrow right"
            onClick={next}
            disabled={index >= maxIndex}
            aria-label="Next"
          >
            ›
          </button>
        </div>
      </div>
    </section>
  );
}
