        </div>
      </div>
    </>,
    document.body
  );
}

/* Fancy checkbox components used inside filter drawer */
function IconCheck({ checked }) {
  return (
    <svg className={`checkmark ${checked ? "show" : ""}`} width="14" height="14" viewBox="0 0 24 24" aria-hidden>
      <path fill="none" stroke="currentColor" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" d="M20 6L9 17l-5-5" />
    </svg>
  );
}
function FancyCheckbox({ checked = false, onToggle, label, id }) {
  return (
    <label className="f-chk" htmlFor={id}>
      <input id={id} type="checkbox" checked={checked} onChange={() => onToggle(!checked)} />
      <span className="f-chk-box" aria-hidden><IconCheck checked={checked} /></span>
      <span className="f-chk-label">{label}</span>
    </label>
  );
}
function ColorCheckbox({ colorCode = "#ddd", checked = false, onToggle, label, id }) {
  return (
    <label className="f-chk color" htmlFor={id}>
      <input id={id} type="checkbox" checked={checked} onChange={() => onToggle(!checked)} />
      <span className="f-chk-box" aria-hidden>
        <span className="swatch" style={{ backgroundColor: colorCode }} />
        <IconCheck checked={checked} />
      </span>
      <span className="f-chk-label">{label}</span>
    </label>
  );
}

/* =========================
   Filter drawer - BRANDS + MODELS expanded behavior
   - left nav stays visible on desktop
   - Brands list shows chevrons; expanding shows model checkboxes
   - selecting brand/model updates local state; Apply applies filters to main page
   ========================= */
function FilterDrawerEmbedded({ open, onClose, appliedFilters = {}, onApply, onClear }) {
  // local selection structure:
  // { year: [...], kms: [...], brands: [...], brandModels: { BrandName: [model1,model2] }, colors: [...], transmission: [...], features: [...], seats: [...], rto: [...] }
  const [local, setLocal] = useState({
    year: appliedFilters.year ? [appliedFilters.year] : [],
    kms: appliedFilters.kms ? [appliedFilters.kms] : [],
    brands: appliedFilters.brands ?? [],
    brandModels: {}, // maps brand -> array of selected models
    colors: appliedFilters.color ? [appliedFilters.color] : [],
    transmission: appliedFilters.trans ? [appliedFilters.trans] : [],
    features: appliedFilters.features ?? [],
    seats: appliedFilters.seats ?? [],
    rto: appliedFilters.rto ?? [],
  });

  const categories = [
    "Saved Filters",
    "Price Range",
    "Brands + Models",
    "Year",
    "Kms Driven",
    "Fuel Type",
    "Body Type",
    "Transmission",
    "Car Category",
    "Color",
    "Features",
    "Seats",
    "RTO",
    "Owner",
    "Spinny Hubs",
    "Availability",
  ];
  const [active, setActive] = useState("Brands + Models");
  const [brandQuery, setBrandQuery] = useState("");
  const [expandedBrand, setExpandedBrand] = useState(null); // brand currently expanded to show models

  // sample brands -> models mapping (replace with your real data)
  const brandsToModels = {
    "Maruti Suzuki": ["Baleno", "Swift", "Dzire", "Wagon R", "Ciaz"],
    Hyundai: ["i20", "Creta", "Verna", "Venue"],
    Honda: ["City", "Amaze", "Jazz"],
    Tata: ["Tiago", "Altroz", "Harrier"],
    Kia: ["Sonet", "Seltos"],
    Renault: ["Kiger", "Triber"],
    Ford: ["EcoSport"],
    Mahindra: ["XUV500"],
    Volkswagen: ["Polo"],
    Skoda: ["Rapid"],
    Toyota: ["Glanza"],
    Nissan: ["Magnite"],
    "MG Motors": ["Glamour"],
    Datsun: ["Go"],
  };

  // derived brand list from mapping
  const brandOptions = Object.keys(brandsToModels);

  // radio / checkbox option lists
  const yearOptions = ["2023 & above", "2021 & above", "2019 & above", "2017 & above", "2015 & above", "2013 & above"];
  const kmsOptions = ["0 - 20,000", "20,001 - 50,000", "50,001 - 1,00,000", "100,001+"];
  const transmissionOptions = ["Automatic", "Manual", "AMT"];
  const featuresOptions = ["Sunroof", "Keyless start", "ABS", "Airbags", "Rear camera", "Rear parking sensor", "Power windows", "Power steering"];
  const seatsOptions = ["4 seater", "5 seater", "6+ seater"];
  const rtoOptions = ["UP", "DL", "HR"];
  const colorMap = [
    { name: "Yellow, Golden", code: "#FFF56D" },
    { name: "Red, Maroon", code: "#C8342A" },
    { name: "Beige, Bronze, Brown", code: "#D9CBB3" },
    { name: "Violet, Purple, Wine", code: "#7B35D9" },
    { name: "White", code: "#FFFFFF" },
    { name: "Silver", code: "#CFCFCF" },
    { name: "Grey", code: "#949494" },
    { name: "Black", code: "#000000" },
    { name: "Blue", code: "#2D76E6" },
    { name: "Green", code: "#3BB54A" },
    { name: "Orange", code: "#F08D2E" },
  ];

  useEffect(() => {
    if (open) {
      setLocal((s) => ({
        year: appliedFilters.year ? [appliedFilters.year] : [],
        kms: appliedFilters.kms ? [appliedFilters.kms] : [],
        brands: appliedFilters.brands ?? [],
        brandModels: {}, // reset model selections on open (you can persist if you want)
        colors: appliedFilters.color ? [appliedFilters.color] : [],
        transmission: appliedFilters.trans ? [appliedFilters.trans] : [],
        features: appliedFilters.features ?? [],
        seats: appliedFilters.seats ?? [],
        rto: appliedFilters.rto ?? [],
      }));
      setActive("Brands + Models");
      setBrandQuery("");
      setExpandedBrand(null);
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
    return () => (document.body.style.overflow = "");
  }, [open, appliedFilters]);

  useEffect(() => {
    function onKey(e) { if (e.key === "Escape") onClose(); }
    if (open) window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [open, onClose]);

  if (!open) return null;

  /* Helpers to update local state */
  function toggleArray(key, value) {
    setLocal((s) => {
      const prev = s[key] || [];
      const found = prev.includes(value);
      return { ...s, [key]: found ? prev.filter((x) => x !== value) : [...prev, value] };
    });
  }

  function singleSelect(key, value) {
    setLocal((s) => ({ ...s, [key]: s[key] && s[key][0] === value ? [] : [value] }));
  }

  /* brand-level checkbox toggles brand selection and optionally clears/retains model selections */
  function toggleBrand(brand) {
    setLocal((s) => {
      const brands = s.brands || [];
      const isSelected = brands.includes(brand);
      const nextBrands = isSelected ? brands.filter((b) => b !== brand) : [...brands, brand];
      // when unselecting a brand, remove its models too
      const nextBrandModels = { ...(s.brandModels || {}) };
      if (isSelected) { delete nextBrandModels[brand]; }
      return { ...s, brands: nextBrands, brandModels: nextBrandModels };
    });
  }

  /* toggle model checkbox for a brand */
  function toggleModel(brand, model) {
    setLocal((s) => {
      const bm = { ...(s.brandModels || {}) };
      const prev = bm[brand] || [];
      const found = prev.includes(model);
      bm[brand] = found ? prev.filter((m) => m !== model) : [...prev, model];
      // if we add a model but brand not selected, ensure brand is selected too
      const brands = new Set(s.brands || []);
      if (!brands.has(brand)) brands.add(brand);
      return { ...s, brandModels: bm, brands: Array.from(brands) };
    });
  }

  function handleApply() {
    // build filter shape parent expects
    // combine brandModels into structure parent can use (we provide both)
    const out = {
      year: local.year[0] ?? null,
      kms: local.kms[0] ?? null,
      brands: local.brands,
      brandModels: local.brandModels,
      color: local.colors[0] ?? null,
      trans: local.transmission[0] ?? null,
      features: local.features,
      seats: local.seats,
      rto: local.rto,
    };
    onApply?.(out);
    onClose?.();
  }

  function handleClear() {
    setLocal({ year: [], kms: [], brands: [], brandModels: {}, colors: [], transmission: [], features: [], seats: [], rto: [] });
    onClear?.();
  }

  const filteredBrands = brandOptions.filter((b) => b.toLowerCase().includes(brandQuery.toLowerCase()));

  /* left meta badges or labels */
  function leftMeta(cat) {
    switch (cat) {
      case "Brands + Models": {
        const count = local.brands.length || Object.values(local.brandModels || {}).reduce((s, arr) => s + (arr?.length || 0), 0);
        return count ? `${count} selected` : null;
      }
      case "Color": return local.colors.length ? `${local.colors.length} selected` : null;
      case "Transmission": return local.transmission.length ? `${local.transmission.length} selected` : null;
      case "Features": return local.features.length ? `${local.features.length} selected` : null;
      case "Seats": return local.seats.length ? `${local.seats.length} selected` : null;
      case "RTO": return local.rto.length ? `${local.rto.length} selected` : null;
      case "Year": return local.year[0] ?? null;
      case "Kms Driven": return local.kms[0] ?? null;
      default: return null;
    }
  }

  return createPortal(
    <>
      <div className="filter-backdrop" onClick={onClose} />
      <aside className="filter-drawer" role="dialog" aria-modal="true" aria-label="Filter">
        <header className="filter-head">
          <button className="back" onClick={onClose} aria-label="Close filter"></button>
          <div className="title">Filter</div>
          <button className="clear-all" onClick={handleClear}>CLEAR ALL</button>
        </header>

        <div className="filter-body">
          <nav className="filter-nav" aria-label="Filter categories">
            {categories.map((c) => (
              <button key={c} className={`filter-nav-item ${c === active ? "active" : ""}`} onClick={() => setActive(c)}>
                <span className="nav-label">{c}</span>
                <span className="nav-meta">{leftMeta(c)}</span>
              </button>
            ))}
          </nav>

          <div className="filter-panel">
            {/* Price Range */}
            {active === "Price Range" && (
              <div className="panel-section">
                <h4 className="panel-title">50,000 - 70,00,000</h4>
                <div className="price-visual">
                  <div className="price-rail" />
                  <div className="price-handle top" />
                  <div className="price-handle bottom" />
                  <div className="price-bubble top">70,00,000</div>
                  <div className="price-bubble bottom">50,000</div>
                </div>
              </div>
            )}

            {/* BRANDS + MODELS */}
            {active === "Brands + Models" && (
              <div className="panel-section brands-panel">
                <label className="search">
                  <input placeholder="Search brands" value={brandQuery} onChange={(e) => setBrandQuery(e.target.value)} />
                  <span className="search-icon"></span>
                </label>

                <div className="brand-list">
                  {filteredBrands.map((brand) => {
                    const brandSelected = (local.brands || []).includes(brand);
                    const selectedModels = (local.brandModels && local.brandModels[brand]) || [];
                    const expanded = expandedBrand === brand;
                    return (
                      <div key={brand} className="brand-block">
                        <div className="brand-row">
                          <label className="brand-checkbox">
                            <input type="checkbox" checked={brandSelected} onChange={() => toggleBrand(brand)} />
                            <span className="brand-name">{brand}</span>
                          </label>

                          <button
                            className="brand-expand"
                            aria-expanded={expanded}
                            onClick={() => setExpandedBrand((prev) => (prev === brand ? null : brand))}
                            title={expanded ? "Collapse models" : "Show models"}
                          >
                            <span className={`chev ${expanded ? "open" : ""}`}></span>
                          </button>
                        </div>

                        {/* models list (expand) */}
                        {expanded && (
                          <div className="models-list">
                            {(brandsToModels[brand] || []).map((model) => {
                              const checked = selectedModels.includes(model);
                              return (
                                <label key={model} className="model-row">
                                  <input type="checkbox" checked={checked} onChange={() => toggleModel(brand, model)} />
                                  <span className="model-name">{model}</span>
                                </label>
                              );
                            })}
                            {/* quick action: select all / clear for that brand */}
                            <div className="model-actions">
                              <button onClick={() => {
                                (brandsToModels[brand] || []).forEach((m) => toggleModel(brand, m));
                              }}>Toggle all</button>
                              <button onClick={() => {
                                // clear models for this brand
                                setLocal((s) => {
                                  const bm = { ...(s.brandModels || {}) };
                                  delete bm[brand];
                                  const brands = (s.brands || []).filter((b) => b !== brand);
                                  return { ...s, brandModels: bm, brands };
                                });
                              }}>Clear</button>
                            </div>
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
              </div>
            )}

            {/* YEAR (radio) */}
            {active === "Year" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="radio-list">
                  {yearOptions.map((y) => {
                    const checked = local.year.includes(y);
                    return (
                      <label key={y} className="radio-row" onClick={() => singleSelect("year", y)}>
                        <input type="radio" name="year" checked={checked} onChange={() => singleSelect("year", y)} />
                        <span>{y}</span>
                      </label>
                    );
                  })}
                </div>
              </div>
            )}

            {/* KMS DRIVEN (radio) */}
            {active === "Kms Driven" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="radio-list">
                  {kmsOptions.map((k) => {
                    const checked = local.kms.includes(k);
                    return (
                      <label key={k} className="radio-row" onClick={() => singleSelect("kms", k)}>
                        <input type="radio" name="kms" checked={checked} onChange={() => singleSelect("kms", k)} />
                        <span>{k}</span>
                      </label>
                    );
                  })}
                </div>
              </div>
            )}

            {/* Transmission */}
            {active === "Transmission" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {transmissionOptions.map((t) => {
                    const checked = (local.transmission || []).includes(t);
                    return <FancyCheckbox key={t} id={`trans-${t}`} label={t} checked={checked} onToggle={() => toggleArray("transmission", t)} />;
                  })}
                </div>
              </div>
            )}

            {/* Color */}
            {active === "Color" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="color-grid">
                  {colorMap.map((c) => {
                    const checked = (local.colors || []).includes(c.name);
                    return <ColorCheckbox key={c.name} id={`color-${c.name}`} label={c.name} colorCode={c.code} checked={checked} onToggle={() => toggleArray("colors", c.name)} />;
                  })}
                </div>
              </div>
            )}

            {/* Features */}
            {active === "Features" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {featuresOptions.map((f) => {
                    const checked = (local.features || []).includes(f);
                    return <FancyCheckbox key={f} id={`feat-${f}`} label={f} checked={checked} onToggle={() => toggleArray("features", f)} />;
                  })}
                </div>
              </div>
            )}

            {/* Seats */}
            {active === "Seats" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {seatsOptions.map((s) => {
                    const checked = (local.seats || []).includes(s);
                    return <FancyCheckbox key={s} id={`seat-${s}`} label={s} checked={checked} onToggle={() => toggleArray("seats", s)} />;
                  })}
                </div>
              </div>
            )}

            {/* RTO */}
            {active === "RTO" && (
              <div className="panel-section">
                <h5 className="select-by">SELECT BY</h5>
                <div className="grid-cols">
                  {rtoOptions.map((r) => {
                    const checked = (local.rto || []).includes(r);
                    return <FancyCheckbox key={r} id={`rto-${r}`} label={r} checked={checked} onToggle={() => toggleArray("rto", r)} />;
                  })}
                </div>
              </div>
            )}

            {/* placeholders */}
            {["Saved Filters", "Fuel Type", "Body Type", "Car Category", "Owner", "Spinny Hubs", "Availability"].includes(active) && (
              <div className="panel-section"><p className="placeholder">Options for <strong>{active}</strong> go here  checkboxes, lists or controls as required.</p></div>
            )}
          </div>
        </div>

        <footer className="filter-footer">
          <button className="btn-outline" onClick={handleClear}>Reset</button>
          <button className="btn-primary" onClick={handleApply}>Apply</button>
        </footer>
      </aside>
    </>,
    document.body
  );
}

/* Simple swipable info (unchanged) */
function SwipableInfo({ slides = [] }) {
  const [idx, setIdx] = useState(0);
  const startXRef = useRef(null);
  useEffect(() => { if (!slides || slides.length === 0) return; setIdx((i) => Math.min(i, slides.length - 1)); }, [slides]);
  const prev = () => setIdx((i) => (i - 1 + slides.length) % slides.length);
  const next = () => setIdx((i) => (i + 1) % slides.length);
  function onTouchStart(e) { startXRef.current = e.touches[0].clientX; }
  function onTouchEnd(e) { if (startXRef.current == null) return; const endX = e.changedTouches[0].clientX; const diff = endX - startXRef.current; if (diff > 40) prev(); else if (diff < -40) next(); startXRef.current = null; }
  if (!slides || slides.length === 0) return null;
  const current = slides[idx];
  return (
    <div className="swipe-root" onTouchStart={onTouchStart} onTouchEnd={onTouchEnd} aria-roledescription="carousel">
      <div className="swipe-card">
        <div className="swipe-card-inner">
          <h4 className="swipe-title">{current.title ?? `Info ${idx + 1}`}</h4>
          <p className="swipe-text">{current.text ?? current}</p>
        </div>
        <div className="swipe-controls">
          <button aria-label="Previous" onClick={prev} className="swipe-btn"></button>
          <div className="swipe-dots" role="tablist">{slides.map((_, i) => <button key={i} onClick={() => setIdx(i)} className={`swipe-dot ${i === idx ? "active" : ""}`} aria-label={`Go to slide ${i + 1}`} />)}</div>
          <button aria-label="Next" onClick={next} className="swipe-btn"></button>
        </div>
      </div>
    </div>
  );
}

