import React, { useRef, useState, useEffect } from "react";
import "./CarsAcrossCity.css";

/**
 * CarsAcrossCity
 * - Hero carousel (swipeable - uses native scroll-snap for simplicity & reliability on mobile)
 * - Bottom swappable hub cards (horizontal scroll-snap + arrows)
 *
 * Replace image paths with your actual images in /public and update data arrays as needed.
 */

const HERO_SLIDES = [
  {
    id: "h1",
    image: process.env.PUBLIC_URL + "/hero1.jpg",
    bigTitle: "Extensive\nRange",
    subTitle: "S PARK",
  },
  {
    id: "h2",
    image: process.env.PUBLIC_URL + "/hero2.jpg",
    bigTitle: "Trusted\nSelection",
    subTitle: "Top hubs near you",
  },
  {
    id: "h3",
    image: process.env.PUBLIC_URL + "/hero3.jpg",
    bigTitle: "Great Prices\nEveryday",
    subTitle: "Choose & drive",
  },
];

const HUB_CARDS = [
  {
    id: "c1",
    title: "Spinny Car Hub, Taj Viva…",
    rating: 4.8,
    reviews: 1197,
    cars: 190,
    image: process.env.PUBLIC_URL + "/hub1.jpg",
  },
  {
    id: "c2",
    title: "Spinny Car Hub, Noida Mall",
    rating: 4.9,
    reviews: 256,
    cars: 72,
    image: process.env.PUBLIC_URL + "/hub2.jpg",
  },
  {
    id: "c3",
    title: "Spinny Car Hub, Gurugram",
    rating: 4.7,
    reviews: 410,
    cars: 120,
    image: process.env.PUBLIC_URL + "/hub3.jpg",
  },
  {
    id: "c4",
    title: "Spinny Car Hub, Rohini",
    rating: 4.6,
    reviews: 89,
    cars: 65,
    image: process.env.PUBLIC_URL + "/hub4.jpg",
  },
];

export default function CarsAcrossCity() {
  const heroRef = useRef(null);
  const heroSlidesRef = useRef([]);
  const [heroIndex, setHeroIndex] = useState(0);

  const hubsRef = useRef(null);
  const hubCardsRef = useRef([]);

  // scroll the hero slide into view when index changes
  useEffect(() => {
    const node = heroSlidesRef.current[heroIndex];
    if (node) node.scrollIntoView({ behavior: "smooth", block: "nearest", inline: "center" });
  }, [heroIndex]);

  // next/prev helpers for hero
  const heroPrev = () => setHeroIndex((i) => Math.max(0, i - 1));
  const heroNext = () => setHeroIndex((i) => Math.min(HERO_SLIDES.length - 1, i + 1));

  // hub arrows - scroll by one card width
  const hubScroll = (dir = 1) => {
    const el = hubsRef.current;
    if (!el) return;
    const card = el.querySelector(".hub-card");
    if (!card) return;
    const gap = parseInt(getComputedStyle(card).marginRight || 14);
    const cardW = card.offsetWidth + gap;
    el.scrollBy({ left: cardW * dir, behavior: "smooth" });
  };

  // update heroIndex when user swipes (native scroll)
  useEffect(() => {
    const el = heroRef.current;
    if (!el) return;
    let raf = 0;
    const onScroll = () => {
      cancelAnimationFrame(raf);
      raf = requestAnimationFrame(() => {
        const children = Array.from(el.querySelectorAll(".hero-slide"));
        // find the slide whose center is closest to viewport center
        const centerX = el.scrollLeft + el.clientWidth / 2;
        let bestIdx = 0;
        let bestDist = Infinity;
        children.forEach((c, i) => {
          const rect = c.getBoundingClientRect();
          const left = c.offsetLeft;
          const mid = left + c.offsetWidth / 2;
          const dist = Math.abs(mid - centerX);
          if (dist < bestDist) {
            bestDist = dist;
            bestIdx = i;
          }
        });
        if (bestIdx !== heroIndex) setHeroIndex(bestIdx);
      });
    };
    el.addEventListener("scroll", onScroll, { passive: true });
    return () => {
      el.removeEventListener("scroll", onScroll);
      cancelAnimationFrame(raf);
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [heroIndex]);

  return (
    <section className="cac-section" aria-label="Cars across city">
      <div className="cac-inner">
        <h2 className="cac-title">Cars across Delhi</h2>

        {/* HERO */}
        <div className="hero-shell">
          <div className="hero-viewport" ref={heroRef} role="list" aria-label="Promotional slides">
            {HERO_SLIDES.map((s, i) => (
              <div
                key={s.id}
                ref={(el) => (heroSlidesRef.current[i] = el)}
                className="hero-slide"
                style={{ backgroundImage: `url(${s.image})` }}
                role="listitem"
                aria-hidden={i !== heroIndex}
              >
                <div className="hero-overlay">
                  <div className="hero-sub">{s.subTitle}</div>
                  <h3 className="hero-title">
                    {s.bigTitle.split("\n").map((line, idx) => (
                      <span key={idx} className="hero-title-line">
                        {line}
                      </span>
                    ))}
                  </h3>
                  <div className="hero-card">
                    <div className="hero-card-left">
                      <div className="hub-mini-title">{HUB_CARDS[0].title}</div>
                      <div className="hub-mini-meta">
                        <span className="hub-rating">★ {HUB_CARDS[0].rating.toFixed(1)}</span>
                        <span className="hub-reviews">({HUB_CARDS[0].reviews})</span>
                      </div>
                    </div>
                    <button className="hero-card-btn" onClick={() => window.alert("View cars")}>
                      View cars
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="hero-controls">
            <button className="hero-arrow" onClick={heroPrev} aria-label="Previous slide" disabled={heroIndex === 0}>
              ‹
            </button>
            <div className="hero-dots" role="tablist" aria-label="Slides">
              {HERO_SLIDES.map((_, i) => (
                <button
                  key={i}
                  className={`hero-dot ${i === heroIndex ? "active" : ""}`}
                  onClick={() => setHeroIndex(i)}
                  aria-label={`Go to slide ${i + 1}`}
                />
              ))}
            </div>
            <button
              className="hero-arrow"
              onClick={heroNext}
              aria-label="Next slide"
              disabled={heroIndex === HERO_SLIDES.length - 1}
            >
              ›
            </button>
          </div>
        </div>

        {/* HUB CARDS (scrollable row) */}
        <div className="hubs-shell">
          <div className="hubs-header">
            <div className="hubs-title">Nearby hubs</div>
            <div className="hubs-actions">
              <button className="hub-nav" onClick={() => hubScroll(-1)} aria-label="Prev hubs">
                ‹
              </button>
              <button className="hub-nav" onClick={() => hubScroll(1)} aria-label="Next hubs">
                ›
              </button>
            </div>
          </div>

          <div className="hubs-viewport" ref={hubsRef} role="list" aria-label="Hub cards">
            {HUB_CARDS.map((h, i) => (
              <div
                key={h.id}
                ref={(el) => (hubCardsRef.current[i] = el)}
                className="hub-card"
                role="listitem"
                style={{ backgroundImage: `url(${h.image})` }}
              >
                <div className="hub-card-body">
                  <div className="hub-title">{h.title}</div>
                  <div className="hub-row">
                    <div className="hub-rating">★ {h.rating.toFixed(1)}</div>
                    <div className="hub-cars">{h.cars}+ cars</div>
                  </div>
                  <div className="hub-actions">
                    <button className="hub-btn">View full address ›</button>
                    <button className="hub-mini-btn">View cars</button>
                  </div>
                </div>
              </div>
            ))}
          </div>

          <div className="view-all-row">
            <button className="view-all">View all cars in Delhi</button>
          </div>
        </div>
      </div>
    </section>
  );
}
