// src/components/CitySelectorModal.jsx
import React, { useEffect, useRef, useState } from "react";
import "../styles/CitySelectorModal.css";

/**
 * CitySelectorModal
 * Props:
 * - open: boolean
 * - onClose: () => void
 * - onSelect: (city) => void
 * - selectedCity?: string (currently selected city - optional)
 * - headerImage?: string (optional image url)
 */
export default function CitySelectorModal({
  open,
  onClose,
  onSelect,
  selectedCity = "Delhi",
  headerImage = "/mnt/data/987949e3-8737-42a6-b4dc-775dfa4097ec.png",
}) {
  const overlayRef = useRef(null);
  const searchRef = useRef(null);

  // local selection state so the choice stays highlighted immediately
  const [selected, setSelected] = useState(selectedCity);

  // keep local state in sync when parent prop changes
  useEffect(() => {
    setSelected(selectedCity);
  }, [selectedCity]);

  useEffect(() => {
    // trap focus to search input when opened
    if (open) {
      setTimeout(() => {
        searchRef.current?.focus();
      }, 80);
      document.body.style.overflow = "hidden";
    } else {
      document.body.style.overflow = "";
    }
    return () => {
      document.body.style.overflow = "";
    };
  }, [open]);

  useEffect(() => {
    function onKey(e) {
      if (e.key === "Escape" && open) onClose?.();
    }
    window.addEventListener("keydown", onKey);
    return () => window.removeEventListener("keydown", onKey);
  }, [open, onClose]);

  if (!open) return null;

  // Map each city to its image (place images under public/ for simplicity)
  const cityImages = {
    DelhiNCR: process.env.PUBLIC_URL + "/images/cities/delhincr.png",
    Bangalore: process.env.PUBLIC_URL + "/images/cities/bangalore.png",
    Hyderabad: process.env.PUBLIC_URL + "/images/cities/hyderabad.png",
    Mumbai: process.env.PUBLIC_URL + "/images/cities/mumbai.png",
    Pune: process.env.PUBLIC_URL + "/images/cities/pune.png",
    Delhi: process.env.PUBLIC_URL + "/images/cities/delhi.png",
    Gurgaon: process.env.PUBLIC_URL + "/images/cities/gurgaon.png",
    Noida: process.env.PUBLIC_URL + "/images/cities/noida.png",
    Ahmedabad: process.env.PUBLIC_URL + "/images/cities/ahmedabad.png",
    Chennai: process.env.PUBLIC_URL + "/images/cities/chennai.png",
    Kolkata: process.env.PUBLIC_URL + "/images/cities/kolkata.png",
    Lucknow: process.env.PUBLIC_URL + "/images/cities/lucknow.png",
    Jaipur: process.env.PUBLIC_URL + "/images/cities/jaipur.png",
    Chandigarh: process.env.PUBLIC_URL + "/images/cities/chandigarh.png",
    Ambala: process.env.PUBLIC_URL + "/images/cities/ambala.png",
    Coimbatore: process.env.PUBLIC_URL + "/images/cities/coimbatore.png",
    Faridabad: process.env.PUBLIC_URL + "/images/cities/faridabad.png",
    Ghaziabad: process.env.PUBLIC_URL + "/images/cities/ghaziabad.png",
    Agra: process.env.PUBLIC_URL + "/images/cities/agra.png",
  };

  const popular = [
    "DelhiNCR",
    "Bangalore",
    "Hyderabad",
    "Mumbai",
    "Pune",
    "Delhi",
    "Gurgaon",
    "Noida",
    "Ahmedabad",
    "Chennai",
    "Kolkata",
    "Lucknow",
    "Jaipur",
    "Chandigarh",
  ];

  const more = ["Ambala", "Coimbatore", "Faridabad", "Ghaziabad", "Agra"];

  function handleSelect(city) {
    setSelected(city); // keep selection in modal UI immediately
    onSelect?.(city);  // notify parent
    // note: do not auto-close here unless parent expects it; if you want auto close, call onClose()
    // onClose?.();
  }

  return (
    <div
      className="cs-modal-overlay"
      role="dialog"
      aria-modal="true"
      aria-label="Select your city"
      ref={overlayRef}
      onClick={(e) => {
        // close only when clicking backdrop
        if (e.target === overlayRef.current) onClose?.();
      }}
    >
      <div className="cs-sheet" role="document">
        <button className="cs-close" onClick={onClose} aria-label="Close">
          ✕
        </button>

        <div className="cs-header">
          {headerImage ? (
            <img src={headerImage} alt="Cities" className="cs-header-img" />
          ) : null}
          <div className="cs-header-controls">
            <h2 className="cs-title">{selected}</h2>
            <div className="cs-search">
              <input
                ref={searchRef}
                type="search"
                placeholder="Search for your city"
                aria-label="Search for your city"
              />
              <button className="cs-detect" aria-label="Detect location">
                ⊕ Detect
              </button>
            </div>
          </div>
        </div>

        <div className="cs-body">
          <h3 className="cs-subheading">POPULAR CITIES</h3>

          <div className="cs-grid">
            {popular.map((c) => {
              const isSelected = selected === c;
              return (
                <button
                  key={c}
                  className={`cs-grid-item ${isSelected ? "selected" : ""}`}
                  onClick={() => handleSelect(c)}
                  aria-pressed={isSelected}
                >
                  <div className="cs-icon" aria-hidden>
                    <img
                      src={cityImages[c] || process.env.PUBLIC_URL + "/images/cities/default.png"}
                      alt=""
                      className="cs-city-img"
                    />
                  </div>
                  <div className="cs-city">{c}</div>
                </button>
              );
            })}
          </div>

          <h4 className="cs-more-title">MORE CITIES</h4>
          <ul className="cs-more-list">
            {more.map((m) => (
              <li key={m}>
                <button
                  className={`cs-more-item ${selected === m ? "selected" : ""}`}
                  onClick={() => handleSelect(m)}
                  aria-pressed={selected === m}
                >
                  <img
                    src={cityImages[m] || process.env.PUBLIC_URL + "/images/cities/default.png"}
                    className="cs-city-img-small"
                    alt=""
                  />
                  {m}
                </button>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
}
