// src/pages/CarDetails.jsx
import React, { useEffect, useRef, useState, useCallback } from "react";
import { useParams, useNavigate } from "react-router-dom";
import carsData from "../data/cars";
import styles from "../styles/CarDetailsWeb.module.css";

const TOPBAR_HEIGHT = 72;

export default function CarDetails() {
  const { id } = useParams();
  const navigate = useNavigate();
  const car = carsData.find((c) => Number(c.id) === Number(id));

  const containerRef = useRef(null);
  const leftRef = useRef(null);
  const heroRef = useRef(null);
  const overviewRef = useRef(null);
  const reportRef = useRef(null);
  const specsRef = useRef(null);
  const financeRef = useRef(null);
  const rightRef = useRef(null);
  const rightCardRef = useRef(null);

  const [showStickyTabs, setShowStickyTabs] = useState(false);
  const [activeTab, setActiveTab] = useState("overview");
  const [heroImage, setHeroImage] = useState(car ? car.image : "");
  const [stickyStyle, setStickyStyle] = useState({ left: 0, width: 720 });
  const [isStopped, setIsStopped] = useState(false);

  // Report drawer state
  const [showReportDrawer, setShowReportDrawer] = useState(false);
  const [drawerActive, setDrawerActive] = useState("core");

  const drawerRef = useRef(null);
  const drawerContentRef = useRef(null);
  const rdCoreRef = useRef(null);
  const rdSupportingRef = useRef(null);
  const rdInteriorsRef = useRef(null);
  const rdExteriorsRef = useRef(null);
  const rdWearRef = useRef(null);

  const sections = [
    { id: "overview", ref: overviewRef },
    { id: "report", ref: reportRef },
    { id: "specs", ref: specsRef },
    { id: "finance", ref: financeRef },
  ];

  // ---------- show sticky tabs (hero intersection) ----------
  useEffect(() => {
    if (!heroRef.current) return;
    const io = new IntersectionObserver(
      (entries) => entries.forEach((e) => setShowStickyTabs(!e.isIntersecting)),
      { threshold: 0.04 }
    );
    io.observe(heroRef.current);
    return () => io.disconnect();
  }, []);

  // ---------- sticky position ----------
  const updateStickyPosition = useCallback(() => {
    if (!leftRef.current || !containerRef.current) return;
    const leftRect = leftRef.current.getBoundingClientRect();
    const containerRect = containerRef.current.getBoundingClientRect();
    const left = Math.max(containerRect.left + 12, leftRect.left);
    const width = Math.min(leftRect.width, 760);
    setStickyStyle({ left: Math.round(left), width: Math.round(width) });
  }, []);

  useEffect(() => {
    updateStickyPosition();
    window.addEventListener("resize", updateStickyPosition);
    window.addEventListener("load", updateStickyPosition);
    return () => {
      window.removeEventListener("resize", updateStickyPosition);
      window.removeEventListener("load", updateStickyPosition);
    };
  }, [updateStickyPosition]);

  // ---------- right card stop ----------
  useEffect(() => {
    function computeStop() {
      const footer = document.querySelector("footer");
      const card = rightCardRef.current;
      if (!card || !footer) return;
      const footerRect = footer.getBoundingClientRect();
      const cardRect = card.getBoundingClientRect();
      setIsStopped(cardRect.bottom >= footerRect.top - 12);
    }
    computeStop();
    window.addEventListener("scroll", computeStop, { passive: true });
    window.addEventListener("resize", computeStop);
    return () => {
      window.removeEventListener("scroll", computeStop);
      window.removeEventListener("resize", computeStop);
    };
  }, []);

  // ---------- page scrollspy (for main tabs) ----------
  useEffect(() => {
    const offset = TOPBAR_HEIGHT + 8;
    let raf = null;
    function compute() {
      const pos = window.scrollY + offset + 6;
      let current = sections[0].id;
      for (const s of sections) {
        const el = s.ref.current;
        if (!el) continue;
        const top = el.getBoundingClientRect().top + window.scrollY;
        if (top <= pos) current = s.id;
      }
      setActiveTab((p) => (p === current ? p : current));
      raf = null;
    }
    function onScroll() {
      if (raf) return;
      raf = requestAnimationFrame(compute);
    }
    window.addEventListener("scroll", onScroll, { passive: true });
    window.addEventListener("resize", onScroll);
    compute();
    return () => {
      window.removeEventListener("scroll", onScroll);
      window.removeEventListener("resize", onScroll);
      if (raf) cancelAnimationFrame(raf);
    };
  }, []);

  function scrollToRef(ref) {
    if (!ref?.current) return;
    const offset = TOPBAR_HEIGHT + 8;
    const top = ref.current.getBoundingClientRect().top + window.scrollY - offset;
    window.scrollTo({ top, behavior: "smooth" });
    const sec = ref.current.dataset?.section || null;
    if (sec) setActiveTab(sec);
    setTimeout(() => window.dispatchEvent(new Event("scroll")), 300);
  }

  // ---------- DRAWER: open/close ----------
  function openReportDrawer() {
    setShowReportDrawer(true);
    // ensure drawerActive initially core
    setDrawerActive("core");
    // small delay to allow drawer mount, then scroll top
    setTimeout(() => {
      if (drawerContentRef.current) drawerContentRef.current.scrollTop = 0;
    }, 60);
  }
  function closeReportDrawer() {
    setShowReportDrawer(false);
  }

  // ---------- DRAWER: scroll-to inside drawer ----------
  function scrollDrawerTo(ref) {
    if (!ref?.current || !drawerContentRef.current) return;
    const container = drawerContentRef.current;
    const top = ref.current.offsetTop;
    container.scrollTo({ top: top - 12, behavior: "smooth" });
    // optimistic
    const id = ref.current.dataset?.section;
    if (id) setDrawerActive(id);
  }

  // ---------- DRAWER: scrollspy inside drawer ----------
  useEffect(() => {
    const container = drawerContentRef.current;
    if (!container) return;

    const opts = {
      root: container,
      threshold: [0.1, 0.4, 0.6],
    };
    const refs = [
      { id: "core", el: rdCoreRef },
      { id: "supporting", el: rdSupportingRef },
      { id: "interiors", el: rdInteriorsRef },
      { id: "exteriors", el: rdExteriorsRef },
      { id: "wear", el: rdWearRef },
    ];
    const io = new IntersectionObserver((entries) => {
      const vis = entries.filter((e) => e.isIntersecting);
      if (!vis.length) return;
      vis.sort((a, b) => b.intersectionRatio - a.intersectionRatio);
      const id = vis[0].target.dataset.section;
      if (id) setDrawerActive((p) => (p === id ? p : id));
    }, opts);

    refs.forEach((r) => {
      if (r.el.current) io.observe(r.el.current);
    });

    // fallback rAF if observer fails or content changes rapidly
    let raf = null;
    function fallback() {
      const scrollTop = container.scrollTop + 20;
      let current = "core";
      for (const r of refs) {
        const el = r.el.current;
        if (!el) continue;
        if (el.offsetTop <= scrollTop) current = r.id;
      }
      setDrawerActive((p) => (p === current ? p : current));
      raf = null;
    }
    function onScroll() {
      if (raf) return;
      raf = requestAnimationFrame(fallback);
    }
    container.addEventListener("scroll", onScroll, { passive: true });
    // init
    fallback();

    return () => {
      io.disconnect();
      container.removeEventListener("scroll", onScroll);
      if (raf) cancelAnimationFrame(raf);
    };
  }, [showReportDrawer]);

  if (!car) {
    return (
      <div className={styles.carDetailsPageWrapper}>
        <div className={styles.cdCard}>
          <p>Car not found</p>
          <button className={styles.btn} onClick={() => navigate(-1)}>
            Back
          </button>
        </div>
      </div>
    );
  }

  const thumbs = car.images && car.images.length ? car.images : [car.image];

  return (
    <div className={styles.carDetailsPageWrapper}>
      {/* Sticky tabs */}
      <div
        className={`${styles.cdStickyTabs} ${showStickyTabs ? styles.visible : ""}`}
        style={{
          top: `${TOPBAR_HEIGHT + 8}px`,
          left: `${stickyStyle.left}px`,
          width: `${stickyStyle.width}px`,
        }}
      >
        <div className={styles.cdStickyInner}>
          <button className={`${styles.cdTab} ${activeTab === "overview" ? styles.cdTabActive : ""}`} onClick={() => scrollToRef(overviewRef)}>Overview</button>
          <button className={`${styles.cdTab} ${activeTab === "report" ? styles.cdTabActive : ""}`} onClick={() => scrollToRef(reportRef)}>Report</button>
          <button className={`${styles.cdTab} ${activeTab === "specs" ? styles.cdTabActive : ""}`} onClick={() => scrollToRef(specsRef)}>Feature & Specs</button>
          <button className={`${styles.cdTab} ${activeTab === "finance" ? styles.cdTabActive : ""}`} onClick={() => scrollToRef(financeRef)}>Finance</button>
        </div>
      </div>

      <div className={styles.carDetailsContainer} ref={containerRef}>
        {/* LEFT */}
        <main className={styles.cdLeft} ref={leftRef}>
          {/* Hero */}
          <div className={styles.cdHero} ref={heroRef}>
            <div className={styles.cdHeroInner}>
              <div className={styles.cdHeroImage}>
                <img src={heroImage} alt={car.title} />
                <div className={styles.cdHeroControls}>
                  <button className={styles.cdBack} onClick={() => navigate(-1)}> Back</button>
                </div>
              </div>
              <div className={styles.cdThumbs}>
                {thumbs.map((t, i) => (
                  <button key={i} type="button" className={`${styles.cdThumb} ${t === heroImage ? styles.cdThumbSelected : ""}`} onClick={() => setHeroImage(t)} aria-label={`Show image ${i + 1}`}>
                    <img src={t} alt={`thumb ${i + 1}`} />
                  </button>
                ))}
              </div>
            </div>
          </div>

          {/* hero tabs */}
          <nav className={styles.cdHeroTabs} aria-label="Car sections">
            <button className={`${styles.heroTab} ${activeTab === "overview" ? styles.heroTabActive : ""}`} onClick={() => scrollToRef(overviewRef)}>Overview</button>
            <button className={`${styles.heroTab} ${activeTab === "report" ? styles.heroTabActive : ""}`} onClick={() => scrollToRef(reportRef)}>Report</button>
            <button className={`${styles.heroTab} ${activeTab === "specs" ? styles.heroTabActive : ""}`} onClick={() => scrollToRef(specsRef)}>Feature & Specs</button>
            <button className={`${styles.heroTab} ${activeTab === "finance" ? styles.heroTabActive : ""}`} onClick={() => scrollToRef(financeRef)}>Finance</button>
          </nav>

          {/* Overview (kept same as earlier) */}
          <section id="overview" ref={overviewRef} data-section="overview" className={styles.cdSection}>
            <h2 className={styles.pageTitle}>Car Overview</h2>
            <div className={styles.overviewCard}>
              {/* rows */}
              <div className={styles.overviewRow}>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Make Year</div><div className={styles.fieldValue}>May 2024</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Registration Year</div><div className={styles.fieldValue}>Jun 2024</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Fuel Type</div><div className={styles.fieldValue}>{car.fuel}</div></div>
              </div>
              <div className={styles.overviewRow}>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Km driven</div><div className={styles.fieldValueLarge}>{formatKm(car.km)}</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Transmission</div><div className={styles.fieldValue}>{car.transmission}</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>No. of Owner</div><div className={styles.fieldValue}>{car.owner || "1st Owner"}</div></div>
              </div>
              <div className={styles.overviewRow}>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Insurance Validity</div><div className={styles.fieldValue}>Jun 2026</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>Insurance Type</div><div className={styles.fieldValue}>Comprehensive</div></div>
                <div className={styles.overviewField}><div className={styles.fieldLabel}>RTO</div><div className={styles.fieldValue}>UP14</div></div>
              </div>
              <div className={`${styles.overviewRow} ${styles.locationRow}`}>
                <div>
                  <div className={styles.locationLabel}>Car Location</div>
                  <div className={styles.locationValue}>{car.city}</div>
                </div>
              </div>
            </div>
          </section>

          {/* Report summary card (with View full report) */}
          <section id="report" ref={reportRef} data-section="report" className={styles.cdSection}>
            <h2 className={styles.cdSectionTitle}>Quality report</h2>
            <p className={styles.muted}>1450 parts evaluated by 5 automotive experts</p>

            <div className={styles.reportSummaryCard}>
              <div className={styles.badgesRow}>
                <span className={styles.badge}> Meter not tampered</span>
                <span className={styles.badge}> Non-flooded</span>
                <span className={styles.badge}> Core structure intact</span>
              </div>

              <div className={styles.reportTwoCol}>
                <div className={styles.leftCol}>
                  <div className={styles.reportList}>
                    <div className={styles.reportListItem}>
                      <div className={styles.reportIcon}></div>
                      <div>
                        <div className={styles.reportListTitle}>Core systems</div>
                        <div className={styles.reportListSub}>Engine, transmission & chassis</div>
                      </div>
                    </div>

                    <div className={styles.reportListItem}>
                      <div className={styles.reportIcon}></div>
                      <div>
                        <div className={styles.reportListTitle}>Interiors & AC</div>
                        <div className={styles.reportListSub}>Seats, AC, audio & other features</div>
                      </div>
                    </div>

                    <div className={styles.reportListItem}>
                      <div className={styles.reportIcon}></div>
                      <div>
                        <div className={styles.reportListTitle}>Wear & tear parts</div>
                        <div className={styles.reportListSub}>Tyres, clutch, brakes & more</div>
                      </div>
                    </div>
                  </div>
                </div>

                <div className={styles.rightCol}>
                  <div className={styles.scoresRow}>
                    <div className={styles.scorePill}><div className={styles.scoreNum}>9.7</div><div className={styles.scoreLabel}>Excellent</div></div>
                    <div className={styles.scorePill}><div className={styles.scoreNum}>9.4</div><div className={styles.scoreLabel}>Excellent</div></div>
                    <div className={styles.scorePill}><div className={styles.scoreNum}>8.7</div><div className={styles.scoreLabel}>Good</div></div>
                    <div className={styles.scorePill}><div className={styles.scoreNum}>7.7</div><div className={styles.scoreLabel}>Fair</div></div>
                  </div>

                  <div className={styles.reportCTA}>
                    <button className={styles.btnPrimary} onClick={openReportDrawer}>View full report</button>
                  </div>
                </div>
              </div>
            </div>
          </section>

          {/* Rest sections kept (specs & finance) */}
          <section id="specs" ref={specsRef} data-section="specs" className={styles.cdSection}>
            <h2 className={styles.cdSectionTitle}>Feature & Specs</h2>
            <div className={styles.cdCard}>
              <div className={styles.specsCardGrid}>
                <div className={styles.spec}><div className={styles.specTtl}>Mileage (ARAI)</div><div className={styles.specVal}>22.35 kmpl</div></div>
                <div className={styles.spec}><div className={styles.specTtl}>Boot space</div><div className={styles.specVal}>318 litres</div></div>
                <div className={styles.spec}><div className={styles.specTtl}>Displacement</div><div className={styles.specVal}>1197 cc</div></div>
              </div>
            </div>
          </section>

          <section id="finance" ref={financeRef} data-section="finance" className={styles.cdSection}>
            <h2 className={styles.cdSectionTitle}>Finance</h2>
            <div className={styles.cdCard}>
              <div className={styles.financeCardEmi}>
                <div className={styles.emiLeft}><div className={styles.emiNumber}>9,400</div><div className={styles.meta}>per month (approx)</div></div>
                <div className={styles.emiRight}><div className={styles.meta}>Loan Amount</div><div className={styles.value}>{(car.price - 110400).toLocaleString()}</div></div>
              </div>
            </div>
          </section>

        </main>

        {/* RIGHT sticky card (existing) */}
        <aside className={styles.cdRight} ref={rightRef}>
          <div className={`${styles.cdRightCard} ${isStopped ? styles.cdRightStopCard : ""}`} ref={rightCardRef}
            style={isStopped ? { position: "absolute", bottom: 0, top: "auto", right: 0 } : { position: "fixed", top: `${TOPBAR_HEIGHT + 12}px` }}>
            <div className={styles.rightInner}>
              <div className={styles.titleRow}>
                <h3 className={styles.carTitle}>{car.title}</h3>
                <button className={styles.like} aria-label="save"></button>
              </div>

              <div className={styles.meta}>
                <div>{car.km.toLocaleString()} km  {car.fuel}  {car.transmission}</div>
                <div className={styles.hub}>Spinny Car Hub, {car.city}</div>
              </div>

              <div className={styles.priceBlock}>
                <div className={styles.priceBlockLabel}>Fixed on road price</div>
                <div className={styles.priceBlockPrice}>{(car.price / 100000).toFixed(2)} L</div>
                <div className={styles.priceBlockSmall}>Includes RC transfer, insurance & more</div>
              </div>

              <div className={styles.cta}>
                <button className={styles.btnBook}>BOOK NOW</button>
                <button className={styles.btnTest}>FREE TEST DRIVE</button>
              </div>
            </div>
          </div>
        </aside>
      </div>

      {/* Report Drawer (slide-over) */}
      {showReportDrawer && (
        <div className={styles.drawerWrap}>
          <div className={styles.drawerBackdrop} onClick={closeReportDrawer} />
          <aside className={styles.reportDrawer} ref={drawerRef}>
            <div className={styles.drawerHeader}>
              <button className={styles.drawerClose} onClick={closeReportDrawer}></button>
              <div>
                <h3 className={styles.drawerTitle}>Car quality report</h3>
                <div className={styles.drawerSub}>{car.title}  {formatKm(car.km)}</div>
              </div>
            </div>

            {/* Horizontal scrollable tabs with scores */}
            <div className={styles.drawerTabs} role="tablist" aria-label="Report sections">
              <div className={styles.drawerTabsInner}>
                <button className={`${styles.drawerTab} ${drawerActive === "core" ? styles.drawerTabActive : ""}`} onClick={() => scrollDrawerTo(rdCoreRef)}>
                  Core systems <span className={styles.tabScore}>9.7</span>
                </button>
                <button className={`${styles.drawerTab} ${drawerActive === "supporting" ? styles.drawerTabActive : ""}`} onClick={() => scrollDrawerTo(rdSupportingRef)}>
                  Supporting <span className={styles.tabScore}>9.4</span>
                </button>
                <button className={`${styles.drawerTab} ${drawerActive === "interiors" ? styles.drawerTabActive : ""}`} onClick={() => scrollDrawerTo(rdInteriorsRef)}>
                  Interiors & AC <span className={styles.tabScore}>8.7</span>
                </button>
                <button className={`${styles.drawerTab} ${drawerActive === "exteriors" ? styles.drawerTabActive : ""}`} onClick={() => scrollDrawerTo(rdExteriorsRef)}>
                  Exteriors & lights <span className={styles.tabScore}>7.7</span>
                </button>
                <button className={`${styles.drawerTab} ${drawerActive === "wear" ? styles.drawerTabActive : ""}`} onClick={() => scrollDrawerTo(rdWearRef)}>
                  Wear & tear <span className={styles.tabScore}>7.1</span>
                </button>
              </div>
            </div>

            {/* Drawer content (scrollable) */}
            <div className={styles.drawerContent} ref={drawerContentRef}>
              <section ref={rdCoreRef} data-section="core" className={styles.drawerSection}>
                <div className={styles.drawerSectionHead}>
                  <div className={styles.drawerSectionTitle}>Core systems</div>
                  <div className={styles.drawerSectionScore}>
                    <div className={styles.pillScore}>9.7</div>
                    <div className={styles.pillLabel}>Excellent</div>
                  </div>
                </div>
                <div className={styles.drawerSectionSub}>196 parts across 3 assemblies</div>
                <div className={styles.callout}> Chassis  Minor imperfections, most were restored</div>

                <ul className={styles.detailList}>
                  <li>Chassis  Minor imperfections</li>
                  <li>Engine  Flawless, no imperfections</li>
                  <li>Transmission  Flawless, no imperfections</li>
                </ul>
              </section>

              <section ref={rdSupportingRef} data-section="supporting" className={styles.drawerSection}>
                <div className={styles.drawerSectionHead}>
                  <div className={styles.drawerSectionTitle}>Supporting systems</div>
                  <div className={styles.drawerSectionScore}>
                    <div className={styles.pillScore}>9.4</div>
                    <div className={styles.pillLabel}>Excellent</div>
                  </div>
                </div>
                <div className={styles.drawerSectionSub}>Fuel supply, ignition & other systems</div>
                <ul className={styles.detailList}>
                  <li>Fuel pump  Good</li>
                  <li>Ignition  No issues</li>
                </ul>
              </section>

              <section ref={rdInteriorsRef} data-section="interiors" className={styles.drawerSection}>
                <div className={styles.drawerSectionHead}>
                  <div className={styles.drawerSectionTitle}>Interiors & AC</div>
                  <div className={styles.drawerSectionScore}>
                    <div className={styles.pillScore}>8.7</div>
                    <div className={styles.pillLabel}>Good</div>
                  </div>
                </div>
                <div className={styles.drawerSectionSub}>Seats, AC, audio & other features</div>
                <ul className={styles.detailList}>
                  <li>Seats  Good</li>
                  <li>AC  Cold</li>
                </ul>
              </section>

              <section ref={rdExteriorsRef} data-section="exteriors" className={styles.drawerSection}>
                <div className={styles.drawerSectionHead}>
                  <div className={styles.drawerSectionTitle}>Exteriors & lights</div>
                  <div className={styles.drawerSectionScore}>
                    <div className={styles.pillScore}>7.7</div>
                    <div className={styles.pillLabel}>Fair</div>
                  </div>
                </div>
                <div className={styles.drawerSectionSub}>Panels, glasses, lights & fixtures</div>
                <div className={styles.calloutAlt}> Imperfections on body panels but no impact on build quality  12,000 lower price tag</div>
              </section>

              <section ref={rdWearRef} data-section="wear" className={styles.drawerSection}>
                <div className={styles.drawerSectionHead}>
                  <div className={styles.drawerSectionTitle}>Wear & tear parts</div>
                  <div className={styles.drawerSectionScore}>
                    <div className={styles.pillScore}>7.1</div>
                    <div className={styles.pillLabel}>Fair</div>
                  </div>
                </div>
                <div className={styles.drawerSectionSub}>Tyres, clutch, brakes & more</div>
                <div className={styles.calloutAlt}> Few components have experienced wear and tear  27,000 lower price tag</div>
              </section>

              <div style={{ height: 56 }} />
            </div>
          </aside>
        </div>
      )}
    </div>
  );
}

// helper
function formatKm(n) {
  if (n == null) return "";
  if (n >= 1000 && n < 100000) return `${Math.round(n / 1000)}K km`;
  return `${n.toLocaleString()} km`;
}
